import React, { useState, useEffect, useCallback } from 'react';
import { LogOut, Edit, Trash2 } from 'lucide-react';
import './index.css';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

// Auth Header
const getAuthHeaders = () => ({
    'Authorization': `Bearer ${localStorage.getItem('adminToken')}`
});

// Bild-URL Helper
const getImageUrl = (imagePath) => imagePath?.startsWith('/uploads/') ? `${BACKEND_URL}${imagePath}` : imagePath || FALLBACK_IMAGE_URL;

// ================= PRODUCT FORM =================
const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
    const [formData, setFormData] = useState(productToEdit || {
        name: '', description: '', price: 0, stock: 0, isFeatured: false, fabricId: '', imageFile: null
    });

    useEffect(() => {
        setFormData(productToEdit || {
            name: '', description: '', price: 0, stock: 0, isFeatured: false, fabricId: '', imageFile: null
        });
    }, [productToEdit]);

    const handleChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        if (name === 'imageFile') setFormData({ ...formData, imageFile: files[0] });
        else if (type === 'checkbox') setFormData({ ...formData, [name]: checked });
        else setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <div className="form-card">
            <h3>{productToEdit ? "Produkt bearbeiten" : "Neues Produkt erstellen"}</h3>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Produktname</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Beschreibung</label>
                    <textarea name="description" value={formData.description} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Preis (€)</label>
                    <input type="number" name="price" value={formData.price} step="0.01" min="0" onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Bestand</label>
                    <input type="number" name="stock" value={formData.stock} min="0" onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Stoff wählen</label>
                    <select name="fabricId" value={formData.fabricId} onChange={handleChange}>
                        <option value="">-- Kein Stoff --</option>
                        {fabrics.map(f => <option key={f.id} value={f.id}>{f.name}</option>)}
                    </select>
                </div>
                <div className="form-group">
                    <label>
                        <input type="checkbox" name="isFeatured" checked={formData.isFeatured} onChange={handleChange} />
                        Als Featured anzeigen
                    </label>
                </div>
                <div className="form-group">
                    <label>Bild hochladen</label>
                    <input type="file" name="imageFile" accept="image/*" onChange={handleChange} />
                </div>
                <div>
                    <button type="submit">{productToEdit ? "Speichern" : "Erstellen"}</button>
                    {productToEdit && <button type="button" onClick={onCancel} style={{ marginLeft: '10px', backgroundColor: '#FFD99D', color: '#4F3C3C' }}>Abbrechen</button>}
                </div>
            </form>
        </div>
    );
};

// ================= FABRIC FORM =================
const FabricForm = ({ onAdd }) => {
    const [formData, setFormData] = useState({ name: '', fabricType: '', stock: 0, imageFile: null });

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        if (name === 'imageFile') setFormData({ ...formData, imageFile: files[0] });
        else setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = new FormData();
        Object.keys(formData).forEach(key => {
            if (formData[key] !== null) data.append(key, formData[key]);
        });
        onAdd(data);
    };

    return (
        <div className="form-card">
            <h3>Neuen Stoff hinzufügen</h3>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name</label>
                    <input name="name" value={formData.name} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Typ</label>
                    <input name="fabricType" value={formData.fabricType} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Bestand</label>
                    <input name="stock" type="number" value={formData.stock} min="0" onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Bild hochladen</label>
                    <input type="file" name="imageFile" accept="image/*" onChange={handleChange} />
                </div>
                <button type="submit">Speichern</button>
            </form>
        </div>
    );
};

// ================= ADMIN DASHBOARD =================
const AdminDashboard = ({ onLogout }) => {
    const [products, setProducts] = useState([]);
    const [fabrics, setFabrics] = useState([]);
    const [currentProduct, setCurrentProduct] = useState(null);
    const [message, setMessage] = useState({ text: '', type: '' });

    const fetchAll = useCallback(async () => {
        try {
            const prodRes = await fetch(`${API_URL}/products`, { headers: getAuthHeaders() });
            const fabRes = await fetch(`${API_URL}/fabrics`, { headers: getAuthHeaders() });
            setProducts(await prodRes.json());
            setFabrics(await fabRes.json());
        } catch {
            setMessage({ text: '❌ Fehler beim Laden', type: 'error' });
        }
    }, []);

    useEffect(() => { fetchAll(); }, [fetchAll]);

    const handleProductSubmit = async (data) => {
        const formData = new FormData();
        Object.keys(data).forEach(key => { if (data[key] !== null) formData.append(key, data[key]); });

        const url = data.id ? `${API_URL}/products/${data.id}` : `${API_URL}/products`;
        const method = data.id ? 'PUT' : 'POST';

        const res = await fetch(url, { method, headers: getAuthHeaders(), body: formData });
        if (res.ok) { setMessage({ text: '✅ Produkt gespeichert', type: 'success' }); setCurrentProduct(null); fetchAll(); }
        else setMessage({ text: '❌ Fehler beim Speichern', type: 'error' });
    };

    const handleDeleteProduct = async (id) => {
        if (!window.confirm("Produkt wirklich löschen?")) return;
        const res = await fetch(`${API_URL}/products/${id}`, { method: 'DELETE', headers: getAuthHeaders() });
        if (res.ok) { setMessage({ text: '✅ Produkt gelöscht', type: 'success' }); fetchAll(); }
        else setMessage({ text: '❌ Fehler beim Löschen', type: 'error' });
    };

    const handleAddFabric = async (formData) => {
        const res = await fetch(`${API_URL}/fabrics`, { method: 'POST', headers: getAuthHeaders(), body: formData });
        if (res.ok) { setMessage({ text: '✅ Stoff gespeichert', type: 'success' }); fetchAll(); }
        else setMessage({ text: '❌ Fehler beim Speichern', type: 'error' });
    };

    const handleDeleteFabric = async (id) => {
        if (!window.confirm("Stoff wirklich löschen?")) return;
        const res = await fetch(`${API_URL}/fabrics/${id}`, { method: 'DELETE', headers: getAuthHeaders() });
        if (res.ok) { setMessage({ text: '✅ Stoff gelöscht', type: 'success' }); fetchAll(); }
        else setMessage({ text: '❌ Fehler beim Löschen', type: 'error' });
    };

    return (
        <div className="admin-dashboard-container">
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <h1>Admin Dashboard</h1>
                <button className="admin-button admin-button-delete" onClick={() => { localStorage.removeItem('adminToken'); onLogout(); }}>
                    <LogOut /> Logout
                </button>
            </div>

            {message.text && <div className={`status-message ${message.type === 'error' ? 'status-message-error' : 'status-message-success'}`}>{message.text}</div>}

            <div className="admin-dashboard-grid">
                <ProductForm productToEdit={currentProduct} onSubmit={handleProductSubmit} onCancel={() => setCurrentProduct(null)} fabrics={fabrics} />
                <div className="admin-list-container">
                    <h3>Produkte ({products.length})</h3>
                    <table className="admin-table">
                        <thead>
                            <tr><th>Bild</th><th>Name</th><th>Aktionen</th></tr>
                        </thead>
                        <tbody>
                            {products.map(p => (
                                <tr key={p.id}>
                                    <td><img src={getImageUrl(p.imageUrl)} alt="" style={{ width: 50, height: 50, objectFit: 'cover' }} /></td>
                                    <td>{p.name}</td>
                                    <td>
                                        <button className="admin-button admin-button-edit" onClick={() => setCurrentProduct(p)}><Edit /></button>
                                        <button className="admin-button admin-button-delete" onClick={() => handleDeleteProduct(p.id)}><Trash2 /></button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            <div className="admin-dashboard-grid" style={{ marginTop: '30px' }}>
                <FabricForm onAdd={handleAddFabric} />
                <div className="admin-list-container">
                    <h3>Stoffe ({fabrics.length})</h3>
                    <table className="admin-table">
                        <thead>
                            <tr><th>Bild</th><th>Name</th><th>Aktionen</th></tr>
                        </thead>
                        <tbody>
                            {fabrics.map(f => (
                                <tr key={f.id}>
                                    <td><img src={getImageUrl(f.imageUrl)} alt="" style={{ width: 50, height: 50, objectFit: 'cover' }} /></td>
                                    <td>{f.name}</td>
                                    <td>
                                        <button className="admin-button admin-button-delete" onClick={() => handleDeleteFabric(f.id)}><Trash2 /></button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
