// client/src/App.js
import React from 'react';
// Die Bilder importieren (Pfad anpassen, falls Sie sie anders genannt/gespeichert haben)
import logo from './assets/Logo.jpg'; 
import banner from './assets/banner.png'; 
import './App.css'; // Oder index.css, je nachdem, wo Sie die Variablen definiert haben

function App() {
  return (
    <div className="App">
      {/* 1. Banner (Breite des Headers) */}
      <header className="App-header" style={{ 
          backgroundColor: 'var(--farbe-hintergrund)', 
          textAlign: 'center', 
          padding: '0' 
      }}>
        {/* Das Banner-Bild als oberstes Element */}
        <img 
          src={banner} 
          className="App-banner" 
          alt="Henkes-Stoffzauber Banner: Blog, Shop, Accessoires" 
          style={{ width: '100%', maxHeight: '200px', objectFit: 'cover' }}
        />
      </header>

      {/* 2. Hauptinhalt der Seite */}
      <main style={{ padding: '20px', textAlign: 'center' }}>
        <h2>Willkommen bei Henkes-Stoffzauber.de!</h2>

        {/* Das Logo als visuelle Marke in der Mitte */}
        <img 
          src={logo} 
          className="App-logo" 
          alt="Henkes-Stoffzauber Logo" 
          style={{ width: '150px', margin: '20px 0' }}
        />

        <p>Hier entsteht der kreative Ort für Mützen, Schals, Taschen und tolle Nähtipps von Andrea Henke.</p>
        <button>Zum Shop</button>
        <button style={{ marginLeft: '10px' }}>Zum Blog</button>
      </main>
    </div>
  );
}

export default App;
