import React, { useEffect, useRef } from "react";

export default function Checkout() {
    const paypalRef = useRef();

    useEffect(() => {
        const CLIENT_ID = process.env.REACT_APP_PAYPAL_CLIENT_ID;

        if (!CLIENT_ID) {
            console.error("FEHLER: Kein PayPal Client ID im Environment!");
            return;
        }

        // Falls SDK existiert → Buttons rendern
        if (window.paypal) {
            loadButtons();
            return;
        }

        // Sonst Script laden
        const script = document.createElement("script");
        script.src = `https://www.paypal.com/sdk/js?client-id=${CLIENT_ID}&currency=EUR`;
        script.onload = () => loadButtons();
        script.onerror = () => console.error("PayPal SDK konnte nicht geladen werden.");
        document.body.appendChild(script);

        function loadButtons() {
            if (!window.paypal || !paypalRef.current) return;

            window.paypal.Buttons({
                createOrder: async () => {
                    const res = await fetch("http://localhost:3001/api/create-order", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ amount: "99.99" })
                    });
                    const data = await res.json();
                    return data.id;
                },
                onApprove: async (data) => {
                    await fetch("http://localhost:3001/api/capture-order", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ orderID: data.orderID })
                    });
                    alert("Zahlung erfolgreich!");
                }
            }).render(paypalRef.current);
        }
    }, []);

    return (
        <div>
            <h1>Bezahlung</h1>
            <div ref={paypalRef}></div>
        </div>
    );
}
