// src/components/AdminLogin.js
import React, { useState } from 'react';

const AdminLogin = ({ onLogin }) => {
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        if (password === "stoffzauber2025") {
            // ✅ Admin-Token speichern
            localStorage.setItem('adminToken', "stoffzauber2025");

            // ✅ App informieren
            onLogin();
        } else {
            setError("❌ Falsches Passwort");
        }
    };

    return (
        <div style={{ maxWidth: '400px', margin: '80px auto', padding: '20px', background: '#fff', borderRadius: '10px', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' }}>
            <h2 style={{ textAlign: 'center' }}>Admin Login</h2>

            <form onSubmit={handleSubmit}>
                <input
                    type="password"
                    placeholder="Admin Passwort"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={{ width: '100%', padding: '10px', marginBottom: '15px', borderRadius: '5px', border: '1px solid #ccc' }}
                    required
                />

                {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

                <button
                    type="submit"
                    style={{ width: '100%', padding: '10px', background: '#5A4747', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
                >
                    Einloggen
                </button>
            </form>
        </div>
    );
};

export default AdminLogin;
