import React, { useState, useEffect } from 'react';
// Nur die Komponenten und Hooks importieren, da der Router bereits in index.js gerendert wird!
import { Routes, Route, Link, useParams, useNavigate } from 'react-router-dom'; 

// Importiert die externen Komponenten
// WICHTIGER HINWEIS: Diese Pfade funktionieren, WENN alle Komponenten sich im 
// selben Verzeichnis wie App.js befinden (z.B. src/). 
import Home from './Home';
import Shop from './Shop';
import AdminDashboard from './AdminDashboard';
import AdminLogin from './AdminLogin';
import ProductDetail from './ProductDetail';
import Stoffe from './Stoffe';

// Import des Contexts
import { CartProvider } from './CartContext';

// HILFSFUNKTION: Gibt eine Fehlermeldung für fehlende Komponenten aus
const MissingComponent = ({ name }) => (
    <div style={{ padding: '50px', textAlign: 'center', backgroundColor: '#fdd', borderRadius: '8px', border: '2px solid red', margin: '20px auto' }}>
        <h2 style={{ color: 'red' }}>⚠️ Komponentenfehler: {name}</h2>
        <p style={{ color: '#333' }}>
            Die Komponente `{name}` konnte nicht geladen werden. Bitte stellen Sie sicher, dass die Datei `{name}.js` existiert und die Komponente korrekt als **Standard-Export** (`export default {name};`) exportiert wird.
        </p>
    </div>
);


// -------------------------------------------------------------------------------------------------------------------------
// NEUE KOMPONENTE: Admin Protected Route
// Überprüft den Anmeldestatus und leitet entweder zum Dashboard oder zur Login-Seite weiter.
// Außerdem wird der Status in localStorage persistent gespeichert.
// -------------------------------------------------------------------------------------------------------------------------
const AdminProtectedRoute = () => {
    const navigate = useNavigate();

    // Zustand wird initial aus dem localStorage geladen
    const [isAdminAuthenticated, setIsAdminAuthenticated] = useState(
        localStorage.getItem('adminAuthenticated') === 'true'
    );

    // Funktion wird bei erfolgreichem Login in AdminLogin.js aufgerufen
    const handleAdminLogin = (isAuthenticated) => {
        if (isAuthenticated) {
            setIsAdminAuthenticated(true);
            localStorage.setItem('adminAuthenticated', 'true'); // Speichert den Status persistent
            navigate('/admin', { replace: true }); // Leitet zum Dashboard um, falls nötig
        }
    };

    // Funktion wird vom AdminDashboard aufgerufen
    const handleAdminLogout = () => {
        setIsAdminAuthenticated(false);
        localStorage.removeItem('adminAuthenticated'); // Entfernt den Status
        navigate('/admin', { replace: true });
        // Zeigt optional eine Meldung an, dass die Sitzung abgelaufen ist
        alert('Admin-Sitzung abgelaufen oder manuell beendet.');
    };

    if (isAdminAuthenticated) {
        // Wenn authentifiziert, zeige das Admin Dashboard
        return <AdminDashboard onLogout={handleAdminLogout} />;
    } else {
        // Wenn nicht authentifiziert, zeige das Login-Formular
        return <AdminLogin onLogin={handleAdminLogin} />;
    }
};

// -------------------------------------------------------------------------------------------------------------------------
// HILFSKOMPONENTE: Wrapper für ProductDetail, um useParams und useNavigate bereitzustellen
// -------------------------------------------------------------------------------------------------------------------------
const ProductDetailWrapper = () => {
    // Ruft die Produkt-ID aus der URL ab (z.B. /shop/123)
    const { productId } = useParams();
    // Ruft die Navigate-Funktion ab
    const navigate = useNavigate();

    // Stellt sicher, dass ProductDetail importiert wurde
    if (!ProductDetail) {
        return <MissingComponent name="ProductDetail" />;
    }

    // Übergibt die abgerufene ID und die Navigate-Funktion an die ProductDetail-Komponente
    return <ProductDetail productId={productId} navigate={navigate} />;
};


// -------------------------------------------------------------------------------------------------------------------------
// HAUPTKOMPONENTE: App
// -------------------------------------------------------------------------------------------------------------------------
function App() {
    // Dummy Home Komponente (ersetzen Sie diese später durch Ihre echte Home-Komponente)
    const Home = () => <div style={{ padding: '50px', textAlign: 'center' }}>
        <h1 style={{ color: 'var(--farbe-primär)' }}>Willkommen bei Henkes Stoffzauber!</h1>
        <p style={{ color: 'var(--farbe-text-dunkel)', fontSize: '1.1em', marginTop: '15px' }}>
            Finden Sie die neuesten und schönsten Stoffe für Ihre Kreativprojekte.
        </p>
        <Link to="/shop" style={{ 
            display: 'inline-block',
            marginTop: '30px',
            padding: '12px 25px',
            backgroundColor: 'var(--farbe-akzent)',
            color: 'white',
            textDecoration: 'none',
            borderRadius: '8px',
            fontWeight: 'bold',
            transition: 'background-color 0.2s',
        }} onMouseEnter={(e) => e.target.style.backgroundColor = 'var(--farbe-primär-dunkel)'} onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--farbe-akzent)'}>
            Zum Shop
        </Link>
    </div>;

    // Nur ein einfaches Lade-Screen-Beispiel, falls Sie asynchrone Initialisierung benötigen.
    const [isAppLoading, setIsAppLoading] = useState(false); 

    // Die Warenkorb-Komponente (Cart) ist hier nur als Platzhalter in der Navigation eingefügt
    const Cart = () => (
        <div style={{ 
            backgroundColor: 'var(--farbe-akzent)', 
            color: 'white', 
            padding: '10px 15px', 
            borderRadius: '5px',
            textDecoration: 'none',
            fontSize: '0.9em',
            fontWeight: 'bold',
            transition: 'background-color 0.2s',
        }} onMouseEnter={(e) => e.target.style.backgroundColor = 'var(--farbe-primär)'} onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--farbe-akzent)'}>
            Warenkorb (0)
        </div>
    );

    // Stildefinitionen
    const navItemStyle = { 
        padding: '10px 15px', 
        textDecoration: 'none', 
        color: 'var(--farbe-text-dunkel)', 
        fontWeight: 'bold', 
        borderRadius: '5px',
        transition: 'background-color 0.2s, color 0.2s',
    };
    const navHoverStyle = {
        backgroundColor: 'var(--farbe-grau)',
        color: 'var(--farbe-primär)',
    };

    // Styles für die Navigation, um die Logik auszublenden
    const navLinkHover = (e) => {
        Object.assign(e.target.style, navHoverStyle);
    };
    const navLinkLeave = (e) => {
        Object.assign(e.target.style, { backgroundColor: 'transparent', color: 'var(--farbe-text-dunkel)' });
    };


    return (
        <div className="App" style={{ fontFamily: 'Arial, sans-serif', backgroundColor: 'white', minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
            {/* Kopfzeile und Navigation */}
            <header className="App-header" style={{ 
                backgroundColor: 'white', 
                borderBottom: '1px solid var(--farbe-grau)', 
                padding: '10px 50px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
            }}>
                {/* Logo / Markenname */}
                <h1 style={{ margin: 0, fontSize: '1.8em', color: 'var(--farbe-primär)' }}>
                    <Link to="/" style={{ textDecoration: 'none', color: 'var(--farbe-primär)', display: 'flex', alignItems: 'center' }}>
                        <span style={{ fontSize: '1.2em', marginRight: '5px' }}>✂️</span> Henkes Stoffzauber
                    </Link>
                </h1>

                {/* Navigationsleiste */}
                <nav>
                    <Link to="/" style={navItemStyle} onMouseEnter={navLinkHover} onMouseLeave={navLinkLeave}>Startseite</Link>
                    <Link to="/shop" style={navItemStyle} onMouseEnter={navLinkHover} onMouseLeave={navLinkLeave}>Shop</Link>
                    <Link to="/stoffe" style={navItemStyle} onMouseEnter={navLinkHover} onMouseLeave={navLinkLeave}>Stoff-Typen</Link>
                    <Link to="/admin" style={{ ...navItemStyle, color: 'var(--farbe-akzent)' }} onMouseEnter={navLinkHover} onMouseLeave={navLinkLeave}>Admin</Link>
                    <Link to="/cart" element={Cart ? <Cart /> : null}>
                        <Cart />
                    </Link>
                </nav>
            </header>

            {/* Hauptinhalt */}
            {isAppLoading ? (
                <div style={{ textAlign: 'center', padding: '100px' }}>App wird geladen...</div>
            ) : (
                <CartProvider>
                    <main className="App-main" style={{ flexGrow: 1, padding: '20px 0' }}>
                        <Routes>
                            {/* Basis-Routen */}
                            <Route path="/" element={<Home />} />

                            {/* Shop Route mit MissingComponent Check */}
                            <Route path="/shop" element={Shop ? <Shop /> : <MissingComponent name="Shop" />} />

                            {/* Route für Produktdetails mit dem Wrapper */}
                            <Route path="/shop/:productId" element={<ProductDetailWrapper />} /> 
                            <Route path="/stoffe" element={Stoffe ? <Stoffe /> : <MissingComponent name="Stoffe" />} />
                            <Route path="/cart" element={<div style={{ padding: '50px', textAlign: 'center' }}><h1 style={{ color: 'var(--farbe-primär)' }}>Warenkorb</h1><p>Hier kommt der Warenkorb hin.</p></div>} />

                            {/* Geschützte Route für das Admin Dashboard (entscheidet intern über Login/Dashboard) */
                            /* WICHTIG: AdminProtectedRoute wird nun als Komponente gerendert und verwaltet den isAdminAuthenticated State */}
                            <Route path="/admin" element={<AdminProtectedRoute />} />

                            {/* Fallback-Route */}
                            <Route path="*" element={<h2 style={{padding: '20px', textAlign: 'center'}}>404 - Seite nicht gefunden</h2>} />
                        </Routes>
                    </main>

                    <footer className="App-footer" style={{ 
                        padding: '15px 50px', 
                        backgroundColor: 'var(--farbe-primär)', 
                        color: 'white', 
                        textAlign: 'center',
                        fontSize: '0.9em'
                    }}>
                        © 2025 Henkes-Stoffzauber.de | Alle Rechte vorbehalten.
                    </footer>
                </CartProvider>
            )}
        </div>
    );
}

// Wir verwenden den Standard-Export
export default App;
