// src/components/Shop.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import './Shop.css'

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Kein+Bild";

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const isInStock = product.stock > 0;

  return (
    <div className="product-card">
      <Link to={`/shop/${product.id}`}>
        <img
          src={getImageUrl(product.imageUrl)}
          alt={product.name}
          className="carousel-image"
          style={{ width:'100%', maxHeight:'250px', objectFit:'cover', borderRadius:'12px' }}
        />
      </Link>
      <h3>{product.name}</h3>
      <p style={{ fontWeight:'bold', color:'var(--farbe-akzent)' }}>‚Ç¨ {product.price.toFixed(2)}</p>
      <p style={{ color: isInStock ? 'green' : 'red', fontWeight:'bold' }}>
        {isInStock ? 'Sofort verf√ºgbar' : 'Ausverkauft'}
      </p>
      <button
        onClick={() => addToCart(product, 1)}
        disabled={!isInStock}
        style={{
          backgroundColor: isInStock ? 'var(--farbe-prim√§r)' : '#aaa',
          color: 'var(--farbe-hell)',
          borderRadius: '8px',
          padding: '10px 15px',
          fontWeight: 'bold',
          cursor: isInStock ? 'pointer' : 'not-allowed'
        }}
      >
        {isInStock ? 'In den Warenkorb' : 'Nicht lieferbar'}
      </button>
    </div>
  );
};

const Shop = () => {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { cartItems } = useCart();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch(API_URL);
        if (!res.ok) throw new Error('Netzwerkfehler');
        const data = await res.json();
        setProducts(data);
        setFiltered(data);
      } catch (err) {
        setError(err.message);
      } finally { setLoading(false); }
    };
    fetchProducts();
  }, []);

  useEffect(() => {
    const lower = search.toLowerCase();
    setFiltered(products.filter(p => p.name.toLowerCase().includes(lower)));
  }, [search, products]);

  if (loading) return <p style={{ textAlign:'center', padding:'50px' }}>Shop wird geladen...</p>;
  if (error) return <p style={{ textAlign:'center', color:'red', padding:'50px' }}>Fehler: {error}</p>;

  return (
    <div style={{ maxWidth:'1200px', margin:'50px auto', padding:'0 20px' }}>
      <h1 style={{ textAlign:'center', color:'var(--farbe-prim√§r)', marginBottom:'30px' }}>Shop</h1>
      <p style={{ textAlign:'right', fontWeight:'bold' }}>üõí Warenkorb: {cartItems.reduce((sum, item) => sum + item.quantity, 0)} Artikel</p>
      <input
        type="text"
        placeholder="Suche..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ width:'100%', padding:'12px', marginBottom:'30px', borderRadius:'8px', border:'1px solid #ccc' }}
      />

      <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fill,minmax(220px,1fr))', gap:'20px' }}>
        {filtered.map(p => <ProductCard key={p.id} product={p} />)}
      </div>
    </div>
  );
};

export default Shop;
