import React, { useState, useEffect } from 'react';
import ProductForm from './ProductForm'; // Importiert (vermutlich) als default export
// NEU: Import der Login-Komponente
import AdminLogin from './AdminLogin'; 

const API_URL = 'http://localhost:3001/api/products';
const FALLBACK_IMAGE = "https://placehold.co/50x50/eee/333?text=Bild";


// HILFSFUNKTION: Korrigiert Bildpfade, die vom Backend kommen.
const getImageUrl = (imagePath) => {
    // Wenn der Pfad mit '/uploads/' beginnt, muss die Basis-URL hinzugefügt werden
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `http://localhost:3001${imagePath}`;
    }
    // Handhabt externe URLs (wie placehold.co) oder defekte Pfade
    return imagePath;
};


// --- Komponente: Produktliste ---
// Definiert hier, um eine einzige Datei zu behalten.
const AdminProductList = ({ products, onEdit, onDelete }) => {
    
    // Inline-Stile für die Tabelle
    const tableStyle = {
        width: '100%',
        borderCollapse: 'collapse',
        marginTop: '20px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.05)',
        backgroundColor: 'white',
        borderRadius: '8px',
        overflow: 'hidden',
    };

    const headerCellStyle = {
        padding: '12px 15px',
        backgroundColor: 'var(--farbe-primär)',
        color: 'white',
        textAlign: 'left',
        fontWeight: 'bold',
        fontSize: '0.9em',
        textTransform: 'uppercase',
    };

    const dataCellStyle = {
        padding: '10px 15px',
        borderBottom: '1px solid #eee',
        textAlign: 'left',
        verticalAlign: 'middle',
        fontSize: '0.9em',
    };

    const buttonStyle = {
        padding: '5px 10px',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '0.8em',
        fontWeight: 'bold',
        marginLeft: '5px',
        transition: 'background-color 0.2s',
    };
    
    return (
        <div className="admin-list-container">
            <h2 style={{ fontSize: '1.5em', color: '#333', margin: '0 0 20px 0' }}>
                Alle Produkte im System ({products.length})
            </h2>
            {products.length === 0 ? (
                <p style={{ color: '#666' }}>Keine Produkte gefunden. Erstellen Sie das erste Produkt!</p>
            ) : (
                <table style={tableStyle}>
                    <thead>
                        <tr>
                            <th style={headerCellStyle}>Bild</th>
                            <th style={headerCellStyle}>Name</th>
                            <th style={headerCellStyle}>Preis</th>
                            <th style={headerCellStyle}>Lagernd</th>
                            <th style={headerCellStyle}>Featured</th>
                            <th style={headerCellStyle}>Aktionen</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product.id} className="hover:bg-gray-50">
                                <td style={dataCellStyle}>
                                    <img 
                                        src={getImageUrl(product.imageUrl)} 
                                        alt={product.name} 
                                        style={{ width: '50px', height: '50px', objectFit: 'cover', borderRadius: '5px' }} 
                                        onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                                    />
                                </td>
                                <td style={dataCellStyle}>{product.name}</td>
                                <td style={dataCellStyle}>€ {product.price.toFixed(2)}</td>
                                <td style={dataCellStyle}>
                                    {product.stock > 0 
                                        ? <span style={{ color: 'green', fontWeight: 'bold' }}>{product.stock}</span> 
                                        : <span style={{ color: 'red', fontWeight: 'bold' }}>0 (Aus)</span>
                                    }
                                </td>
                                <td style={dataCellStyle}>{product.isFeatured ? '✅' : '❌'}</td>
                                <td style={dataCellStyle}>
                                    <button 
                                        style={{ ...buttonStyle, backgroundColor: 'var(--farbe-akzent)', color: 'white' }}
                                        onClick={() => onEdit(product)}
                                    >
                                        Bearbeiten
                                    </button>
                                    <button 
                                        style={{ ...buttonStyle, backgroundColor: 'var(--farbe-rot)', color: 'white' }}
                                        onClick={() => onDelete(product.id)}
                                    >
                                        Löschen
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};


// --- Hauptkomponente: AdminDashboard (Jetzt mit Login-Verwaltung) ---
const AdminDashboard = () => {
    // NEU: State für den Login-Status
    const [isLoggedIn, setIsLoggedIn] = useState(false); 
    
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentProduct, setCurrentProduct] = useState(null); // Produkt, das bearbeitet wird
    const [statusMessage, setStatusMessage] = useState({ text: '', type: '' }); // Statusmeldung

    // Funktion zum Abrufen der Produkte
    const fetchProducts = async () => {
        // Produkte nur abrufen, wenn eingeloggt oder wenn der Ladevorgang von useEffect kommt (bevor isLoggedIn gesetzt ist)
        if (!isLoggedIn) {
            // Wenn der Dashboard-Teil nicht angezeigt wird, ist isLoading nicht so wichtig.
            return;
        }

        setIsLoading(true);
        setError(null);
        try {
            const response = await fetch(API_URL);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setProducts(data);
        } catch (e) {
            setError('Fehler beim Abrufen der Produkte: ' + e.message);
            console.error(e);
        } finally {
            setIsLoading(false);
        }
    };

    // Beim Laden der Komponente einmalig abrufen
    useEffect(() => {
        // Datenabruf nur starten, wenn wir eingeloggt sind.
        if (isLoggedIn) {
            fetchProducts();
        }
    }, [isLoggedIn]); // Führt fetchProducts aus, sobald isLoggedIn auf true gesetzt wird

    // NEU: Handler für den Login-Erfolg
    const handleLoginSuccess = (success) => {
        setIsLoggedIn(success);
    };

    // Hilfsfunktion zur Anzeige von Statusmeldungen
    const showStatus = (text, type = 'info') => {
        setStatusMessage({ text, type });
        // Nachricht nach 5 Sekunden ausblenden
        setTimeout(() => setStatusMessage({ text: '', type: '' }), 5000);
    };

    // Produkt erstellen/aktualisieren
    const handleProductSubmit = async (formData) => {
        const isUpdate = !!formData.id;
        const url = isUpdate ? `${API_URL}/${formData.id}` : API_URL;
        const method = isUpdate ? 'PUT' : 'POST';

        // FormData für Datei-Uploads vorbereiten
        const productData = new FormData();
        // Alle Felder anhängen (außer der Datei selbst, falls sie null ist)
        for (const key in formData) {
            if (key !== 'imageFile' && formData[key] !== null) {
                productData.append(key, formData[key]);
            }
        }
        
        // Füge die Bilddatei nur an, wenn eine neue ausgewählt wurde
        if (formData.imageFile) {
            productData.append('imageFile', formData.imageFile);
        }

        try {
            const response = await fetch(url, {
                method: method,
                // ACHTUNG: Bei FormData KEINEN 'Content-Type': 'application/json' setzen!
                body: productData,
            });

            if (!response.ok) {
                const errorData = await response.json().catch(() => ({ message: 'Unbekannter Serverfehler' }));
                throw new Error(errorData.message || `Fehler beim ${isUpdate ? 'Speichern' : 'Erstellen'} des Produkts.`);
            }

            // Nach erfolgreicher Operation Daten neu laden und Status setzen
            await fetchProducts();
            showStatus(`Produkt erfolgreich ${isUpdate ? 'aktualisiert' : 'erstellt'}!`, 'success');
            // Bearbeitungsmodus beenden
            setCurrentProduct(null);
        } catch (e) {
            showStatus(`Fehler: ${e.message}`, 'error');
            console.error(e);
        }
    };

    // Produkt löschen
    const handleDeleteProduct = async (id) => {
        // WICHTIG: window.confirm durch ein benutzerdefiniertes Modal ersetzen!
        // Da ich kein Modal erstellen kann, ohne eine weitere Datei einzuführen,
        // verwende ich temporär einen einfachen Dialog, obwohl dies in Canvas nicht empfohlen ist.
        // BESSER: Ein Zustand für ein Bestätigungs-Modal verwenden.
        if (!window.confirm('Sind Sie sicher, dass Sie dieses Produkt löschen möchten?')) {
            return;
        }

        try {
            const response = await fetch(`${API_URL}/${id}`, {
                method: 'DELETE',
            });

            if (!response.ok) {
                throw new Error('Fehler beim Löschen des Produkts.');
            }

            // Nach erfolgreichem Löschen Daten neu laden
            await fetchProducts();
            showStatus('Produkt erfolgreich gelöscht.', 'success');
            // Sicherstellen, dass der Bearbeitungsmodus beendet ist, falls das Produkt gerade bearbeitet wurde
            if (currentProduct && currentProduct.id === id) {
                setCurrentProduct(null);
            }
        } catch (e) {
            showStatus(`Fehler beim Löschen: ${e.message}`, 'error');
            console.error(e);
        }
    };

    // Bearbeitungsmodus starten
    const handleEditProduct = (product) => {
        setCurrentProduct(product);
        showStatus(`Bearbeite Produkt: ${product.name}`, 'info');
        // Optional: Zum Formular scrollen
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };
    
    // Bearbeitungsmodus abbrechen
    const handleCancelEdit = () => {
        setCurrentProduct(null);
        setStatusMessage({ text: '', type: '' });
    };
    
    // NEU: Logout-Funktion
    const handleLogout = () => {
        setIsLoggedIn(false);
        setCurrentProduct(null);
        setProducts([]);
        showStatus('Erfolgreich abgemeldet.', 'info');
    };


    // Status-Nachrichten-Stil (bleibt inline, da dynamisch)
    const statusStyle = {
        padding: '10px',
        margin: '10px 0',
        borderRadius: '8px',
        textAlign: 'center',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: statusMessage.type === 'error' ? 'var(--farbe-rot)' : 'var(--farbe-grün)',
        display: statusMessage.text ? 'block' : 'none', 
    };
    
    // --- Rendering-Logik ---
    
    // Wenn nicht eingeloggt, zeige die Login-Komponente
    if (!isLoggedIn) {
        return (
            <div className="admin-dashboard-container">
                <AdminLogin onLogin={handleLoginSuccess} />
            </div>
        );
    }
    
    // Wenn eingeloggt, zeige das Dashboard
    
    if (isLoading) {
        return <div className="admin-dashboard-container" style={{ textAlign: 'center', padding: '20px' }}>Lädt Produkte...</div>;
    }

    if (error) {
        return <div className="admin-dashboard-container" style={{ color: 'var(--farbe-rot)', padding: '20px' }}>Fehler beim Laden: {error}</div>;
    }

    return (
        <div className="admin-dashboard-container">
            <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
                <h1 style={{ margin: 0, color: 'var(--farbe-primär)' }}>Admin-Dashboard</h1>
                <button 
                    onClick={handleLogout} 
                    style={{ padding: '8px 15px', backgroundColor: 'var(--farbe-rot)', color: 'white', borderRadius: '5px', border: 'none', cursor: 'pointer' }}
                >
                    Abmelden
                </button>
            </header>
            
            <div style={statusStyle}>{statusMessage.text}</div>

            {/* 1. Spalte: Produktformular */}
            <ProductForm
                key={currentProduct ? currentProduct.id : 'new'} 
                productToEdit={currentProduct}
                onSubmit={handleProductSubmit}
                onCancel={handleCancelEdit}
            />

            {/* 2. Spalte: Produktliste */}
            <AdminProductList 
                products={products}
                onEdit={handleEditProduct}
                onDelete={handleDeleteProduct}
            />
        </div>
    );
};

export default AdminDashboard;
