import React, { useState } from 'react';

const AdminLogin = ({ onLogin }) => {
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch('http://localhost:3001/api/admin/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ password })
            });
            if (!res.ok) throw new Error('Falsches Passwort');
            const data = await res.json();
            localStorage.setItem('adminToken', data.token);
            onLogin();
        } catch (err) {
            setError('❌ ' + err.message);
        }
    };

    return (
        <div style={{ maxWidth: 400, margin: '80px auto', padding: 20, background: '#fff', borderRadius: 10 }}>
            <h2 style={{ textAlign: 'center' }}>Admin Login</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="password"
                    placeholder="Admin Passwort"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    style={{ width: '100%', padding: 10, marginBottom: 15 }}
                    required
                />
                {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
                <button type="submit" style={{ width: '100%', padding: 10, background: '#5A4747', color: '#fff', border: 'none', borderRadius: 5 }}>
                    Einloggen
                </button>
            </form>
        </div>
    );
};

export default AdminLogin;
