// src/App.js
import React, { useState, useEffect } from 'react'; 
import { Routes, Route, Link, useParams, useNavigate } from 'react-router-dom'; 

import Home from './components/Home';
import Shop from './components/Shop';
import AdminDashboard from './components/AdminDashboard';
import AdminLogin from './components/AdminLogin';
import ProductDetail from './components/ProductDetail';
import Stoffe from './components/Stoffe';

import { CartProvider } from './components/CartContext';

const MissingComponent = ({ name }) => (
    <div style={{ padding: '50px', textAlign: 'center', color: 'red', border: '1px solid red', margin: '20px' }}>
        <h3>⚠️ Fehler: {name} nicht gefunden</h3>
        <p>Bitte stellen Sie sicher, dass die Datei <code>src/components/{name}.js</code> existiert.</p>
    </div>
);

const ProductDetailWrapper = () => {
    const { productId } = useParams();
    const navigate = useNavigate();
    return ProductDetail ? <ProductDetail productId={productId} navigate={navigate} /> : <MissingComponent name="ProductDetail" />;
};

function App() {
    const navigate = useNavigate();
    const [isAdmin, setIsAdmin] = useState(false);
    const [isCheckingAuth, setIsCheckingAuth] = useState(true);

    // ✅ Prüfen, ob Token im localStorage vorhanden ist
    useEffect(() => {
        const token = localStorage.getItem('adminToken');
        if (token) {
            setIsAdmin(true);
        }
        setIsCheckingAuth(false);
    }, []);

    // ✅ Login-Callback
    const handleLogin = () => {
        setIsAdmin(true);
        navigate('/admin');
    };

    // ✅ Logout-Callback
    const handleLogout = () => {
        localStorage.removeItem('adminToken');
        setIsAdmin(false);
        navigate('/');
    };

    // ✅ Geschützte Admin-Route
    const AdminProtectedRoute = () => {
        if (isCheckingAuth) return <p style={{textAlign:'center', padding:'50px'}}>Lade Admin...</p>;
        return isAdmin ? <AdminDashboard onLogout={handleLogout} /> : <AdminLogin onLogin={handleLogin} />;
    };

    const styles = {
        nav: {
            display: 'flex', justifyContent: 'space-between', alignItems: 'center',
            padding: '15px 50px', backgroundColor: '#5A4747', color: 'white',
        },
        link: { color: 'white', textDecoration: 'none', margin: '0 10px', fontWeight: 'bold' }
    };

    return (
        <div className="App" style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
            <CartProvider>
                <header>
                    <nav style={styles.nav}>
                        <Link to="/" style={{...styles.link, fontSize: '1.5em'}}>Henkes-Stoffzauber</Link>
                        <div>
                            <Link to="/" style={styles.link}>Startseite</Link>
                            <Link to="/shop" style={styles.link}>Shop</Link>
                            <Link to="/stoffe" style={styles.link}>Stoffe</Link>
                            <Link to="/admin" style={{...styles.link, color: '#F2B2B4'}}>
                                {isAdmin ? 'Dashboard' : 'Login'}
                            </Link>
                        </div>
                    </nav>
                </header>

                <main style={{ flexGrow: 1 }}>
                    <Routes>
                        <Route path="/" element={<Home />} /> 
                        <Route path="/shop" element={<Shop />} />
                        <Route path="/shop/:productId" element={<ProductDetailWrapper />} /> 

                        <Route path="/stoffe" element={<Stoffe />} />
                        <Route path="/admin" element={<AdminProtectedRoute />} />
                        <Route path="*" element={<h2 style={{textAlign:'center', padding:'30px'}}>404 - Seite nicht gefunden</h2>} />
                    </Routes>
                </main>

                <footer style={{ padding: '20px', backgroundColor: '#5A4747', color: 'white', textAlign: 'center' }}>
                    © 2025 Henkes-Stoffzauber.de
                </footer>
            </CartProvider>
        </div>
    );
}

export default App;
