// src/components/Shop.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import './Shop.css';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getImageUrl = (imagePath) => imagePath?.startsWith('/uploads/') ? `${BACKEND_URL}${imagePath}` : imagePath || FALLBACK_IMAGE_URL;

const ProductCard = ({ product, onAdd }) => (
  <div className="product-card">
    <Link to={`/shop/${product.id}`}>
      <img src={getImageUrl(product.imageUrl)} alt={product.name} onError={e => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }} />
    </Link>
    <h3>{product.name}</h3>
    <p className="price-tag">‚Ç¨ {product.price.toFixed(2)}</p>
    <p className={product.stock > 0 ? 'instock' : 'outstock'}>{product.stock > 0 ? 'Sofort verf√ºgbar' : 'Ausverkauft'}</p>
    <button className="heart-button" onClick={() => onAdd(product)} disabled={product.stock <= 0}>
      {product.stock > 0 ? 'In den Warenkorb' : 'Nicht lieferbar'}
    </button>
  </div>
);

const Shop = () => {
  const { cartItems, addToCart, removeOneFromCart, removeFromCart, clearCart, getTotalPrice } = useCart();
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState('');
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(console.error);
  }, []);

  const filtered = products.filter(p => p.name.toLowerCase().includes(search.toLowerCase()));

  return (
    <div className="shop-container">
      <h1 className="shop-title">Schau dich um ‚ú®</h1>

      <input type="text" placeholder="Suche ..." value={search} onChange={e => setSearch(e.target.value)} className="search-input" />

      <div className="product-grid">
        {filtered.map(p => <ProductCard key={p.id} product={p} onAdd={addToCart} />)}
      </div>

      {/* Sticky Cart */}
      <div className={`sticky-cart ${showCart ? 'open' : ''}`}>
        <h3>üõí Dein Warenkorb</h3>
        {cartItems.length === 0 ? <p>Dein Warenkorb ist leer.</p> :
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                {item.name} x {item.quantity} ‚Äì ‚Ç¨ {(item.price * item.quantity).toFixed(2)}
                <button onClick={() => removeOneFromCart(item.id)}>‚ûñ</button>
                <button onClick={() => addToCart(item, 1)}>‚ûï</button>
                <button onClick={() => removeFromCart(item.id)}>‚ùå</button>
              </li>
            ))}
          </ul>
        }
        {cartItems.length > 0 && (
          <>
            <p className="total-price">Gesamt: ‚Ç¨ {getTotalPrice().toFixed(2)}</p>
            <button onClick={() => alert('Zur Kasse gehen‚Ä¶')}>Bezahlen</button>
            <button onClick={clearCart}>Alles l√∂schen</button>
          </>
        )}
        <button onClick={() => setShowCart(false)}>Schlie√üen</button>
      </div>

      {/* Floating Button */}
      <button className="open-cart-button" onClick={() => setShowCart(prev => !prev)}>
        üõí {cartItems.reduce((sum, i) => sum + i.quantity, 0)}
      </button>
    </div>
  );
};

export default Shop;
