// src/components/Shop.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import './Shop.css';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getAuthHeaders = () => {
  const token = localStorage.getItem('adminToken');
  return token ? { Authorization: `Bearer ${token}` } : {};
};

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

// ---------------- Product Card ----------------
const ProductCard = ({ product, onAdd }) => {
  const isInStock = product.stock > 0;

  return (
    <div className="product-card">
      <Link to={`/shop/${product.id}`}>
        <img
          src={getImageUrl(product.imageUrl)}
          alt={product.name}
          onError={e => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
        />
      </Link>
      <h3>{product.name}</h3>
      <p className="price-tag">‚Ç¨ {product.price.toFixed(2)}</p>
      <p className={isInStock ? 'instock' : 'outstock'}>
        {isInStock ? 'Sofort verf√ºgbar' : 'Ausverkauft'}
      </p>
      <button
        className="heart-button"
        onClick={() => onAdd(product)}
        disabled={!isInStock}
      >
        {isInStock ? 'In den Warenkorb' : 'Nicht lieferbar'}
      </button>
    </div>
  );
};

// ---------------- Shop ----------------
const Shop = () => {
  const { cartItems, addToCart, removeFromCart, updateQuantity, getTotalPrice } = useCart();
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch(API_URL, { headers: getAuthHeaders() });
        if (!res.ok) throw new Error(`Netzwerkfehler: ${res.status}`);
        const data = await res.json();
        setProducts(data);
        setFiltered(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  useEffect(() => {
    const lower = search.toLowerCase();
    setFiltered(products.filter(p => p.name.toLowerCase().includes(lower)));
  }, [search, products]);

  const handleAddToCart = (product) => {
    addToCart(product, 1);
    setShowCart(true);
  };

  const handleRemove = (id) => {
    removeFromCart(id);
  };

  const handleQuantityChange = (id, value) => {
    const qty = Math.max(1, parseInt(value) || 1);
    updateQuantity(id, qty);
  };

  if (loading) return <p className="loading">Shop wird geladen...</p>;
  if (error) return <p className="error">Fehler: {error}</p>;

  return (
    <div className="shop-container">
      <h1 className="shop-title">Schau dich um ‚ú®</h1>

      <input
        type="text"
        placeholder="Suche ..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="search-input"
      />

      {filtered.length === 0 ? (
        <p className="no-results">Kein Produkt gefunden.</p>
      ) : (
        <div className="product-grid">
          {filtered.map(p => (
            <ProductCard key={p.id} product={p} onAdd={handleAddToCart} />
          ))}
        </div>
      )}

      {/* üå∏ Sticky Cart */}
      <div className={`sticky-cart ${showCart ? 'open' : ''}`}>
        <h3>üõí Dein Warenkorb</h3>
        {cartItems.length === 0 ? (
          <p>Dein Warenkorb ist leer.</p>
        ) : (
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                {item.name} - ‚Ç¨ {item.price.toFixed(2)} x 
                <input 
                  type="number" 
                  value={item.quantity} 
                  min="1"
                  onChange={(e) => handleQuantityChange(item.id, e.target.value)} 
                  style={{width: '50px', marginLeft: '5px', marginRight: '5px'}}
                />
                = ‚Ç¨ {(item.price * item.quantity).toFixed(2)}
                <button className="remove-btn" onClick={() => handleRemove(item.id)}>‚ùå</button>
              </li>
            ))}
          </ul>
        )}
        {cartItems.length > 0 && (
          <>
            <p className="total">Gesamt: ‚Ç¨ {getTotalPrice().toFixed(2)}</p>
            <button className="checkout-btn">Zur Kasse</button>
          </>
        )}
        <button className="close-cart-btn" onClick={() => setShowCart(false)}>Schlie√üen</button>
      </div>

      {/* üå∏ Floating Cart Button */}
      <button className="open-cart-button" onClick={() => setShowCart(!showCart)}>
        üõí {cartItems.reduce((sum, i) => sum + i.quantity, 0)}
      </button>
    </div>
  );
};

export default Shop;
