import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/500x500/F2B2B4/4F3C3C?text=Stoffmuster";

// Hilfsfunktion zur Generierung der korrekten Bild-URL
const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

const ProductDetail = ({ productId, navigate }) => {
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [fabrics, setFabrics] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [message, setMessage] = useState('');

  const isInStock = product && product.stock > 0;

  useEffect(() => {
    const fetchProductAndFabrics = async () => {
      setIsLoading(true);
      setError(null);

      try {
        const productRes = await fetch(`${API_URL}/products/${productId}`);
        if (!productRes.ok) throw new Error('Produkt nicht gefunden');
        const productData = await productRes.json();
        setProduct(productData);

        const fabricsRes = await fetch(`${API_URL}/fabrics`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('adminToken')}` }
        });
        if (fabricsRes.ok) {
          const fabricsData = await fabricsRes.json();
          setFabrics(fabricsData);
        }

        if (productData.stock < quantity) setQuantity(Math.max(1, productData.stock));
      } catch (err) {
        console.error(err);
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProductAndFabrics();
  }, [productId, quantity]);

  const fabricName = product && fabrics.length
    ? (fabrics.find(f => f.id === Number(product.fabricId))?.name || 'N/A')
    : 'N/A';

  const handleAddToCart = () => {
    if (!product || !isInStock) return;
    addToCart(product, quantity);
    setMessage(`${quantity}x ${product.name} wurde zum Warenkorb hinzugefügt.`);
    setTimeout(() => setMessage(''), 3000);
  };

  if (isLoading) return <p style={{ textAlign:'center', padding:'50px' }}>Produktdetails werden geladen...</p>;
  if (error) return <p style={{ textAlign:'center', color:'red', fontWeight:'bold' }}>Fehler: {error}</p>;
  if (!product) return <p style={{ textAlign:'center' }}>Produkt nicht verfügbar.</p>;

  return (
    <div style={{ maxWidth:'1000px', margin:'50px auto', padding:'20px' }}>
      <div style={{ display:'flex', gap:'30px', backgroundColor:'var(--farbe-hell)', borderRadius:'12px', padding:'30px', boxShadow:'0 8px 25px rgba(0,0,0,0.1)', flexWrap:'wrap' }}>
        
        <div style={{ flex:'1 1 40%', minWidth:'300px' }}>
          <img 
            src={getImageUrl(product.imageUrl)} 
            alt={product.name} 
            style={{ width:'100%', height:'auto', borderRadius:'8px', objectFit:'cover', aspectRatio:'1 / 1' }}
            onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
          />
        </div>

        <div style={{ flex:'1 1 60%' }}>
          <h1 style={{ color:'var(--farbe-primär)', fontSize:'2.5em', marginBottom:'10px' }}>{product.name}</h1>
          <p style={{ color:'var(--farbe-text-dunkel)', fontSize:'1.1em', lineHeight:'1.6', marginBottom:'20px' }}>{product.description}</p>

          <table style={{ width:'100%', borderCollapse:'collapse', marginTop:'20px', fontSize:'0.95em' }}>
            <tbody>
              <tr style={{ borderBottom:'1px solid #eee' }}>
                <td style={{ padding:'10px 0', fontWeight:'bold', width:'30%', color:'var(--farbe-primär)' }}>Stoffart:</td>
                <td style={{ padding:'10px 0', color:'var(--farbe-text-dunkel)' }}>{fabricName}</td>
              </tr>
              <tr style={{ borderBottom:'1px solid #eee' }}>
                <td style={{ padding:'10px 0', fontWeight:'bold', color:'var(--farbe-primär)' }}>Bestand:</td>
                <td style={{ padding:'10px 0', color:'var(--farbe-text-dunkel)' }}>
                  {product.stock > 0 ? `${product.stock} Einheiten verfügbar` : 'Momentan ausverkauft'}
                </td>
              </tr>
              <tr style={{ borderBottom:'1px solid #eee' }}>
                <td style={{ padding:'10px 0', fontWeight:'bold', color:'var(--farbe-primär)' }}>Preis:</td>
                <td style={{ padding:'10px 0', color:'var(--farbe-text-dunkel)' }}>
                  <span style={{ color:'var(--farbe-akzent)', fontWeight:'bold' }}>{product.price.toFixed(2)} €</span>
                </td>
              </tr>
            </tbody>
          </table>

          <div style={{ marginTop:'30px', display:'flex', alignItems:'center', gap:'15px' }}>
            <input
              type="number"
              min="1"
              max={product.stock}
              value={quantity}
              onChange={(e) => setQuantity(Math.max(1, Math.min(product.stock, parseInt(e.target.value) || 1)))}
              style={{ width:'60px', padding:'10px', border:'1px solid #ccc', borderRadius:'5px', textAlign:'center' }}
              disabled={!isInStock}
            />
            <button
              onClick={handleAddToCart}
              disabled={!isInStock}
              style={{
                backgroundColor: isInStock ? 'var(--farbe-akzent)' : 'var(--farbe-grau)',
                color:'white',
                padding:'10px 20px',
                borderRadius:'5px',
                border:'none',
                cursor:isInStock?'pointer':'not-allowed',
                fontWeight:'bold'
              }}
            >
              {isInStock ? 'In den Warenkorb' : 'Nicht verfügbar'}
            </button>
          </div>

          {message && <div style={{ marginTop:'20px', padding:'10px', backgroundColor:'var(--farbe-akzent-hell)', color:'var(--farbe-akzent)', borderRadius:'5px', fontWeight:'bold' }}>{message}</div>}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
