// client/src/components/Shop.js

import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:3001/api/products';


// Hilfsfunktion zur Generierung der korrekten Bild-URL (FIX FÜR BILDER)
const getImageUrl = (imagePath) => {
    // Wenn der Pfad mit '/uploads/' beginnt, muss die Basis-URL hinzugefügt werden
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `http://localhost:3001${imagePath}`;
    }
    // Ansonsten ist es eine externe URL oder ein Fallback-Pfad
    return imagePath;
};


// --- Shop-Produktkarte ---
const ProductCard = ({ product }) => {
    const cardStyle = {
        backgroundColor: 'var(--farbe-hell)',
        borderRadius: '12px',
        overflow: 'hidden',
        boxShadow: '0 8px 16px rgba(0, 0, 0, 0.1)',
        transition: 'transform 0.3s, box-shadow 0.3s',
        display: 'flex',
        flexDirection: 'column',
        textDecoration: 'none', // Link-Stil entfernen
        color: 'inherit',
        height: '100%',
        cursor: 'pointer',
    };

    const imageStyle = {
        width: '100%',
        // Platzhaltergröße, bis das echte Bild geladen wird
        aspectRatio: '1 / 1', 
        objectFit: 'cover',
        borderBottom: '1px solid #eee',
    };
    
    // Link zur neuen Detailseite `/product/:id`
    const linkPath = `/product/${product.id}`;

    // Inline-Style für Button-Hover
    const [isHovered, setIsHovered] = useState(false);

    const buttonStyle = {
        padding: '8px 15px',
        borderRadius: '6px',
        border: 'none',
        backgroundColor: isHovered ? 'var(--farbe-akzent)' : 'var(--farbe-akzent)', // Bei Hover bleibt die Farbe (nur für Style-Konsistenz)
        color: 'white',
        fontWeight: '600',
        cursor: 'pointer',
        transition: 'background-color 0.2s',
    };

    return (
        <a 
            href={linkPath} // Href für den nativen Link
            onClick={(e) => {
                // Verhindert das Neuladen der Seite und nutzt den App-internen Router
                e.preventDefault(); 
                window.history.pushState({}, "", linkPath);
                window.dispatchEvent(new PopStateEvent('popstate'));
            }}
            style={cardStyle}
            onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'translateY(-5px)';
                e.currentTarget.style.boxShadow = '0 12px 20px rgba(0, 0, 0, 0.15)';
            }}
            onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = '0 8px 16px rgba(0, 0, 0, 0.1)';
            }}
        >
            <img 
                src={getImageUrl(product.imageUrl)} 
                alt={product.name} 
                style={imageStyle} 
                onError={(e) => {
                    // Fallback, falls Bild nicht geladen werden kann
                    e.target.onerror = null; // Vermeidet endlose Schleife
                    e.target.src = "https://placehold.co/450x450/ccc/333?text=Bild+fehlt";
                }}
            />
            <div style={{ padding: '15px', flexGrow: 1 }}>
                <h3 style={{ margin: '0 0 5px 0', fontSize: '1.2em', color: 'var(--farbe-text-dunkel)' }}>
                    {product.name}
                </h3>
                {/* Beschreibung kurz halten */}
                <p style={{ margin: '0 0 10px 0', fontSize: '0.9em', color: '#666', overflow: 'hidden', textOverflow: 'ellipsis', display: '-webkit-box', WebkitLineClamp: 2, WebkitBoxOrient: 'vertical', minHeight: '36px' }}>
                    {product.description}
                </p>
            </div>
            <div style={{ padding: '15px', borderTop: '1px solid #eee', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span style={{ fontSize: '1.4em', fontWeight: 'bold', color: 'var(--farbe-akzent)' }}>
                    € {product.price.toFixed(2)}
                </span>
                <button 
                    style={buttonStyle}
                    onMouseEnter={() => setIsHovered(true)}
                    onMouseLeave={() => setIsHovered(false)}
                    onClick={(e) => {
                        // Verhindert, dass der Klick auf den Button den Link auslöst
                        e.stopPropagation(); 
                        alert(`"${product.name}" in den Warenkorb gelegt! (Funktionalität fehlt noch)`);
                    }}
                >
                    In den Warenkorb
                </button>
            </div>
        </a>
    );
};


// --- Hauptkomponente Shop ---
const Shop = () => {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch(API_URL);
                if (!response.ok) throw new Error('Fehler beim Laden der Produkte.');
                const data = await response.json();
                setProducts(data);
                setError(null);
            } catch (e) {
                console.error("Fetch error:", e);
                setError('Die Produkte konnten nicht geladen werden. Bitte prüfen Sie den Backend-Server.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchProducts();
    }, []);

    const shopContainerStyle = {
        // Layout-Verbesserung: Volle Breite nutzen und padding hinzufügen
        maxWidth: '1200px',
        margin: '20px auto', 
        padding: '0 20px', 
    };

    const gridStyle = {
        display: 'grid',
        // Minimale Spaltenbreite von 280px für Mobil und Desktop
        gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', 
        gap: '30px',
        marginTop: '30px',
    };

    if (isLoading) {
        return (
            <div style={shopContainerStyle}>
                <p style={{ textAlign: 'center', fontSize: '1.2em', color: 'var(--farbe-text-dunkel)' }}>
                    Produkte werden geladen...
                </p>
            </div>
        );
    }
    
    if (error) {
        return (
            <div style={shopContainerStyle}>
                <p style={{ textAlign: 'center', fontSize: '1.2em', color: 'red' }}>
                    {error}
                </p>
            </div>
        );
    }

    return (
        <div style={shopContainerStyle}>
            <h1 style={{ textAlign: 'center', color: 'var(--farbe-primär)', marginBottom: '30px' }}>
                Schau dich Um
            </h1>
            
            <div style={gridStyle}>
                {products.length > 0 ? (
                    products.map(product => (
                        <ProductCard key={product.id} product={product} />
                    ))
                ) : (
                    <div style={{ gridColumn: '1 / -1', textAlign: 'center', padding: '50px', backgroundColor: 'var(--farbe-hell)', borderRadius: '10px' }}>
                        <p style={{ fontSize: '1.1em', color: '#666' }}>
                            Es sind noch keine Produkte im Shop vorhanden. Bitte legen Sie welche im Admin-Bereich an!
                        </p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Shop;
