// client/src/components/Stoffe.js
import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';

// WICHTIG: Die URL muss später auf einen funktionierenden Backend-Endpoint angepasst werden.
const API_URL = 'http://localhost:3001/api/fabrics'; 
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/D9E3F0/333?text=Stoffmuster";

/**
 * HILFSFUNKTION: Korrigiert Bildpfade, die vom Backend kommen.
 */
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    return imagePath || FALLBACK_IMAGE_URL;
};

// Liste der möglichen Verwendungszwecke für die Filter
const USAGE_OPTIONS = [
    'Mütze (Außen)', 'Mütze (Innen)', 'Bündchen', 'Bekleidung (Oberteil)', 
    'Bekleidung (Hose)', 'Babybekleidung', 'Deko', 'Taschen', 'Accessoires'
];

// Stil-Definitionen
const styles = {
    container: {
        maxWidth: '1200px',
        margin: '50px auto',
        padding: '0 20px',
    },
    title: {
        color: 'var(--farbe-primär)',
        textAlign: 'center',
        marginBottom: '40px',
        fontSize: '2.2em'
    },
    filterGroup: {
        display: 'flex',
        flexWrap: 'wrap',
        gap: '15px',
        marginBottom: '30px',
        padding: '15px',
        backgroundColor: 'var(--farbe-hell)',
        borderRadius: '10px',
        boxShadow: '0 2px 5px rgba(0,0,0,0.05)'
    },
    select: {
        padding: '10px',
        borderRadius: '5px',
        border: '1px solid #ddd',
        minWidth: '150px'
    },
    grid: {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
        gap: '30px',
    },
    card: {
        backgroundColor: 'white',
        borderRadius: '12px',
        boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
        overflow: 'hidden',
        transition: 'transform 0.3s, box-shadow 0.3s',
        cursor: 'pointer',
    },
    cardHover: {
        transform: 'translateY(-5px)',
        boxShadow: '0 8px 20px rgba(0, 0, 0, 0.15)',
    },
    image: {
        width: '100%',
        height: '250px',
        objectFit: 'cover',
        borderBottom: '1px solid #eee',
    },
    cardContent: {
        padding: '15px',
    },
    cardTitle: {
        fontSize: '1.4em',
        color: 'var(--farbe-primär-dunkel)',
        marginBottom: '8px',
        fontWeight: '600'
    },
    cardDescription: {
        fontSize: '0.9em',
        color: 'var(--farbe-text-dunkel)',
        marginBottom: '10px',
        height: '40px',
        overflow: 'hidden'
    }
};


const Stoffe = () => {
    const navigate = useNavigate();
    const [stoffe, setStoffe] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [fabricTypeFilter, setFabricTypeFilter] = useState('');
    const [usageFilter, setUsageFilter] = useState('');
    const [hoveredStoffId, setHoveredStoffId] = useState(null);

    // Liste der Stoffarten (muss mit StoffForm.js synchronisiert sein)
    const FABRIC_TYPES = [
        'Jersey', 'French Terry (Sommersweat)', 'Sweat', 'Bündchenstoff', 'Musselin', 
        'Webware Baumwolle', 'Cord', 'Fleece', 'Softshell', 'Leder (Imitat/Echt)'
    ];

    /**
     * NEUE FUNKTION: Ruft alle Stoffe vom Backend ab.
     */
    const fetchFabrics = async () => {
        setIsLoading(true);
        setError(null);
        try {
            const response = await fetch(API_URL);
            if (!response.ok) {
                // Erfasst den 404-Fehler besser
                const errorBody = await response.text();
                throw new Error(`Netzwerkfehler: ${response.status}. Antwort: ${errorBody.substring(0, 100)}`);
            }
            const data = await response.json();
            setStoffe(data);
        } catch (err) {
            console.error('Fehler beim Abrufen der Stoffe:', err);
            setError(`Fehler beim Laden der Stoffe: ${err.message}`);
        } finally {
            setIsLoading(false);
        }
    };

    // Beim Laden der Komponente Stoffe abrufen
    useEffect(() => {
        // WICHTIG: Stellt sicher, dass hier NUR fetchFabrics() aufgerufen wird
        fetchFabrics(); 
    }, []); // Leeres Array bedeutet, dass es nur einmal nach dem ersten Rendern ausgeführt wird.


    // Filterlogik
    const filteredStoffe = useMemo(() => {
        return stoffe.filter(stoff => {
            const matchesType = fabricTypeFilter 
                ? stoff.fabricType === fabricTypeFilter 
                : true;
            
            const matchesUsage = usageFilter
                ? stoff.suitableFor && stoff.suitableFor.includes(usageFilter)
                : true;
                
            return matchesType && matchesUsage;
        });
    }, [stoffe, fabricTypeFilter, usageFilter]);


    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Stoffe werden geladen...</p>;
    }
    
    if (error) {
        return <p style={{ textAlign: 'center', color: '#f44336', padding: '50px', fontWeight: 'bold' }}>{error}</p>;
    }


    return (
        <div style={styles.container}>
            <h1 style={styles.title}>Stoffe-Übersicht & Inspiration</h1>
            
            {/* Filter-Bereich */}
            <div style={styles.filterGroup}>
                {/* Stoffart-Filter */}
                <select 
                    value={fabricTypeFilter}
                    onChange={(e) => setFabricTypeFilter(e.target.value)}
                    style={styles.select}
                >
                    <option value="">Alle Stoffarten</option>
                    {FABRIC_TYPES.map(type => (
                        <option key={type} value={type}>{type}</option>
                    ))}
                </select>

                {/* Verwendungszweck-Filter */}
                <select
                    value={usageFilter}
                    onChange={(e) => setUsageFilter(e.target.value)}
                    style={styles.select}
                >
                    <option value="">Alle Verwendungszwecke</option>
                    {USAGE_OPTIONS.map(usage => (
                        <option key={usage} value={usage}>{usage}</option>
                    ))}
                </select>
                
                {/* Reset Button */}
                {(fabricTypeFilter || usageFilter) && (
                    <button
                        onClick={() => { setFabricTypeFilter(''); setUsageFilter(''); }}
                        style={{ ...styles.select, backgroundColor: 'var(--farbe-akzent)', color: 'white', border: 'none', cursor: 'pointer' }}
                    >
                        Filter zurücksetzen
                    </button>
                )}
            </div>

            {/* Stoff-Grid */}
            <div style={styles.grid}>
                {filteredStoffe.length > 0 ? (
                    filteredStoffe.map(stoff => (
                        <div 
                            key={stoff.id} 
                            style={{ 
                                ...styles.card, 
                                ...(hoveredStoffId === stoff.id ? styles.cardHover : {})
                            }}
                            onMouseEnter={() => setHoveredStoffId(stoff.id)}
                            onMouseLeave={() => setHoveredStoffId(null)}
                            // NEU: Beim Klick auf eine Stoffkarte zur Shop-Seite navigieren
                            onClick={() => navigate(`/shop?fabricId=${stoff.id}`)}
                        >
                            <img 
                                src={getImageUrl(stoff.imageUrl)} 
                                alt={stoff.name} 
                                onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
                                style={styles.image} 
                            />
                            <div style={styles.cardContent}>
                                <h3 style={styles.cardTitle}>{stoff.name}</h3>
                                <p style={styles.cardDescription}>
                                    {stoff.description || 'Keine Beschreibung vorhanden.'}
                                </p>
                                <p className="text-sm font-semibold" style={{ color: 'var(--farbe-sekundär)' }}>
                                    Art: {stoff.fabricType || 'N/A'}
                                </p>
                                <p className="text-xs" style={{ color: 'var(--farbe-text-hell)' }}>
                                    Bestand: {stoff.stock || 0} m
                                </p>
                                
                                {/* Verwendungszwecke als Tags anzeigen */}
                                <div className="flex flex-wrap gap-1 mt-2">
                                    {stoff.suitableFor && stoff.suitableFor.slice(0, 3).map(usage => (
                                        <span key={usage} className="text-xs px-2 py-1 rounded-full" style={{ backgroundColor: 'var(--farbe-primär-dunkel)', color: 'white' }}>
                                            {usage}
                                        </span>
                                    ))}
                                    {stoff.suitableFor && stoff.suitableFor.length > 3 && (
                                        <span className="text-xs px-2 py-1 rounded-full" style={{ backgroundColor: 'var(--farbe-grau)', color: 'var(--farbe-text)' }}>
                                            +{stoff.suitableFor.length - 3}
                                        </span>
                                    )}
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="col-span-full text-center text-lg mt-10" style={{ color: 'var(--farbe-text-dunkel)' }}>
                        Keine Stoffe gefunden, die den Kriterien entsprechen.
                    </p>
                )}
            </div>
        </div>
    );
};

export default Stoffe;
