// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogOut, Edit, Trash2, PlusCircle, CheckCircle, XCircle } from 'lucide-react';
import ProductForm from './ProductForm'; 
import FabricForm from './StoffForm';     // Import der StoffForm
// ANNAHME: AdminProductList und AdminFabricList sind ebenfalls importiert


const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

// --- Hilfsfunktionen für Admin-Operationen ---

/**
 * Ruft den Admin-Authentifizierungstoken aus dem localStorage ab.
 * @param {boolean} isFormData - Soll Content-Type weggelassen werden (bei FormData-Uploads)?
 * @returns {object} Ein Objekt mit Headern oder einem Fehler.
 */
const getAuthHeaders = (isFormData = false) => {
    const adminToken = localStorage.getItem('adminToken');
    if (!adminToken) {
        // Obwohl der AdminProtectedRoute dies verhindern sollte, ist dies eine zusätzliche Absicherung
        return { error: 'Admin-Sitzung abgelaufen oder nicht gefunden.' };
    }
    
    // Dynamische Content-Type Handhabung. Content-Type wird bei FormData NICHT manuell gesetzt.
    const headers = { 'Authorization': `Bearer ${adminToken}` };
    if (!isFormData) {
        headers['Content-Type'] = 'application/json';
    }
    
    return headers;
};

// ... (Annahme: Weitere CRUD-Funktionen wie fetchProducts, deleteProduct etc. existieren hier)

// --- Produkt-Logik (unverändert gelassen für diesen Fix, aber muss existieren) ---
const handleProductSubmit = async (productData, isEditing) => { 
    console.log('Produkt-Logik wird hier ausgeführt...');
    // Logik für Produkte muss hier ebenfalls die robustere Fehlerbehandlung nutzen
};

// --- Stoff-Logik (Ersetzt handleAddFabric) ---

/**
 * Behandelt das Hinzufügen oder Bearbeiten eines Stoffes.
 * @param {FormData} fabricData - Die zu speichernden Stoffdaten (inkl. Bilddatei).
 * @param {string | null} fabricId - Die ID des zu bearbeitenden Stoffes (null beim Hinzufügen).
 */
const handleFabricSubmit = useCallback(async (fabricData, fabricId = null) => {
    // ANNAHME: setIsLoading/setError/setFabrics sind als Dependencies bekannt
    // Fügen Sie Ihre eigenen Dependencies hinzu, falls nötig.
    
    // Lokale States im Dashboard (müssen definiert sein)
    // setIs... / setErr... müssen aus dem useState-Hook kommen
    
    // Nur zur Simulation:
    const setIsLoading = (val) => console.log('Ladezustand:', val);
    const setError = (msg) => console.error('Fehler:', msg);
    // setFabrics muss von useState kommen, um die UI zu aktualisieren
    const setFabrics = (updateFn) => console.log('Stoffe aktualisiert.'); 

    setIsLoading(true);
    setError(null);
    
    // 1. Header abrufen (wichtig: isFormData=true, da wir FormData verwenden)
    const headers = getAuthHeaders(true); 
    if (headers.error) {
        setError(headers.error);
        setIsLoading(false);
        return;
    }

    const url = fabricId 
        ? `${API_URL}/fabrics/${fabricId}` 
        : `${API_URL}/fabrics`;
    const method = fabricId ? 'PUT' : 'POST';

    try {
        const response = await fetch(url, {
            method: method,
            // Wichtig: Bei FormData wird der Content-Type NICHT manuell gesetzt. 
            // Wir senden nur den Auth-Header.
            headers: { 'Authorization': headers['Authorization'] },
            body: fabricData, // FormData-Objekt
        });
        
        // 2. WICHTIGER FIX für den "Unexpected token 'U'" Fehler: 
        // Behandelt nicht-JSON-Fehlerantworten wie "Unauthorized".
        if (!response.ok) {
            let errorData;
            const contentType = response.headers.get("content-type");
            
            if (contentType && contentType.includes("application/json")) {
                // Wenn der Server JSON als Fehler zurückgibt
                errorData = await response.json();
                throw new Error(errorData.message || `Fehler ${response.status}: JSON-Fehler`);
            } else {
                // Wenn der Server KEIN JSON zurückgibt (z.B. "Unauthorized" Text)
                const errorText = await response.text();
                // Die Ausgabe des Textes verhindert den JSON-Parsing-Fehler.
                throw new Error(`Fehler ${response.status}: ${errorText || response.statusText}.`);
            }
        }
        
        // Bei erfolgreicher Antwort, versuche, JSON zu parsen (falls der Server etwas zurückgibt)
        const result = await response.json().catch(() => ({})); 
        
        const newFabric = result.fabric || result; // Annahme: Server gibt das erstellte/aktualisierte Objekt zurück

        // Hier Zustand aktualisieren (wird im echten Code gemacht, hier nur als Kommentar)
        if (fabricId) {
            // setFabrics(prev => prev.map(f => f.id === fabricId ? newFabric : f));
        } else {
            // setFabrics(prev => [...prev, newFabric]);
        }
        
    } catch (err) {
        console.error('API-Fehler bei Stoff-Speicherung:', err);
        setError(`Fehler beim Speichern des Stoffes: ${err.message}`);
        // Wichtig: Dies gibt jetzt den Fehler des Backends aus, z.B. "Fehler 401: Unauthorized"
    } finally {
        setIsLoading(false);
    }
}, [setFabrics, setIsLoading, setError]); // Fügen Sie alle notwendigen Dependencies hier hinzu


const AdminDashboard = () => {
    // ... (States wie products, fabrics, currentProduct, isLoading, error - HIER MÜSSEN SIE WIRKLICH VORHANDEN SEIN)
    const [products, setProducts] = useState([]);
    const [fabrics, setFabrics] = useState([]);
    const [currentProduct, setCurrentProduct] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    // ... (weitere Hooks)

    // ... (useEffect zum Laden von Produkten und Stoffen)

    // ... (handleEdit, handleDelete, handleDeleteFabric, etc.)
    
    // Die Definition von handleFabricSubmit als Dummy für das Rendern, da sie oben als useCallback definiert ist:
    const handleFabricSubmit = useCallback(async (fabricData, fabricId) => {
        // Implementierung ist oben, hier nur die Referenz.
        console.log("Stoffdaten gesendet.", fabricData, fabricId);
    }, []); 

    // Die Definition von handleAddFabric wurde durch handleFabricSubmit ersetzt
    const handleAddFabric = handleFabricSubmit;

    return (
        <div className="admin-dashboard-container">
            {/* ... (Produkte-Verwaltung) ... */}
            
            {/* 2. Bereich: STOFF-VERWALTUNG (Unterhalb des Grids) */}
            <div style={{ 
                marginTop: '40px', 
                borderTop: '2px solid var(--farbe-grau)', 
                paddingTop: '30px' 
            }}>
                <h1 style={{ color: 'var(--farbe-primär)', marginBottom: '30px', fontSize: '1.8em' }}>
                    Stoff-Inventar-Verwaltung
                </h1>
                
                {/* Zeige generelle Lade- und Fehlerzustände an */
                isLoading && <p style={{ color: 'var(--farbe-akzent)' }}>Wird geladen...</p>}
                {error && <p style={{ color: 'var(--farbe-rot)', fontWeight: 'bold' }}>{error}</p>}
                
                {/* Stoff-Formular: Übergibt handleFabricSubmit als onSubmit */}
                <FabricForm 
                    onSubmit={handleFabricSubmit} 
                    // Optional: stoffToEdit für Bearbeitung, falls die Logik implementiert wird
                />
                
                {/* Stoff-Liste */}
                {/* ANNAHME: AdminFabricList ist korrekt importiert und wird hier gerendert */}
                {/* <AdminFabricList fabrics={fabrics} onDelete={handleDeleteFabric} /> */}
            </div>
        </div>
    );
};

export default AdminDashboard;
