// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useCallback } from 'react';
import { LogOut, Edit, Trash2 } from 'lucide-react';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

const getAuthHeaders = () => {
  const token = localStorage.getItem('adminToken');
  return token ? { Authorization: `Bearer ${token}` } : {};
};

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) {
    return `${BACKEND_URL}${imagePath}`;
  }
  return imagePath || FALLBACK_IMAGE_URL;
};

// ===================== PRODUKT FORMULAR =====================
const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
  const [formData, setFormData] = useState(productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0,
    size: '',
    isFeatured: false,
    fabricId: '',
    imageFile: null
  });

  useEffect(() => {
    setFormData(productToEdit || {
      name: '',
      description: '',
      price: 0,
      stock: 0,
      size: '',
      isFeatured: false,
      fabricId: '',
      imageFile: null
    });
  }, [productToEdit]);

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (name === 'imageFile') setFormData({ ...formData, imageFile: files[0] });
    else if (type === 'checkbox') setFormData({ ...formData, [name]: checked });
    else setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h2>{productToEdit ? "Produkt bearbeiten" : "Neues Produkt erstellen"}</h2>

      <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Produktname" required />
      <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Beschreibung" required />
      <input type="number" name="price" value={formData.price} onChange={handleChange} min="0" step="0.01" placeholder="Preis (€)" required />
      <input type="number" name="stock" value={formData.stock} onChange={handleChange} min="0" placeholder="Bestand" required />
      <input type="text" name="size" value={formData.size} onChange={handleChange} placeholder="Größe / Maß (z.B. 56cm, M, L…)" required />

      <select name="fabricId" value={formData.fabricId} onChange={handleChange}>
        <option value="">-- Stoff wählen --</option>
        {fabrics.map(f => <option key={f.id} value={f.id}>{f.name}</option>)}
      </select>

      <label>
        <input type="checkbox" name="isFeatured" checked={formData.isFeatured} onChange={handleChange} /> Als Angebot anzeigen
      </label>

      <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />

      <button type="submit">{productToEdit ? "Speichern" : "Erstellen"}</button>
      {productToEdit && <button type="button" onClick={onCancel}>Abbrechen</button>}
    </form>
  );
};

// ===================== STOFF FORMULAR =====================
const FabricForm = ({ onSubmit, fabricToEdit, onCancel }) => {
  const [formData, setFormData] = useState(fabricToEdit || {
    name: '',
    fabricType: '',
    stock: 0,
    unit: 'cm',
    imageFile: null
  });

  useEffect(() => {
    setFormData(fabricToEdit || {
      name: '',
      fabricType: '',
      stock: 0,
      unit: 'cm',
      imageFile: null
    });
  }, [fabricToEdit]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'imageFile') setFormData({ ...formData, imageFile: files[0] });
    else setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach(key => { if (formData[key] !== null) data.append(key, formData[key]); });
    onSubmit(data);
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h2>{fabricToEdit ? "Stoff bearbeiten" : "Neuen Stoff hinzufügen"}</h2>

      <input name="name" value={formData.name} onChange={handleChange} placeholder="Stoffname" required />
      <input name="fabricType" value={formData.fabricType} onChange={handleChange} placeholder="Kategorie" required />
      <input name="stock" type="number" value={formData.stock} onChange={handleChange} min="0" placeholder="Bestand" required />
      <select name="unit" value={formData.unit} onChange={handleChange}>
        <option value="cm">cm</option>
        <option value="m">m</option>
      </select>
      <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />

      <button type="submit">{fabricToEdit ? "Speichern" : "Hinzufügen"}</button>
      {fabricToEdit && <button type="button" onClick={onCancel}>Abbrechen</button>}
    </form>
  );
};

// ===================== ADMIN DASHBOARD =====================
const AdminDashboard = ({ onLogout }) => {
  const [products, setProducts] = useState([]);
  const [fabrics, setFabrics] = useState([]);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [currentFabric, setCurrentFabric] = useState(null);
  const [message, setMessage] = useState("");
  const [search, setSearch] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);

  const fetchAll = useCallback(async () => {
    try {
      const prodRes = await fetch(`${API_URL}/products`, { headers: getAuthHeaders() });
      const fabRes = await fetch(`${API_URL}/fabrics`, { headers: getAuthHeaders() });

      if (!prodRes.ok || !fabRes.ok) throw new Error("Fehler beim Laden");

      const prodData = await prodRes.json();
      setProducts(prodData);
      setFilteredProducts(prodData);

      setFabrics(await fabRes.json());
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  }, []);

  useEffect(() => { fetchAll(); }, [fetchAll]);

  // ===================== Produkt speichern =====================
  const handleProductSubmit = async (data) => {
    try {
      const formData = new FormData();
      Object.keys(data).forEach(key => { if (data[key] !== null) formData.append(key, data[key]); });

      const method = data.id ? "PUT" : "POST";
      const url = data.id ? `${API_URL}/products/${data.id}` : `${API_URL}/products`;

      const res = await fetch(url, { method, headers: getAuthHeaders(), body: formData });
      if (!res.ok) throw new Error("Fehler beim Speichern");
      
      setMessage("✅ Produkt gespeichert");
      setCurrentProduct(null);
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm("Produkt wirklich löschen?")) return;
    try {
      const res = await fetch(`${API_URL}/products/${id}`, { method: "DELETE", headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Fehler beim Löschen");
      setMessage("✅ Produkt gelöscht");
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // ===================== Stoff speichern =====================
  const handleFabricSubmit = async (formData) => {
    try {
      const method = formData.get('id') ? "PUT" : "POST";
      const url = formData.get('id') ? `${API_URL}/fabrics/${formData.get('id')}` : `${API_URL}/fabrics`;

      const res = await fetch(url, { method, headers: getAuthHeaders(), body: formData });
      if (!res.ok) throw new Error("Fehler beim Speichern des Stoffes");

      setMessage("✅ Stoff gespeichert");
      setCurrentFabric(null);
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  const handleDeleteFabric = async (id) => {
    if (!window.confirm("Stoff wirklich löschen?")) return;
    try {
      const res = await fetch(`${API_URL}/fabrics/${id}`, { method: "DELETE", headers: getAuthHeaders() });
      if (!res.ok) throw new Error("Fehler beim Löschen");
      setMessage("✅ Stoff gelöscht");
      fetchAll();
    } catch (err) {
      setMessage("❌ " + err.message);
    }
  };

  // ===================== Produktsuche =====================
  useEffect(() => {
    const lower = search.toLowerCase();
    setFilteredProducts(products.filter(p => p.name.toLowerCase().includes(lower)));
  }, [search, products]);

  return (
    <div className="admin-dashboard-container">
      <h1>Admin Dashboard</h1>
      <button onClick={() => { localStorage.removeItem('adminToken'); onLogout(); }}><LogOut /> Logout</button>

      {message && (
        <p className={message.includes("❌") ? "status-message-error" : "status-message-success"}>{message}</p>
      )}

      <input type="text" placeholder="Produkte suchen ..." value={search} onChange={e => setSearch(e.target.value)} />

      {/* PRODUKTE */}
      <div className="admin-dashboard-grid">
        <ProductForm productToEdit={currentProduct} onSubmit={handleProductSubmit} onCancel={() => setCurrentProduct(null)} fabrics={fabrics} />
        <div className="admin-list-container">
          <h3>Produkte ({filteredProducts.length})</h3>
          {filteredProducts.map(p => (
            <div key={p.id} className="admin-list-item">
              <img src={getImageUrl(p.imageUrl)} alt="" />
              <span>{p.name}</span>
              <button onClick={() => setCurrentProduct(p)}><Edit /></button>
              <button onClick={() => handleDeleteProduct(p.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>

      {/* STOFFE */}
      <div className="admin-dashboard-grid" style={{ marginTop: '40px' }}>
        <FabricForm fabricToEdit={currentFabric} onSubmit={handleFabricSubmit} onCancel={() => setCurrentFabric(null)} />
        <div className="admin-list-container">
          <h3>Stoffe ({fabrics.length})</h3>
          {fabrics.map(f => (
            <div key={f.id} className="admin-list-item">
              <img src={getImageUrl(f.imageUrl)} alt="" />
              <span>{f.name} – {f.stock}{f.unit}</span>
              <button onClick={() => setCurrentFabric(f)}><Edit /></button>
              <button onClick={() => handleDeleteFabric(f.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
