import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import './Shop.css';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getImageUrl = (imagePath) =>
  imagePath && imagePath.startsWith('/uploads/') ? `${BACKEND_URL}${imagePath}` : FALLBACK_IMAGE_URL;

const ProductCard = ({ product, onAdd }) => {
  const [hearts, setHearts] = useState([]);

  const addHeart = (e) => {
    const x = e.nativeEvent.offsetX;
    const y = e.nativeEvent.offsetY;
    const id = Date.now();
    setHearts([...hearts, { id, x, y }]);
    onAdd(product);

    setTimeout(() => {
      setHearts(current => current.filter(h => h.id !== id));
    }, 800);
  };

  return (
    <div className="product-card">
      <Link to={`/shop/${product.id}`}>
        <img src={getImageUrl(product.imageUrl)} alt={product.name} />
      </Link>
      <h3>{product.name}</h3>
      <p className="price-tag">‚Ç¨ {product.price.toFixed(2)}</p>
      <p className={product.stock > 0 ? 'instock' : 'outstock'}>
        {product.stock > 0 ? 'Sofort verf√ºgbar' : 'Ausverkauft'}
      </p>
      <button onClick={addHeart} disabled={product.stock <= 0}>
        {product.stock > 0 ? 'In den Warenkorb' : 'Nicht lieferbar'}
      </button>

      {hearts.map(h => (
        <span
          key={h.id}
          className="flying-heart"
          style={{ left: h.x, top: h.y }}
        >
          üíñ
        </span>
      ))}
    </div>
  );
};

// ---------------------- Shop ----------------------
const Shop = () => {
  const { cartItems, addToCart, removeFromCart, clearCart } = useCart();
  const [products, setProducts] = useState([]);
  const [cartVisible, setCartVisible] = useState(false);

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(setProducts)
      .catch(console.error);
  }, []);

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="shop-container">
      <h1>Schau dich um ‚ú®</h1>
      <div className="product-grid">
        {products.map(p => <ProductCard key={p.id} product={p} onAdd={addToCart} />)}
      </div>

      <button className="open-cart-button" onClick={() => setCartVisible(v => !v)}>
        üõí {cartItems.reduce((sum, item) => sum + item.quantity, 0)}
      </button>

      <div className={`sticky-cart ${cartVisible ? 'visible' : ''}`}>
        <h3>üõí Dein Warenkorb</h3>
        {cartItems.length === 0 ? (
          <p>Dein Warenkorb ist leer</p>
        ) : (
          <>
            <ul>
              {cartItems.map(item => (
                <li key={item.id}>
                  {item.name} x {item.quantity} = ‚Ç¨ {(item.price * item.quantity).toFixed(2)}
                  <button className="remove-btn" onClick={() => removeFromCart(item.id)}>‚ùå</button>
                </li>
              ))}
            </ul>
            <p><strong>Gesamt: ‚Ç¨ {totalPrice.toFixed(2)}</strong></p>
            <div className="sticky-cart-actions">
              <button onClick={() => alert('Zur Kasse‚Ä¶')}>Bezahlen</button>
              <button onClick={clearCart}>Alles l√∂schen</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Shop;
