// Checkout.js
import React, { useEffect, useRef } from "react";
import "./Checkout.css";

export default function Checkout() {
    const paypalRef = useRef(null);

    useEffect(() => {
        if (!paypalRef.current) return;

        // PayPal Script nur EINMAL laden
        if (document.getElementById("paypal-sdk")) return loadButtons();

        const script = document.createElement("script");
        script.id = "paypal-sdk";
        script.src =
            "https://www.paypal.com/sdk/js?client-id=YOUR_PAYPAL_CLIENT_ID&currency=EUR";

        script.onload = () => loadButtons();
        document.body.appendChild(script);

        function loadButtons() {
            window.paypal.Buttons({
                createOrder: async () => {
                    const res = await fetch("http://localhost:3001/api/create-order", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ amount: "99.99" })
                    });

                    const data = await res.json();
                    return data.id;
                },
                onApprove: async (data) => {
                    await fetch("http://localhost:3001/api/capture-order", {
                        method: "POST",
                        headers: { "Content-Type": "application/json" },
                        body: JSON.stringify({ orderID: data.orderID })
                    });

                    alert("Zahlung abgeschlossen!");
                }
            }).render(paypalRef.current);
        }
    }, []);

    return (
        <div className="checkout-container">
            <h2>Bezahlung</h2>
            <div ref={paypalRef} className="paypal-button-wrapper"></div>
        </div>
    );
}
