import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom';

// WICHTIG: Externe Imports wurden entfernt, um Build-Fehler zu vermeiden.
// Die Funktionalität von Komponenten, Assets und CSS wurde intern definiert.
// Die Styles werden aus dem globalen index.css geladen (sofern es existiert).

// --- Konstanter Wert für das Admin-Passwort (NUR FRONTEND-SCHUTZ) ---
const ADMIN_PASSWORD = "stoffzauber2025"; 
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/500x500/F2B2B4/4F3C3C?text=Stoffmuster";
const LOGO_URL = "https://placehold.co/500x150/C7E9D6/4F3C3C?text=Henkes-Stoffzauber+Logo"; // Platzhalter-Logo

/**
 * HILFSFUNKTION: Korrigiert Bildpfade, die vom Backend kommen.
 */
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    return imagePath;
};

// --- 1. AdminLogin Komponente (ehemals components/AdminLogin.js) ---
const AdminLogin = ({ onLogin }) => {
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        if (password === ADMIN_PASSWORD) {
            onLogin(true);
        } else {
            setError('Falsches Passwort. Bitte versuchen Sie es erneut.');
            setPassword(''); 
        }
    };

    return (
        <div className="flex justify-center items-center py-10">
            <div className="form-card w-full max-w-md mx-auto">
                <h2 style={{ color: 'var(--farbe-text)', textAlign: 'center', marginBottom: '20px' }}>Admin-Anmeldung</h2>

                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="password">Passwort:</label>
                        <input
                            id="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>

                    {error && (
                        <p style={{ color: 'var(--farbe-rot)', marginBottom: '15px', fontWeight: 'bold' }}>
                            {error}
                        </p>
                    )}

                    <button type="submit" style={{ width: '100%', backgroundColor: 'var(--farbe-text)' }}>
                        Anmelden
                    </button>
                </form>
                <p style={{ marginTop: '20px', fontSize: '0.8em', color: 'var(--farbe-text)', opacity: 0.6, textAlign: 'center' }}>
                    Passwort für Testzwecke: `{ADMIN_PASSWORD}`
                </p>
            </div>
        </div>
    );
};

// --- 2. ImageCarousel Komponente (ehemals components/ImageCarousel.js) ---
const ImageCarousel = () => {
    const [products, setProducts] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            const API_URL = 'http://localhost:3001/api/products/featured';
            try {
                const response = await fetch(API_URL);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                const featuredProducts = data.filter(p => p.imageUrl);
                setProducts(featuredProducts);
            } catch (e) {
                console.error("Fehler beim Abrufen der Featured Products:", e);
                setError("Fehler beim Laden der Produkte. Backend (Port 3001) nicht erreichbar.");
            } finally {
                setIsLoading(false);
            }
        };
        fetchProducts();
    }, []); 

    useEffect(() => {
        if (products.length > 1) {
            const intervalId = setInterval(() => {
                setCurrentIndex(prevIndex => 
                    (prevIndex + 1) % products.length
                );
            }, 4000); 
            return () => clearInterval(intervalId); 
        }
    }, [products.length]);


    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>⭐ Stoffzauber wird geladen...</p>;
    }
    if (error) {
        return <p style={{ textAlign: 'center', color: 'var(--farbe-rot)', padding: '50px', fontWeight: 'bold' }}>{error}</p>;
    }
    if (products.length === 0) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Keine Featured Products gefunden.</p>;
    }

    const currentProduct = products[currentIndex];

    return (
        <div className="carousel-container">
            <img 
                src={getImageUrl(currentProduct.imageUrl)} 
                alt={currentProduct.name} 
                className="carousel-image" 
                onError={(e) => { 
                    e.target.onerror = null; 
                    e.target.src = FALLBACK_IMAGE_URL; 
                }}
            />
            <div style={{
                position: 'absolute',
                bottom: 50,
                left: 0,
                right: 0,
                backgroundColor: 'rgba(255, 255, 255, 0.7)',
                padding: '10px 20px',
                textAlign: 'center',
                color: 'var(--farbe-text)'
            }}>
                <h3 style={{ margin: 0, fontSize: '1.2em' }}>{currentProduct.name}</h3>
                <p style={{ margin: '5px 0 0', fontWeight: 'bold' }}>€ {currentProduct.price.toFixed(2)}</p>
            </div>

            <div className="carousel-dots">
                {products.map((_, index) => (
                    <div
                        key={index}
                        className={`dot ${index === currentIndex ? 'active' : ''}`}
                        onClick={() => setCurrentIndex(index)}
                    />
                ))}
            </div>
        </div>
    );
};

