    const [isAdminAuthenticated, setIsAdminAuthenticated] = useState(false);

    // Funktion zum Setzen des Authentifizierungsstatus
    const handleAdminLogin = (isAuthenticated) => {
        setIsAdminAuthenticated(isAuthenticated);
    };

    // Komponente zum bedingten Rendern/Schutz der Route
    const AdminProtectedRoute = ({ element: Element }) => {
        if (isAdminAuthenticated) {
            return <Element />;
        }
        // Übergibt die Login-Funktion an die AdminLogin-Komponente
        return <AdminLogin onLogin={handleAdminLogin} />;
    };

  return (
    <div className="App">
      
      <nav>
        <Link to="/">Home</Link>
        <Link to="/shop">Shop</Link>
        <Link to="/blog">Blog</Link>
        {/* Admin-Link/Logout-Button */}
        {!isAdminAuthenticated ? (
            <Link to="/admin">Admin-Login</Link>
        ) : (
            // Bei Klick auf den Logout-Button wird der Authentifizierungsstatus zurückgesetzt
            <Link to="/" onClick={() => setIsAdminAuthenticated(false)} style={{ color: 'var(--farbe-akzent)' }}>
                Admin-Logout
            </Link>
        )}
      </nav>
      
      <main>
        <Routes>
