// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useCallback } from 'react';
import { LogOut, Edit, Trash2 } from 'lucide-react';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

// Auth Header
const getAuthHeaders = () => {
  const token = localStorage.getItem('adminToken');
  return token ? { Authorization: `Bearer ${token}` } : {};
};

// Bildpfad helper
const getImageUrl = (path) =>
  path && path.startsWith('/uploads/')
    ? `${BACKEND_URL}${path}`
    : path || FALLBACK_IMAGE_URL;

// ✅ --- PRODUKT-FORMULAR ---
const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
  const [formData, setFormData] = useState(productToEdit || {
    name: '', description: '', price: 0, stock: 0,
    availableWidth: 0, availableHeight: 0,
    isFeatured: false, fabricId: '', imageFile: null, imageUrl: ''
  });

  useEffect(() => {
    setFormData(productToEdit || {
      name: '', description: '', price: 0, stock: 0,
      availableWidth: 0, availableHeight: 0,
      isFeatured: false, fabricId: '', imageFile: null, imageUrl: ''
    });
  }, [productToEdit]);

  const handleChange = e => {
    const { name, value, type, checked, files } = e.target;
    if (name === 'imageFile') return setFormData({ ...formData, imageFile: files[0] });
    if (type === 'checkbox') return setFormData({ ...formData, [name]: checked });
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h3>{productToEdit ? "Produkt bearbeiten" : "Neues Produkt erstellen"}</h3>

      <input name="name" value={formData.name} onChange={handleChange} placeholder="Produktname" required />
      <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Beschreibung" required />

      <div className="admin-grid-2">
        <input type="number" name="price" value={formData.price} onChange={handleChange} placeholder="Preis (€)" min="0" step="0.01" required />
        <input type="number" name="stock" value={formData.stock} onChange={handleChange} placeholder="Bestand" min="0" required />
      </div>

      {/* ✅ NEU: Maße */}
      <div className="admin-grid-2">
        <input type="number" name="availableWidth" value={formData.availableWidth} onChange={handleChange} placeholder="Breite (cm)" min="0" />
        <input type="number" name="availableHeight" value={formData.availableHeight} onChange={handleChange} placeholder="Höhe (cm)" min="0" />
      </div>

      <select name="fabricId" value={formData.fabricId} onChange={handleChange}>
        <option value="">-- Stoff wählen --</option>
        {fabrics.map(f => <option key={f.id} value={f.id}>{f.name}</option>)}
      </select>

      <label>
        <input type="checkbox" name="isFeatured" checked={formData.isFeatured} onChange={handleChange} />
        Als Angebot anzeigen
      </label>

      <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />

      <button type="submit">{productToEdit ? "Speichern" : "Erstellen"}</button>
      {productToEdit && <button type="button" onClick={onCancel}>Abbrechen</button>}
    </form>
  );
};

// ✅ --- STOFF-FORMULAR ---
const FabricForm = ({ onAdd }) => {
  const [formData, setFormData] = useState({
    name: '', fabricType: '', stock: 0,
    width: 0, weightGsm: 0,
    imageFile: null
  });

  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'imageFile') return setFormData({ ...formData, imageFile: files[0] });
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach(k => {
      if (formData[k] !== null) data.append(k, formData[k]);
    });
    onAdd(data);
  };

  return (
    <form onSubmit={handleSubmit} className="form-card">
      <h3>Neuen Stoff hinzufügen</h3>

      <input name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
      <input name="fabricType" value={formData.fabricType} onChange={handleChange} placeholder="Stoffart" required />
      <input name="stock" type="number" value={formData.stock} onChange={handleChange} placeholder="Bestand (m)" min="0" required />

      {/* ✅ NEU: Maße */}
      <div className="admin-grid-2">
        <input name="width" type="number" value={formData.width} onChange={handleChange} placeholder="Breite (cm)" min="0" />
        <input name="weightGsm" type="number" value={formData.weightGsm} onChange={handleChange} placeholder="Grammatur (g/m²)" min="0" />
      </div>

      <input type="file" name="imageFile" onChange={handleChange} accept="image/*" />

      <button type="submit">Speichern</button>
    </form>
  );
};

// ✅ --- HAUPKOMPONENTE ---
const AdminDashboard = ({ onLogout }) => {
  const [products, setProducts] = useState([]);
  const [fabrics, setFabrics] = useState([]);
  const [currentProduct, setCurrentProduct] = useState(null);
  const [message, setMessage] = useState("");

  const fetchAll = useCallback(async () => {
    try {
      const prodRes = await fetch(`${API_URL}/products`, { headers: getAuthHeaders() });
      const fabRes = await fetch(`${API_URL}/fabrics`, { headers: getAuthHeaders() });

      setProducts(await prodRes.json());
      setFabrics(await fabRes.json());
    } catch {
      setMessage("❌ Fehler beim Laden");
    }
  }, []);

  useEffect(() => { fetchAll(); }, [fetchAll]);

  // ✅ Produkt speichern
  const handleProductSubmit = async (data) => {
    try {
      const form = new FormData();
      Object.keys(data).forEach(k => form.append(k, data[k]));

      const method = data.id ? "PUT" : "POST";
      const url = data.id ? `${API_URL}/products/${data.id}` : `${API_URL}/products`;

      await fetch(url, { method, headers: getAuthHeaders(), body: form });

      setMessage("✅ Produkt gespeichert");
      setCurrentProduct(null);
      fetchAll();
    } catch {
      setMessage("❌ Fehler beim Speichern");
    }
  };

  // ✅ Stoff speichern
  const handleAddFabric = async (formData) => {
    try {
      await fetch(`${API_URL}/fabrics`, {
        method: "POST",
        headers: getAuthHeaders(),
        body: formData
      });
      setMessage("✅ Stoff gespeichert");
      fetchAll();
    } catch {
      setMessage("❌ Fehler beim Speichern");
    }
  };

  return (
    <div className="admin-dashboard-container">
      <h1>Admin Dashboard</h1>
      <button onClick={() => { localStorage.removeItem('adminToken'); onLogout(); }}>
        <LogOut /> Logout
      </button>

      {message && <p>{message}</p>}

      <div className="admin-dashboard-grid">
        <ProductForm
          productToEdit={currentProduct}
          onSubmit={handleProductSubmit}
          onCancel={() => setCurrentProduct(null)}
          fabrics={fabrics}
        />

        <div className="admin-list-container">
          <h3>Produkte ({products.length})</h3>
          {products.map(p => (
            <div key={p.id} className="admin-list-item">
              <img src={getImageUrl(p.imageUrl)} alt="" />
              <span>{p.name}</span>
              <button onClick={() => setCurrentProduct(p)}><Edit /></button>
              <button onClick={() => handleDeleteProduct(p.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>

      <div className="admin-dashboard-grid" style={{ marginTop: '40px' }}>
        <FabricForm onAdd={handleAddFabric} />
        <div className="admin-list-container">
          <h3>Stoffe ({fabrics.length})</h3>
          {fabrics.map(f => (
            <div key={f.id} className="admin-list-item">
              <img src={getImageUrl(f.imageUrl)} alt="" />
              <span>{f.name}</span>
              <button onClick={() => handleDeleteFabric(f.id)}><Trash2 /></button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
