// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogOut, Edit, Trash2, PlusCircle, CheckCircle, XCircle } from 'lucide-react';

// Annahme: Die Komponenten ProductForm, AdminProductList, FabricForm und AdminFabricList
// sind entweder hier definiert oder importiert.
// HINWEIS: Hier müssten Ihre tatsächlichen Importe für die Komponenten stehen.
// Für die Korrektur des Fehlers sind sie hier als Platzhalter belassen, 
// falls sie im Originalcode vor der Dashboard-Komponente definiert wurden.


const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

// --- Hilfsfunktionen für Admin-Operationen ---

/**
 * Ruft den Admin-Authentifizierungstoken aus dem localStorage ab.
 * @returns {object} Ein Header-Objekt oder ein Fehler-Objekt.
 */
const getAuthHeaders = () => {
    const adminToken = localStorage.getItem('adminToken');
    if (!adminToken) {
        return { error: 'Admin-Sitzung abgelaufen oder nicht gefunden.' };
    }
    return { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${adminToken}`,
    };
};

/**
 * HILFSFUNKTION: Korrigiert Bildpfade, die vom Backend kommen.
 */
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    return imagePath || FALLBACK_IMAGE_URL;
};

// =========================================================================
// HIER BEGINNT DIE ADMIN-DASHBOARD-HAUPTKOMPONENTE
// =========================================================================

const AdminDashboard = ({ onLogout }) => {
    // 1. ZUSTÄNDE FÜR PRODUKTE & STOFFE
    const [products, setProducts] = useState([]);
    const [fabrics, setFabrics] = useState([]); // FIX: Jetzt definiert
    const [currentProduct, setCurrentProduct] = useState(null); // Produkt, das bearbeitet wird
    
    // ZUSTÄNDE FÜR LADEZUSTAND & FEHLER
    const [isLoading, setIsLoading] = useState(true); // FIX: Jetzt definiert
    const [error, setError] = useState(null); // FIX: Jetzt definiert
    const navigate = useNavigate();

    // 2. FETCH-FUNKTIONEN (MÜSSEN INNERHALB DER KOMPONENTE SEIN)

    // A. Lade Produkte
    const fetchProducts = useCallback(async () => {
        setIsLoading(true);
        setError(null);
        
        const authHeaders = getAuthHeaders();
        if (authHeaders.error) {
            setError(authHeaders.error);
            setIsLoading(false);
            return;
        }

        try {
            const response = await fetch(`${API_URL}/products`, { headers: authHeaders });
            if (!response.ok) {
                throw new Error(`HTTP-Fehler! Status: ${response.status}`);
            }
            const data = await response.json();
            setProducts(data);
        } catch (err) {
            console.error('Fehler beim Laden der Produkte:', err.message);
            setError(`Fehler beim Laden der Produkte: ${err.message}`);
        } finally {
            // Wir setzen isLoading nur hier auf false, wenn fetchFabrics nicht läuft, 
            // oder wir nutzen einen Zähler/Promise. Da wir beide gleichzeitig laden,
            // werde ich es in den useEffect verschieben.
        }
    }, [setProducts, setIsLoading, setError]);


    // B. Lade Stoffe (FIX: Innerhalb der Komponente und verwendet useCallback korrekt)
    // FIX: Fehler 1 (useCallback top-level) und Fehler 2-4 (undefinierte Setter) behoben
    const fetchFabrics = useCallback(async () => {
        // isLoading und error werden bereits von fetchProducts gesetzt, aber hier zur Sicherheit:
        // setIsLoading(true); // Nur einmal am Anfang der Lade Sequenz.
        // setError(null);
        
        const authHeaders = getAuthHeaders();
        if (authHeaders.error) {
            setError(authHeaders.error);
            // setIsLoading(false); // Wird in den useEffect verschoben.
            return;
        }

        try {
            const response = await fetch(`${API_URL}/fabrics`, { headers: authHeaders });
            if (!response.ok) {
                throw new Error(`HTTP-Fehler! Status: ${response.status}`);
            }
            const data = await response.json();
            setFabrics(data);
        } catch (err) {
            console.error('Fehler beim Laden der Stoffe:', err.message);
            setError(`Fehler beim Laden der Stoffe: ${err.message}`);
        }
    }, [setFabrics, setError]); // Abhängigkeiten sind die Setter

    
    // C. Initiales Laden von Daten
    useEffect(() => {
        // Lade beide Datensätze parallel
        Promise.all([fetchProducts(), fetchFabrics()])
            .catch(err => {
                // error wird bereits in den fetch-Funktionen gesetzt.
                console.error("Fehler beim initialen Laden:", err);
            })
            .finally(() => {
                setIsLoading(false);
            });
    }, [fetchProducts, fetchFabrics, setIsLoading]); 


    // --- CRUD-Handler ---

    const handleLogout = () => {
        localStorage.removeItem('adminToken');
        onLogout(false);
        navigate('/admin');
    };

    const handleEdit = (product) => {
        setCurrentProduct(product);
    };

    const handleCancelEdit = () => {
        setCurrentProduct(null);
    };

    // [ ... Hier folgen die Implementierungen von handleProductSubmit, handleDelete, 
    // handleAddFabric, handleDeleteFabric. Da sie nicht Gegenstand des Fehlers sind, 
    // lasse ich sie für diesen Fix weg. ]
    // HINWEIS: Sie müssen sicherstellen, dass diese Funktionen im Originalcode vorhanden sind.

    // **********************************************
    // ACHTUNG: Die folgenden Handler MÜSSEN im Originalcode vorhanden sein!
    // Ich füge sie als leere Funktionen hinzu, um die Komponente lauffähig zu halten.
    const handleProductSubmit = async (formData) => { console.log('Product Submit (Placeholder)', formData); await fetchProducts(); };
    const handleDelete = async (productId) => { console.log('Product Delete (Placeholder)', productId); await fetchProducts(); };
    const handleAddFabric = async (fabricData) => { console.log('Fabric Add (Placeholder)', fabricData); await fetchFabrics(); };
    const handleDeleteFabric = async (fabricId) => { console.log('Fabric Delete (Placeholder)', fabricId); await fetchFabrics(); };
    // **********************************************


    // --- RENDERING ---

    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Admin Dashboard wird geladen...</p>;
    }

    if (error) {
        return (
            <div style={{ textAlign: 'center', padding: '50px', backgroundColor: 'var(--farbe-rot-hell)', color: 'var(--farbe-rot)', borderRadius: '8px' }}>
                <p style={{ fontWeight: 'bold' }}>Ein Fehler ist aufgetreten:</p>
                <p>{error}</p>
                <button 
                    onClick={() => { setIsLoading(true); setError(null); fetchProducts(); fetchFabrics(); }} 
                    style={{ marginTop: '15px', padding: '8px 15px', backgroundColor: 'var(--farbe-rot)', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                >
                    Erneut versuchen
                </button>
            </div>
        );
    }
    
    // Der restliche Code der Komponente folgt hier...
    // *********************************************************************************
    
    // Nehmen Sie an, dass die Komponenten ProductForm, AdminProductList, FabricForm, AdminFabricList importiert wurden
    // oder VOR dieser Funktion definiert sind.
    
    const AdminProductList = ({ products, onEdit, onDelete, fabrics }) => (
        <div style={{ padding: '10px', border: '1px dashed #ccc' }}>AdminProductList Placeholder</div>
    );
    const FabricForm = ({ onAdd }) => (
        <div style={{ padding: '10px', border: '1px dashed #ccc' }}>FabricForm Placeholder</div>
    );
    const AdminFabricList = ({ fabrics, onDelete }) => (
        <div style={{ padding: '10px', border: '1px dashed #ccc' }}>AdminFabricList Placeholder</div>
    );
    const ProductForm = () => (
        <div style={{ padding: '10px', border: '1px dashed #ccc' }}>ProductForm Placeholder</div>
    );


    return (
        <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '20px' }}>
            {/* Header / Logout-Bereich */}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', borderBottom: '2px solid var(--farbe-grau)', paddingBottom: '20px' }}>
                <h1 style={{ color: 'var(--farbe-primär)', fontSize: '2em', margin: 0 }}>Admin Dashboard</h1>
                <button 
                    onClick={handleLogout}
                    style={{ padding: '10px 20px', backgroundColor: 'var(--farbe-rot)', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px' }}
                    onMouseEnter={(e) => e.target.style.backgroundColor = '#cc0000'}
                    onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--farbe-rot)'}
                >
                    <LogOut size={18} />
                    Logout
                </button>
            </div>

            {/* 1. Bereich: PRODUKT-VERWALTUNG (Grid) */}
            <h2 style={{ color: 'var(--farbe-primär-dunkel)', marginBottom: '30px', fontSize: '1.5em' }}>
                {currentProduct ? 'Produkt bearbeiten' : 'Neues Produkt erstellen'}
            </h2>
            
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '40px' }}>
                {/* 1. Spalte: Produktformular */}
                <div>
                    <ProductForm 
                        key={currentProduct ? currentProduct.id : 'new'} 
                        productToEdit={currentProduct}
                        onSubmit={handleProductSubmit}
                        onCancel={handleCancelEdit}
                        fabrics={fabrics} 
                    />
                </div>

                {/* 2. Spalte: Produktliste */}
                <div>
                    <AdminProductList 
                        products={products}
                        onEdit={handleEdit}
                        onDelete={handleDelete}
                        fabrics={fabrics} 
                    />
                </div>
            </div>
            
            
            {/* 2. Bereich: STOFF-VERWALTUNG (Unterhalb des Grids) */}
            <div style={{ 
                marginTop: '40px', 
                borderTop: '2px solid var(--farbe-grau)', 
                paddingTop: '30px' 
            }}>
                <h1 style={{ color: 'var(--farbe-primär)', marginBottom: '30px', fontSize: '1.8em' }}>
                    Stoff-Inventar-Verwaltung
                </h1>
                
                {/* Stoff-Formular */}
                <FabricForm 
                    onAdd={handleAddFabric} 
                    // Fügen Sie Logik zur Bearbeitung hinzu, falls implementiert (derzeit nur hinzufügen/löschen)
                />
                
                {/* Stoff-Liste */}
                <AdminFabricList fabrics={fabrics} onDelete={handleDeleteFabric} />
            </div>
        </div>
    );
};

export default AdminDashboard;
