import React, { useState, useEffect, useMemo } from 'react';

// =========================================================================
// KONSTANTEN & HILFSFUNKTIONEN
// =========================================================================

const API_URL_PRODUCTS = 'http://localhost:3001/api/products';
const API_URL_FABRICS = 'http://localhost:3001/api/fabrics';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE = "https://placehold.co/50x50/eee/333?text=Bild";
const FALLBACK_IMAGE_LARGE = "https://placehold.co/100x100/CCCCCC/333333?text=Stoff";


/**
 * HILFSFUNKTION: Korrigiert Bildpfade, die vom Backend kommen.
 * Wenn der Pfad mit '/uploads/' beginnt, wird die Backend-URL vorangestellt.
 */
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    // Handhabt externe URLs (wie placehold.co) oder defekte Pfade
    return imagePath;
};


// =========================================================================
// 1. INTERNE KOMPONENTE: STOFF-FORMULAR (FabricForm)
// =========================================================================

// Liste der möglichen Verwendungszwecke (zum Checkbox-Rendern)
const USAGE_OPTIONS = [
    'Mütze (Außen)', 'Mütze (Innen)', 'Bündchen', 'Bekleidung (Oberteil)', 
    'Bekleidung (Hose)', 'Babybekleidung', 'Deko', 'Taschen', 'Accessoires'
];

// Liste der Stoffarten
const FABRIC_TYPES = [
    'Jersey', 'French Terry (Sommersweat)', 'Sweat', 'Bündchenstoff', 'Musselin', 
    'Webware Baumwolle', 'Cord', 'Fleece', 'Softshell', 'Leder (Imitat/Echt)'
];

const getInitialFabricState = (stoffToEdit) => stoffToEdit || {
    name: '',
    description: '',
    fabricType: '',
    suitableFor: [],
    stock: 0,
    imageUrl: '',
    imageFile: null,
};

const FabricForm = ({ stoffToEdit, onSubmit, onCancel }) => {
    
    const initialState = useMemo(() => getInitialFabricState(stoffToEdit), [stoffToEdit]);
    
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!stoffToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);


    useEffect(() => {
        setFormData(initialState);
        setIsEditing(!!stoffToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, stoffToEdit]); 

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        
        if (type === 'checkbox' && name === 'suitableFor') {
            const currentUsages = formData.suitableFor;
            setFormData(prev => ({
                ...prev,
                suitableFor: checked 
                    ? [...currentUsages, value] 
                    : currentUsages.filter(u => u !== value)
            }));
        } else if (type === 'file') {
            setFormData(prev => ({ 
                ...prev, 
                imageFile: e.target.files[0] 
            }));
            setPreviewImage(URL.createObjectURL(e.target.files[0]));
        } else if (name === 'stock') {
            // Sicherstellen, dass der Bestand eine positive Ganzzahl ist
            const stockValue = Math.max(0, parseInt(value, 10) || 0);
            setFormData(prev => ({ ...prev, [name]: stockValue }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
    };

    const getFinalPreviewUrl = () => {
        if (formData.imageFile) return previewImage;
        if (formData.imageUrl) return getImageUrl(formData.imageUrl);
        return FALLBACK_IMAGE_LARGE;
    };


    return (
        <form onSubmit={handleSubmit} className="admin-form-card">
            <h3 style={{ color: 'var(--farbe-primär-dunkel)', borderBottom: '2px solid #eee', paddingBottom: '10px', marginBottom: '20px' }}>
                {isEditing ? `Stoff bearbeiten: ${formData.name}` : 'Neuen Stoff anlegen'}
            </h3>

            {/* 1. Name */}
            <div className="admin-form-group">
                <label htmlFor="name">Name des Stoffes:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>

            {/* 2. Stoffart (Dropdown) */}
            <div className="admin-form-group">
                <label htmlFor="fabricType">Stoffart:</label>
                <select
                    id="fabricType"
                    name="fabricType"
                    value={formData.fabricType}
                    onChange={handleChange}
                    required
                >
                    <option value="">-- Bitte wählen --</option>
                    {FABRIC_TYPES.map(type => (
                        <option key={type} value={type}>{type}</option>
                    ))}
                </select>
            </div>

            {/* 3. Bestand in Metern */}
            <div className="admin-form-group">
                <label htmlFor="stock">Lagerbestand in Metern:</label>
                <input
                    type="number"
                    id="stock"
                    name="stock"
                    min="0"
                    value={formData.stock}
                    onChange={handleChange}
                    required
                />
            </div>

            {/* 4. Beschreibung (optional) */}
            <div className="admin-form-group">
                <label htmlFor="description">Beschreibung (optional):</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="3"
                ></textarea>
            </div>
            
            {/* 5. Verwendungszwecke (Checkboxen) */}
            <div className="admin-form-group">
                <label style={{fontWeight: 'bold'}}>Verwendungszwecke:</label>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '5px', border: '1px solid #eee', padding: '10px', borderRadius: '8px', background: '#f9f9f9' }}>
                    {USAGE_OPTIONS.map(usage => (
                        <div key={usage} style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
                            <input
                                type="checkbox"
                                id={usage}
                                name="suitableFor"
                                value={usage}
                                checked={formData.suitableFor.includes(usage)}
                                onChange={handleChange}
                                style={{ width: '15px', height: '15px' }}
                            />
                            <label htmlFor={usage} style={{ margin: 0, fontWeight: 'normal', fontSize: '0.9em', cursor: 'pointer' }}>
                                {usage}
                            </label>
                        </div>
                    ))}
                </div>
            </div>

            {/* 6. Bild-Upload und Vorschau */}
            <div className="admin-form-group">
                <label htmlFor="imageFile">Stoffmuster Bild (optional):</label>
                <input
                    type="file"
                    id="imageFile"
                    name="imageFile"
                    accept="image/*"
                    onChange={handleChange}
                />
                
                <div style={{ marginTop: '15px' }}>
                    <img
                        src={getFinalPreviewUrl()}
                        alt="Vorschau"
                        style={{ maxWidth: '100px', maxHeight: '100px', borderRadius: '8px', border: '1px solid #ccc' }}
                    />
                </div>
            </div>

            {/* 7. Aktionen */}
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <button type="submit" className="admin-form-submit-button">
                    {isEditing ? 'Änderungen speichern' : 'Stoff erstellen'}
                </button>
            </div>
            
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} className="admin-form-submit-button" style={{backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};


// =========================================================================
// 2. INTERNE KOMPONENTE: STOFF-LISTE (AdminFabricList)
// =========================================================================

const AdminFabricList = ({ fabrics, onDelete, onEdit }) => {

    const listStyle = {
        marginTop: '20px',
        padding: '20px',
        backgroundColor: 'var(--farbe-hell)',
        borderRadius: '10px',
        boxShadow: '0 2px 5px rgba(0,0,0,0.05)',
    };

    const itemStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '15px 0',
        borderBottom: '1px dashed #ddd',
    };

    const textStyle = { margin: 0 };
    const stockStyle = (stock) => ({ 
        fontWeight: 'bold', 
        color: stock > 0 ? 'var(--farbe-grün)' : 'var(--farbe-rot)' 
    });


    return (
        <div style={listStyle}>
            <h2 style={{ fontSize: '1.5em', color: 'var(--farbe-primär)', margin: '0 0 20px 0', borderBottom: '2px solid var(--farbe-grau)', paddingBottom: '10px' }}>
                Verfügbare Stoffe ({fabrics.length})
            </h2>
            
            {fabrics.length === 0 ? (
                <p style={{ color: '#666', textAlign: 'center' }}>Noch keine Stoffe im Inventar.</p>
            ) : (
                fabrics.map((stoff) => (
                    <div key={stoff.id} style={itemStyle}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '15px', flexGrow: 1 }}>
                            <img 
                                src={getImageUrl(stoff.imageUrl || FALLBACK_IMAGE)} 
                                alt={stoff.name} 
                                style={{ width: '40px', height: '40px', borderRadius: '4px', objectFit: 'cover' }}
                                onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                            />
                            <div>
                                <p style={textStyle}>
                                    <strong>{stoff.name}</strong> 
                                    <span style={{ color: '#888', marginLeft: '10px', fontSize: '0.9em' }}>({stoff.fabricType})</span>
                                </p>
                                <p style={stockStyle(stoff.stock)}>
                                    Bestand: {stoff.stock || 0} m
                                </p>
                            </div>
                        </div>

                        <div style={{ display: 'flex', gap: '10px' }}>
                            <button 
                                onClick={() => onEdit(stoff)} 
                                style={{ backgroundColor: 'var(--farbe-akzent)', color: 'white', padding: '5px 10px', borderRadius: '5px' }}
                            >
                                Bearbeiten
                            </button>
                            <button 
                                onClick={() => onDelete(stoff.id)} 
                                style={{ backgroundColor: 'var(--farbe-rot)', color: 'white', padding: '5px 10px', borderRadius: '5px' }}
                            >
                                Löschen
                            </button>
                        </div>
                    </div>
                ))
            )}
        </div>
    );
};


// =========================================================================
// 3. INTERNE KOMPONENTE: PRODUKT-FORMULAR (ProductForm)
// =========================================================================

const getInitialProductState = (productToEdit) => productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0,
    availableHeight: 0, // In cm
    availableWidth: 0, // In cm
    fabricId: '', // NEU: Verweis auf den ausgewählten Stoff
    isFeatured: false,
    imageFile: null,
    imageUrl: '',
};

const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
    
    const initialState = useMemo(() => getInitialProductState(productToEdit), [productToEdit]);
    
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!productToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);


    useEffect(() => {
        setFormData(initialState);
        setIsEditing(!!productToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, productToEdit]); 
    

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        
        if (type === 'checkbox') {
            setFormData(prev => ({ ...prev, [name]: checked }));
        } else if (type === 'file') {
            setFormData(prev => ({ ...prev, imageFile: e.target.files[0] }));
            setPreviewImage(URL.createObjectURL(e.target.files[0]));
        } else if (name === 'price' || name === 'stock' || name === 'availableHeight' || name === 'availableWidth') {
            // Sicherstellen, dass numerische Werte korrekt sind
            const numericValue = parseFloat(value) || 0;
            const finalValue = name === 'stock' ? Math.max(0, parseInt(numericValue, 10)) : Math.max(0, numericValue);
            setFormData(prev => ({ ...prev, [name]: finalValue }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Stellen Sie sicher, dass fabricId gesetzt ist, falls vorhanden
        if (formData.fabricId === '') {
            // Optional: Warnung oder Standardwert setzen
        }
        onSubmit(formData);
    };

    const getFinalPreviewUrl = () => {
        if (formData.imageFile) return previewImage;
        if (formData.imageUrl) return getImageUrl(formData.imageUrl);
        return FALLBACK_IMAGE;
    };


    return (
        <form onSubmit={handleSubmit} className="admin-form-card">
            <h3 style={{ color: 'var(--farbe-primär-dunkel)', borderBottom: '2px solid #eee', paddingBottom: '10px', marginBottom: '20px' }}>
                {isEditing ? `Produkt bearbeiten: ${formData.name}` : 'Neues Produkt anlegen'}
            </h3>

            {/* 1. Name */}
            <div className="admin-form-group">
                <label htmlFor="name">Name des Produkts:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>

            {/* 2. Stoffauswahl (NEU) */}
            <div className="admin-form-group">
                <label htmlFor="fabricId">Zugehöriger Stoff (optional):</label>
                <select
                    id="fabricId"
                    name="fabricId"
                    value={formData.fabricId}
                    onChange={handleChange}
                >
                    <option value="">-- Kein Stoff zugeordnet --</option>
                    {fabrics.map(f => (
                        <option key={f.id} value={f.id}>{f.name} ({f.fabricType})</option>
                    ))}
                </select>
            </div>

            {/* 3. Preis & Bestand */}
            <div style={{ display: 'flex', gap: '20px' }}>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="price">Preis (€):</label>
                    <input
                        type="number"
                        id="price"
                        name="price"
                        min="0"
                        step="0.01"
                        value={formData.price}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="stock">Bestand (Einheiten):</label>
                    <input
                        type="number"
                        id="stock"
                        name="stock"
                        min="0"
                        value={formData.stock}
                        onChange={handleChange}
                        required
                    />
                </div>
            </div>

            {/* 4. Abmessungen (NEU) */}
            <div style={{ display: 'flex', gap: '20px' }}>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="availableWidth">Verfügbare Breite (cm):</label>
                    <input
                        type="number"
                        id="availableWidth"
                        name="availableWidth"
                        min="0"
                        value={formData.availableWidth}
                        onChange={handleChange}
                    />
                </div>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="availableHeight">Verfügbare Höhe (cm):</label>
                    <input
                        type="number"
                        id="availableHeight"
                        name="availableHeight"
                        min="0"
                        value={formData.availableHeight}
                        onChange={handleChange}
                    />
                </div>
            </div>

            {/* 5. Beschreibung */}
            <div className="admin-form-group">
                <label htmlFor="description">Beschreibung:</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                ></textarea>
            </div>

            {/* 6. Bild-Upload und Vorschau */}
            <div className="admin-form-group" style={{ border: '1px solid #ccc', borderRadius: '8px', padding: '15px', background: '#fcfcfc' }}>
                <label htmlFor="imageFile">Produktbild:</label>
                <input
                    type="file"
                    id="imageFile"
                    name="imageFile"
                    accept="image/*"
                    onChange={handleChange}
                />
                
                {previewImage && (
                    <div style={{ marginTop: '15px' }}>
                        <p style={{ margin: '0 0 5px 0', fontSize: '0.9em', color: '#666' }}>Aktuelles Bild/Vorschau:</p>
                        <img
                            src={getFinalPreviewUrl()}
                            alt="Produktvorschau"
                            style={{ maxWidth: '100px', maxHeight: '100px', borderRadius: '8px', border: '1px solid #ccc' }}
                            onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                        />
                    </div>
                )}
            </div>
            
            {/* 7. Featured Checkbox */}
            <div className="admin-form-group" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <input
                    type="checkbox"
                    id="isFeatured"
                    name="isFeatured"
                    checked={formData.isFeatured}
                    onChange={handleChange}
                    style={{ width: '20px', height: '20px' }}
                />
                <label htmlFor="isFeatured" style={{ margin: 0, fontWeight: 'normal', cursor: 'pointer' }}>
                    Als "Featured Product" auf der Startseite anzeigen
                </label>
            </div>
            
            {/* 8. Submit/Cancel Buttons */}
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <button type="submit" className="admin-form-submit-button">
                    {isEditing ? 'Änderungen speichern' : 'Produkt erstellen'}
                </button>
            </div>
            
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} className="admin-form-submit-button" style={{backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};


// =========================================================================
// 4. INTERNE KOMPONENTE: PRODUKT-LISTE (AdminProductList)
// =========================================================================

const AdminProductList = ({ products, onEdit, onDelete, fabrics }) => {
    
    // Hilfsfunktion, um den Stoffnamen anhand der ID zu finden
    const getFabricName = (fabricId) => {
        const fabric = fabrics.find(f => f.id === fabricId);
        return fabric ? fabric.name : 'Nicht zugeordnet';
    };

    return (
        // Verwendet jetzt CSS-Klassen aus index.css
        <div className="admin-list-container">
            <h2 style={{ fontSize: '1.5em', color: 'var(--farbe-primär)', margin: '0 0 20px 0', borderBottom: '2px solid var(--farbe-grau)', paddingBottom: '10px' }}>
                Alle Produkte im System ({products.length})
            </h2>
            {products.length === 0 ? (
                <p style={{ color: '#666', textAlign: 'center' }}>Keine Produkte gefunden.</p>
            ) : (
                <ul style={{ listStyle: 'none', padding: 0 }}>
                    {products.map((product) => (
                        <li key={product.id} className="admin-list-item">
                            <div className="admin-list-item-content">
                                <img 
                                    src={getImageUrl(product.imageUrl || FALLBACK_IMAGE)} 
                                    alt={product.name} 
                                    style={{ width: '50px', height: '50px', borderRadius: '8px', objectFit: 'cover' }}
                                    onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                                />
                                <div style={{ flexGrow: 1, minWidth: 0, marginLeft: '15px' }}>
                                    <h4 style={{ margin: '0 0 5px 0', fontSize: '1.1em', color: 'var(--farbe-text-dunkel)' }}>{product.name}</h4>
                                    <p style={{ margin: 0, fontSize: '0.9em', color: '#666' }}>
                                        Preis: €{product.price.toFixed(2)} | Bestand: {product.stock} | Stoff: {getFabricName(product.fabricId)}
                                    </p>
                                </div>
                            </div>
                            <div className="admin-list-item-actions">
                                <button onClick={() => onEdit(product)} className="admin-list-edit-button">
                                    Bearbeiten
                                </button>
                                <button onClick={() => onDelete(product.id)} className="admin-list-delete-button">
                                    Löschen
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};


// =========================================================================
// 5. HAUPTKOMPONENTE: ADMIN DASHBOARD
// =========================================================================

const AdminDashboard = () => {
    const [products, setProducts] = useState([]);
    const [fabrics, setFabrics] = useState([]); // NEU: Zustand für Stoffe
    const [currentProduct, setCurrentProduct] = useState(null);
    const [currentFabric, setCurrentFabric] = useState(null); // NEU: Zustand für aktuell bearbeiteten Stoff
    const [statusMessage, setStatusMessage] = useState({ text: '', type: '' });
    const [isLoading, setIsLoading] = useState(true);

    // Hilfsfunktion für Statusnachrichten
    const showStatus = (text, type = 'success') => {
        setStatusMessage({ text, type });
        setTimeout(() => setStatusMessage({ text: '', type: '' }), 5000);
    };

    // --- DATENABRUF (Produkte & Stoffe) ---
    const fetchData = async () => {
        setIsLoading(true);
        try {
            const [productsResponse, fabricsResponse] = await Promise.all([
                fetch(API_URL_PRODUCTS),
                fetch(API_URL_FABRICS)
            ]);

            const productsData = await productsResponse.json();
            const fabricsData = await fabricsResponse.json();

            setProducts(productsData);
            setFabrics(fabricsData);
            showStatus('Produkte und Stoffe erfolgreich geladen.', 'info');
        } catch (error) {
            console.error('Fehler beim Abrufen der Daten:', error);
            showStatus('Fehler beim Laden der Produkte/Stoffe.', 'error');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, []); 

    // --- PRODUKT-LOGIK (CRUD) ---

    const handleProductSubmit = async (formData) => {
        const isEditing = !!formData.id;
        const url = isEditing ? `${API_URL_PRODUCTS}/${formData.id}` : API_URL_PRODUCTS;
        const method = isEditing ? 'PUT' : 'POST';

        const data = new FormData();
        // Alle Felder, außer imageFile, an das FormData-Objekt anhängen
        Object.keys(formData).forEach(key => {
            if (key !== 'imageFile' && key !== 'imageUrl') {
                // Konvertiere Arrays (wie suitableFor) oder Objekte in JSON-Strings
                if (Array.isArray(formData[key]) || typeof formData[key] === 'object' && formData[key] !== null) {
                    data.append(key, JSON.stringify(formData[key]));
                } else {
                    data.append(key, formData[key]);
                }
            }
        });

        // Bilddatei anhängen, wenn vorhanden
        if (formData.imageFile) {
            data.append('imageFile', formData.imageFile);
        } else if (isEditing && formData.imageUrl) {
            // Beim Bearbeiten ohne neues Bild den bestehenden Pfad mitsenden
            data.append('imageUrl', formData.imageUrl);
        }

        try {
            const response = await fetch(url, {
                method: method,
                // ACHTUNG: Keine 'Content-Type': 'application/json' für FormData!
                body: data, 
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP-Fehler! Status: ${response.status}`);
            }

            // Daten neu laden, um die Liste zu aktualisieren
            await fetchData(); 
            setCurrentProduct(null); // Formular zurücksetzen/abbrechen
            showStatus(`Produkt erfolgreich ${isEditing ? 'aktualisiert' : 'erstellt'}.`);
        } catch (error) {
            console.error('Fehler beim Speichern des Produkts:', error);
            showStatus(`Fehler beim Speichern: ${error.message}`, 'error');
        }
    };


    const handleEdit = (product) => {
        setCurrentProduct(product);
        setCurrentFabric(null); // Schließt ggf. das Stoff-Formular
        showStatus(`Produkt ${product.name} zum Bearbeiten geladen.`, 'info');
        // Optional: Zum Formular scrollen
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };
    
    const handleCancelEdit = () => {
        setCurrentProduct(null);
        setStatusMessage({ text: '', type: '' });
    };

    const handleDelete = async (productId) => {
        if (!window.confirm('Sind Sie sicher, dass Sie dieses Produkt löschen möchten?')) return;

        try {
            const response = await fetch(`${API_URL_PRODUCTS}/${productId}`, {
                method: 'DELETE',
            });

            if (!response.ok) {
                throw new Error(`HTTP-Fehler! Status: ${response.status}`);
            }

            await fetchData(); // Liste neu laden
            showStatus('Produkt erfolgreich gelöscht.');
        } catch (error) {
            console.error('Fehler beim Löschen des Produkts:', error);
            showStatus(`Fehler beim Löschen: ${error.message}`, 'error');
        }
    };


    // --- STOFF-LOGIK (CRUD) ---

    const handleAddFabric = async (formData) => {
        const isEditing = !!formData.id;
        const url = isEditing ? `${API_URL_FABRICS}/${formData.id}` : API_URL_FABRICS;
        const method = isEditing ? 'PUT' : 'POST';

        const data = new FormData();
        Object.keys(formData).forEach(key => {
            if (key !== 'imageFile' && key !== 'imageUrl') {
                if (Array.isArray(formData[key])) {
                    // Verwendungszwecke als JSON-String senden
                    data.append(key, JSON.stringify(formData[key]));
                } else {
                    data.append(key, formData[key]);
                }
            }
        });

        // Bilddatei anhängen, wenn vorhanden
        if (formData.imageFile) {
            data.append('imageFile', formData.imageFile);
        } else if (isEditing && formData.imageUrl) {
            data.append('imageUrl', formData.imageUrl);
        }
        
        try {
            const response = await fetch(url, {
                method: method,
                body: data, 
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP-Fehler! Status: ${response.status}`);
            }

            await fetchData(); 
            setCurrentFabric(null); 
            showStatus(`Stoff erfolgreich ${isEditing ? 'aktualisiert' : 'angelegt'}.`);
        } catch (error) {
            console.error('Fehler beim Speichern des Stoffs:', error);
            showStatus(`Fehler beim Speichern des Stoffs: ${error.message}`, 'error');
        }
    };

    const handleEditFabric = (fabric) => {
        setCurrentFabric(fabric);
        setCurrentProduct(null); // Schließt ggf. das Produkt-Formular
        showStatus(`Stoff ${fabric.name} zum Bearbeiten geladen.`, 'info');
        window.scrollTo({ top: document.getElementById('fabric-management-section').offsetTop, behavior: 'smooth' });
    }

    const handleCancelFabricEdit = () => {
        setCurrentFabric(null);
        setStatusMessage({ text: '', type: '' });
    };

    const handleDeleteFabric = async (fabricId) => {
        if (!window.confirm('Sind Sie sicher, dass Sie diesen Stoff löschen möchten? Alle zugeordneten Produkte bleiben erhalten, verlieren aber die Stoffzuordnung.')) return;

        try {
            const response = await fetch(`${API_URL_FABRICS}/${fabricId}`, {
                method: 'DELETE',
            });

            if (!response.ok) {
                throw new Error(`HTTP-Fehler! Status: ${response.status}`);
            }

            await fetchData(); 
            showStatus('Stoff erfolgreich gelöscht.');
        } catch (error) {
            console.error('Fehler beim Löschen des Stoffs:', error);
            showStatus(`Fehler beim Löschen des Stoffs: ${error.message}`, 'error');
        }
    };


    // Status-Nachrichten-Stil (bleibt inline, da dynamisch)
    const statusStyle = {
        padding: '10px',
        margin: '10px 0 20px 0',
        borderRadius: '8px',
        textAlign: 'center',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: statusMessage.type === 'error' ? 'var(--farbe-rot)' : 'var(--farbe-grün)',
        display: statusMessage.text ? 'block' : 'none', 
    };

    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Daten werden geladen...</p>;
    }


    return (
        <div className="admin-dashboard-container">
            <h1 style={{ color: 'var(--farbe-primär)', marginBottom: '30px', fontSize: '2em', textAlign: 'center' }}>
                Admin-Dashboard
            </h1>
            
            <div style={statusStyle}>{statusMessage.text}</div>

            {/* 1. Bereich: PRODUKT-VERWALTUNG (Zwei Spalten) */}
            <h1 style={{ color: 'var(--farbe-akzent)', marginBottom: '30px', fontSize: '1.8em', borderBottom: '2px solid var(--farbe-grau)', paddingBottom: '10px' }}>
                Produkt-Verwaltung
            </h1>
            <div className="admin-grid-layout">
                
                {/* 1. Spalte: Produktformular */}
                <div>
                    <ProductForm
                        key={currentProduct ? currentProduct.id : 'new'} 
                        productToEdit={currentProduct}
                        onSubmit={handleProductSubmit}
                        onCancel={handleCancelEdit}
                        fabrics={fabrics} 
                    />
                </div>

                {/* 2. Spalte: Produktliste */}
                <div>
                    <AdminProductList 
                        products={products}
                        onEdit={handleEdit}
                        onDelete={handleDelete}
                        fabrics={fabrics} 
                    />
                </div>
            </div>
            
            
            {/* 2. Bereich: STOFF-VERWALTUNG (Unterhalb des Grids) */}
            <div id="fabric-management-section" style={{ 
                marginTop: '60px', 
                borderTop: '2px solid var(--farbe-primär)', 
                paddingTop: '30px' 
            }}>
                <h1 style={{ color: 'var(--farbe-primär)', marginBottom: '30px', fontSize: '1.8em' }}>
                    Stoff-Inventar-Verwaltung
                </h1>
                
                <div className="admin-grid-layout">
                    {/* Stoff-Formular */}
                    <div>
                        <FabricForm 
                            stoffToEdit={currentFabric} 
                            onSubmit={handleAddFabric} 
                            onCancel={handleCancelFabricEdit}
                        />
                    </div>
                    
                    {/* Stoff-Liste */}
                    <div>
                        <AdminFabricList 
                            fabrics={fabrics} 
                            onDelete={handleDeleteFabric} 
                            onEdit={handleEditFabric}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
