import React, { useState } from 'react';
import { useCart } from './CartContext';
import { PayPalScriptProvider, PayPalButtons } from '@paypal/react-paypal-js';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', email: '', phone: '',
    street: '', houseNumber: '', city: '', zip: '',
    paymentMethod: 'paypal'
  });

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2);

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    const res = await fetch('http://localhost:3001/api/checkout', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cartItems, formData })
    });
    const data = await res.json();
    if (formData.paymentMethod !== 'paypal') {
      alert(data.message);
      clearCart();
    }
  };

  return (
    <div className="checkout-container">
      <h2>Zur Kasse ‚ú®</h2>
      {cartItems.length === 0 ? <p>Dein Warenkorb ist leer</p> : (
        <>
          <p><strong>Gesamtbetrag: ‚Ç¨ {totalPrice}</strong></p>
          <form className="checkout-form" onSubmit={handleSubmit}>
            {/* ... alle Inputs ... */}
            <label>Zahlungsmethode</label>
            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
              <option value="paypal">PayPal</option>
              <option value="bank">√úberweisung</option>
              <option value="cash">Barzahlung</option>
            </select>
            <div className="checkout-buttons">
              <button type="submit">Bestellen</button>
              <button type="button" onClick={clearCart}>Alles l√∂schen</button>
            </div>
          </form>

          {formData.paymentMethod === 'paypal' && (
            <PayPalScriptProvider options={{ "client-id": process.env.REACT_APP_PAYPAL_CLIENT_ID, currency: "EUR" }}>
              <PayPalButtons
                style={{ layout: 'vertical' }}
                createOrder={async () => {
                  const res = await fetch('http://localhost:3001/api/checkout', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ cartItems, formData })
                  });
                  const data = await res.json();
                  return data.paypalOrderId;
                }}
                onApprove={async (data) => {
                  alert('PayPal-Zahlung erfolgreich! üíñ');
                  clearCart();
                }}
              />
            </PayPalScriptProvider>
          )}
        </>
      )}
    </div>
  );
};

export default Checkout;
