// client/src/components/ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';
import { useParams, useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/500x500/F2B2B4/4F3C3C?text=Stoffmuster";

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

const ProductDetail = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();

  const [product, setProduct] = useState(null);
  const [fabricName, setFabricName] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');

  const isInStock = product && product.stock > 0;

  useEffect(() => {
    const fetchProduct = async () => {
      setIsLoading(true);
      setError(null);

      try {
        const res = await fetch(`${API_URL}/products/${productId}`);
        if (!res.ok) throw new Error('Produkt nicht gefunden');

        const data = await res.json();
        setProduct(data);

        if (data.fabricId) {
          const fRes = await fetch(`${API_URL}/fabrics/${data.fabricId}`);
          if (fRes.ok) {
            const fData = await fRes.json();
            setFabricName(fData.name);
          }
        }

        setQuantity(Math.min(1, data.stock));
      } catch (err) { setError(err.message); }
      finally { setIsLoading(false); }
    };

    fetchProduct();
  }, [productId]);

  const handleAddToCart = () => {
    if (!product || !isInStock) return;
    addToCart(product, quantity);
    setMessage(`${quantity}x ${product.name} zum Warenkorb hinzugefügt`);
    setTimeout(() => setMessage(''), 3000);
  };

  if (isLoading) return <p style={{ textAlign: 'center', padding: '50px' }}>Produktdetails werden geladen...</p>;
  if (error) return <p style={{ textAlign: 'center', color:'red', padding: '50px', fontWeight:'bold' }}>{error}</p>;
  if (!product) return <p style={{ textAlign: 'center', padding:'50px' }}>Produkt nicht verfügbar</p>;

  return (
    <div style={{ maxWidth:'1000px', margin:'50px auto', padding:'20px' }}>
      <div style={{ display:'flex', gap:'30px', flexWrap:'wrap', backgroundColor:'#fff', borderRadius:'12px', padding:'30px', boxShadow:'0 8px 25px rgba(0,0,0,0.1)' }}>
        <div style={{ flex:'1 1 40%', minWidth:'300px' }}>
          <img src={getImageUrl(product.imageUrl)} alt={product.name} style={{ width:'100%', borderRadius:'8px', objectFit:'cover', aspectRatio:'1 / 1' }} />
        </div>
        <div style={{ flex:'1 1 60%' }}>
          <h1 style={{ color:'#FF6F61', fontSize:'2.5em' }}>{product.name}</h1>
          <p>{product.description}</p>
          <p><strong>Stoff:</strong> {fabricName || 'N/A'}</p>
          <p><strong>Bestand:</strong> {product.stock}</p>
          <p style={{ color:'#FF6F61', fontWeight:'bold', fontSize:'1.5em' }}>€ {product.price.toFixed(2)}</p>

          <div style={{ display:'flex', gap:'10px', alignItems:'center', marginTop:'20px' }}>
            <input type="number" min="1" max={product.stock} value={quantity} onChange={e => setQuantity(Math.max(1, Math.min(product.stock, parseInt(e.target.value)||1)))} style={{ width:'60px', padding:'8px', borderRadius:'5px', border:'1px solid #ccc' }} />
            <button onClick={handleAddToCart} disabled={!isInStock} style={{ padding:'10px 20px', backgroundColor: isInStock ? '#FF6F61' : '#aaa', color:'#fff', borderRadius:'5px', border:'none', cursor: isInStock ? 'pointer' : 'not-allowed', fontWeight:'bold' }}>
              {isInStock ? 'In den Warenkorb' : 'Nicht verfügbar'}
            </button>
          </div>

          {message && <p style={{ marginTop:'15px', color:'green', fontWeight:'bold' }}>{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
