// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogOut, Edit, Trash2, PlusCircle, CheckCircle, XCircle } from 'lucide-react';

// --- Hilfsfunktionen für Admin-Operationen (aus früheren Snippets übernommen) ---
const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";

const getAuthHeaders = () => {
    const adminToken = localStorage.getItem('adminToken');
    if (!adminToken) {
        return { error: 'Admin-Sitzung abgelaufen oder nicht gefunden.' };
    }
    return { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${adminToken}`,
    };
};

const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    return imagePath;
};
// --- Ende Hilfsfunktionen ---


// ANMERKUNG: Die vollständigen Definitionen der Unterkomponenten (ProductForm, AdminProductList etc.) 
// wurden der Übersichtlichkeit halber durch Platzhalter ersetzt, der Fokus liegt auf der Behebung des Fehlers.

// --- Komponente: AdminDashboard ---
// Fügt `onLogout` zu den Props hinzu
const AdminDashboard = ({ onLogout }) => {
    // onLogout ist nun verfügbar und wird von App.js übergeben
    const navigate = useNavigate();

    // Zustände und Handler (Platzhalter)
    const [products, setProducts] = useState([]);
    const [fabrics, setFabrics] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [currentProduct, setCurrentProduct] = useState(null);
    const [error, setError] = useState(null);

    // Placeholder-Funktionen (müssen im vollständigen Code implementiert sein)
    const handleCancelEdit = () => setCurrentProduct(null);
    const handleEdit = (product) => setCurrentProduct(product);
    
    // ... weitere Placeholder für die CRUD-Operationen

    // Effekt zum Laden der Daten beim Mounten
    useEffect(() => {
        const loadData = async () => {
            setIsLoading(true);
            try {
                // Hier würden die tatsächlichen Fetch-Aufrufe stattfinden
                setProducts([]); 
                setFabrics([]);
            } catch (err) {
                setError("Fehler beim Laden der Admin-Daten.");
            } finally {
                setIsLoading(false);
            }
        };
        loadData();
    }, []);
    
    // --- LOGOUT-HANDLER ---
    const handleLogoutClick = () => {
        // Ruft die von App.js übergebene onLogout-Funktion auf
        if (onLogout) {
            onLogout(); 
        }
        // Navigiert nach dem Logout zur Startseite
        navigate('/'); 
    };
    // --- ENDE LOGOUT-HANDLER ---


    const dashboardStyles = {
        padding: '30px 50px',
        maxWidth: '1200px',
        margin: '0 auto',
        backgroundColor: 'var(--farbe-hell)',
        minHeight: '80vh',
        borderRadius: '10px',
        boxShadow: '0 0 20px rgba(0,0,0,0.05)'
    };
    
    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Admin-Daten werden geladen...</p>;
    }

    if (error) {
        return <p style={{ textAlign: 'center', color: 'red', padding: '50px' }}>{error}</p>;
    }


    return (
        <div style={dashboardStyles}>
            
            {/* Hinzufügen der Kopfzeile mit Logout-Button */}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px', borderBottom: '2px solid var(--farbe-grau)', paddingBottom: '20px' }}>
                <h1 style={{ color: 'var(--farbe-primär)', fontSize: '2em' }}>
                    Admin Dashboard
                </h1>
                <button 
                    onClick={handleLogoutClick} 
                    style={{ 
                        display: 'flex', 
                        alignItems: 'center', 
                        gap: '8px', 
                        padding: '10px 20px', 
                        backgroundColor: 'var(--farbe-rot)', 
                        color: 'white', 
                        border: 'none', 
                        borderRadius: '8px', 
                        cursor: 'pointer',
                        fontWeight: 'bold',
                        transition: 'background-color 0.2s',
                    }}
                    onMouseEnter={(e) => e.target.style.backgroundColor = 'var(--farbe-primär-dunkel)'}
                    onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--farbe-rot)'}
                >
                    <LogOut size={20} />
                    Logout
                </button>
            </div>
            {/* Ende der Kopfzeile */}
            
            
            {/* 1. Bereich: PRODUKT-VERWALTUNG (Grid) */}
            <div style={{ 
                display: 'grid', 
                gridTemplateColumns: '1fr 2fr', 
                gap: '40px', 
                marginTop: '20px' 
            }}>
                {/* 1. Spalte: Produktformular */}
                <div>
                    <h2 style={{ color: 'var(--farbe-primär-dunkel)', marginBottom: '20px', fontSize: '1.5em' }}>
                        {currentProduct ? 'Produkt bearbeiten' : 'Neues Produkt erstellen'}
                    </h2>
                    
                    {/* Placeholder für ProductForm */}
                    <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '8px', backgroundColor: '#fff' }}>
                        <p>
                            **ProductForm Placeholder:** Wird normalerweise hier gerendert.
                        </p>
                        {/* <ProductForm
                            // ... Props
                        />
                        */}
                    </div>
                </div>

                {/* 2. Spalte: Produktliste */}
                <div>
                    <h2 style={{ color: 'var(--farbe-primär-dunkel)', marginBottom: '20px', fontSize: '1.5em' }}>
                        Produktliste
                    </h2>
                    {/* Placeholder für AdminProductList */}
                    <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '8px', backgroundColor: '#fff' }}>
                        <p>
                            **AdminProductList Placeholder:** Wird hier gerendert.
                        </p>
                        {/*
                        <AdminProductList 
                            // ... Props
                        />
                        */}
                    </div>
                </div>
            </div>
            
            
            {/* 2. Bereich: STOFF-VERWALTUNG (Unterhalb des Grids) */}
            <div style={{ 
                marginTop: '40px', 
                borderTop: '2px solid var(--farbe-grau)', 
                paddingTop: '30px' 
            }}>
                <h1 style={{ color: 'var(--farbe-primär)', marginBottom: '30px', fontSize: '1.8em' }}>
                    Stoff-Inventar-Verwaltung
                </h1>
                
                {/* Stoff-Formular */}
                <div style={{ marginBottom: '30px', padding: '20px', border: '1px solid #ccc', borderRadius: '8px', backgroundColor: '#fff' }}>
                    <p>
                        **FabricForm Placeholder:** Wird hier gerendert.
                    </p>
                    {/*
                    <FabricForm 
                        // ... Props
                    />
                    */}
                </div>
                
                {/* Stoff-Liste */}
                <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '8px', backgroundColor: '#fff' }}>
                    <p>
                        **AdminFabricList Placeholder:** Wird hier gerendert.
                    </p>
                    {/*
                    <AdminFabricList fabrics={fabrics} onDelete={handleDeleteFabric} />
                    */}
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
