import React, { useState, useEffect } from 'react';
import ProductForm from './ProductForm'; // Angepasster Pfad

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE = "https://placehold.co/50x50/eee/333?text=Bild";


// FIX: Diese Funktion stellt sicher, dass /uploads/ Pfade die Server-URL erhalten
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    // Handhabt externe URLs (wie placehold.co) oder defekte Pfade
    return imagePath;
};


// --- Komponente: Produktliste ---
const AdminProductList = ({ products, onEdit, onDelete, setStatusMessage }) => {
    
    // Einfaches Styling für die Listenelemente
    const listStyle = {
        listStyle: 'none',
        padding: 0,
    };
    
    const listItemStyle = {
        display: 'flex',
        alignItems: 'center',
        padding: '15px',
        borderBottom: '1px solid #eee',
        backgroundColor: 'white',
        borderRadius: '8px',
        marginBottom: '10px',
        boxShadow: '0 2px 5px rgba(0, 0, 0, 0.05)',
    };
    
    const imageStyle = {
        width: '50px',
        height: '50px',
        objectFit: 'cover',
        borderRadius: '5px',
        marginRight: '15px',
        border: '1px solid #ddd',
    };
    
    const infoStyle = {
        flexGrow: 1,
    };
    
    const buttonGroupStyle = {
        display: 'flex',
        gap: '10px',
    };
    
    const buttonStyle = (color) => ({
        padding: '8px 15px',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: color,
        transition: 'background-color 0.3s ease',
    });
    
    const handleDeleteClick = async (productId, productName) => {
        if (!window.confirm(`Sind Sie sicher, dass Sie das Produkt "${productName}" löschen möchten?`)) {
            return;
        }
        
        try {
            const response = await fetch(`${API_URL}/${productId}`, {
                method: 'DELETE',
            });
            
            if (!response.ok) {
                throw new Error('Fehler beim Löschen des Produkts.');
            }
            
            // Erfolg: Aktualisiert die Liste über den Parent-Zustand
            onDelete(productId);
            setStatusMessage({ text: `Produkt "${productName}" erfolgreich gelöscht.`, type: 'info' });
        } catch (error) {
            console.error('Löschfehler:', error);
            setStatusMessage({ text: 'Fehler beim Löschen des Produkts.', type: 'error' });
        }
    };


    return (
        // Verwendet jetzt Inline-Styles und die Struktur der Formular-Styles
        <div style={{ padding: '30px', backgroundColor: 'var(--farbe-hell)', borderRadius: '15px', boxShadow: '0 8px 25px rgba(0, 0, 0, 0.1)' }}>
            <h2 style={{ fontSize: '1.5em', color: 'var(--farbe-primär)', margin: '0 0 20px 0' }}>
                Alle Produkte im System ({products.length})
            </h2>
            {products.length === 0 ? (
                <p style={{ color: '#666' }}>Keine Produkte gefunden.</p>
            ) : (
                <ul style={listStyle}>
                    {products.map(product => (
                        <li key={product.id} style={listItemStyle}>
                            <img 
                                src={getImageUrl(product.imageUrl || FALLBACK_IMAGE)} 
                                alt={product.name} 
                                style={imageStyle} 
                                onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                            />
                            <div style={infoStyle}>
                                <h4 style={{ margin: '0 0 5px 0', fontSize: '1.1em', color: 'var(--farbe-text)' }}>{product.name}</h4>
                                <p style={{ margin: 0, fontSize: '0.9em', color: '#666' }}>
                                    Preis: €{product.price.toFixed(2)} | Bestand: {product.stock} | Featured: {product.isFeatured ? 'Ja' : 'Nein'}
                                </p>
                            </div>
                            <div style={buttonGroupStyle}>
                                <button 
                                    onClick={() => onEdit(product)} 
                                    style={buttonStyle('#286090')} // Blau
                                    onMouseEnter={(e) => e.target.style.backgroundColor = '#1d486f'}
                                    onMouseLeave={(e) => e.target.style.backgroundColor = '#286090'}
                                >
                                    Bearbeiten
                                </button>
                                <button 
                                    onClick={() => handleDeleteClick(product.id, product.name)} 
                                    style={buttonStyle('#ac292b')} // Rot
                                    onMouseEnter={(e) => e.target.style.backgroundColor = '#8c1f21'}
                                    onMouseLeave={(e) => e.target.style.backgroundColor = '#ac292b'}
                                >
                                    Löschen
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};


// --- Hauptkomponente: AdminDashboard ---
const AdminDashboard = () => {
    const [products, setProducts] = useState([]);
    const [currentProduct, setCurrentProduct] = useState(null); // Produkt, das bearbeitet wird
    const [isLoading, setIsLoading] = useState(true);
    // { text: String, type: 'info' | 'error' }
    const [statusMessage, setStatusMessage] = useState({ text: '', type: '' }); 

    // 1. Datenabruf beim Laden der Komponente
    const fetchProducts = async () => {
        setIsLoading(true);
        try {
            const response = await fetch(API_URL);
            if (!response.ok) throw new Error('Netzwerkfehler beim Abrufen der Produkte.');
            const data = await response.json();
            setProducts(data);
            setStatusMessage({ text: '', type: '' });
        } catch (error) {
            console.error("Fehler:", error);
            setStatusMessage({ text: 'Fehler beim Laden der Produktliste.', type: 'error' });
        } finally {
            setIsLoading(false);
        }
    };
    
    useEffect(() => {
        fetchProducts();
    }, []); 

    // 2. Produkt erstellen/speichern
    const handleProductSubmit = async (formData, isEditing) => {
        const data = new FormData();
        
        // Füge alle Felder hinzu
        data.append('name', formData.name);
        data.append('description', formData.description);
        data.append('price', formData.price);
        data.append('stock', formData.stock);
        data.append('isFeatured', formData.isFeatured);

        // Füge die Datei nur hinzu, wenn eine neue Datei ausgewählt wurde
        if (formData.imageFile) {
            data.append('imageFile', formData.imageFile);
        } else if (isEditing && formData.imageUrl) {
            // Wenn wir bearbeiten und kein neues Bild hochgeladen wurde,
            // senden wir die aktuelle URL als String, damit das Backend sie behält.
            data.append('imageUrl', formData.imageUrl); 
        }

        const method = isEditing ? 'PUT' : 'POST';
        const url = isEditing ? `${API_URL}/${formData.id}` : API_URL;

        try {
            const response = await fetch(url, {
                method: method,
                // ACHTUNG: Bei FormData NICHT 'Content-Type': 'application/json' setzen!
                body: data, 
            });

            if (!response.ok) {
                // Versuche, eine Fehlermeldung aus dem Backend zu lesen
                const errorText = await response.text();
                throw new Error(`Fehler beim Speichern: ${errorText || response.statusText}`);
            }

            // Erfolg: Daten neu laden, Formular zurücksetzen
            fetchProducts();
            setCurrentProduct(null); // Bearbeitungsmodus beenden
            setStatusMessage({ text: `Produkt "${formData.name}" erfolgreich ${isEditing ? 'aktualisiert' : 'erstellt'}.`, type: 'info' });
            
        } catch (error) {
            console.error("Speicherfehler:", error);
            setStatusMessage({ text: error.message || 'Ein unbekannter Fehler ist aufgetreten.', type: 'error' });
        }
    };
    
    // 3. Bearbeitungsmodus starten
    const handleEdit = (product) => {
        setCurrentProduct(product);
        setStatusMessage({ text: `Bearbeite Produkt: ${product.name}`, type: 'info' });
        // Optional: Zum Formular scrollen
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };
    
    // 4. Bearbeitungsmodus abbrechen
    const handleCancelEdit = () => {
        setCurrentProduct(null);
        setStatusMessage({ text: '', type: '' });
    };

    // 5. Löschen aus der Liste (nach erfolgreicher API-Antwort in AdminProductList)
    const handleDeleteFromList = (productId) => {
        setProducts(prevProducts => prevProducts.filter(p => p.id !== productId));
    };

    // Status-Nachrichten-Stil (bleibt inline, da dynamisch)
    const statusStyle = {
        padding: '10px',
        margin: '10px 0',
        borderRadius: '8px',
        textAlign: 'center',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: statusMessage.type === 'error' ? '#ac292b' : '#4caf50',
        display: statusMessage.text ? 'block' : 'none', 
    };
    
    const dashboardContainerStyle = {
        maxWidth: '1200px',
        margin: '50px auto',
        padding: '0 20px',
        display: 'grid',
        gridTemplateColumns: '1fr',
        gap: '30px',
    };
    
    // Wenn geladen wird
    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Admin Dashboard wird geladen...</p>;
    }


    return (
        <div style={dashboardContainerStyle}>
            
            <div style={statusStyle}>{statusMessage.text}</div>

            {/* 1. Spalte: Produktformular */}
            <ProductForm
                // Key zwingt React, die Komponente bei Wechsel des Produkts neu zu rendern
                key={currentProduct ? currentProduct.id : 'new'} 
                productToEdit={currentProduct}
                onSubmit={handleProductSubmit}
                onCancel={handleCancelEdit}
            />

            {/* 2. Spalte: Produktliste */}
            <AdminProductList 
                products={products}
                onEdit={handleEdit}
                onDelete={handleDeleteFromList}
                setStatusMessage={setStatusMessage} // Übergibt die Setter-Funktion
            />
            
            {/* Debug Info */}
            <p style={{marginTop: '20px', fontSize: '0.8em', color: '#666', borderTop: '1px solid #eee', paddingTop: '10px'}}>
                Backend-API-URL: {API_URL}
            </p>
        </div>
    );
};

export default AdminDashboard;
