                        <button onClick={handleFabricClick}>Zu den Stoffen</button>
                    </div>
                </div>

                <div className="hero-logo">
                    <img 
                        src={logoAsset} 
                        alt="Henkes Stoffzauber Logo"
                        onError={(e) => { 
                            e.target.onerror = null; 
                            e.target.src = 'https://placehold.co/350x150/6f42c1/ffffff?text=LOGO+FEHLT';
                        }}
                    />
                </div>
            </section>

