import React from 'react';
import { useNavigate } from 'react-router-dom'; 

// KORREKTUR: Das Logo wird jetzt direkt importiert, da es in src/assets/ liegt.
// Der Importname 'logoAsset' repräsentiert den korrekten Pfad, den der Bundler generiert.
import logoAsset from './assets/logo.jpg'; // Angenommen, logo.jpg liegt direkt neben Home.js in src/assets/

// Die Komponente ImageCarousel wird nun importiert, da sie im Code verwendet wird.
import ImageCarousel from './ImageCarousel'; 

const Home = () => {
    
    // Hook für die Navigation
    const navigate = useNavigate(); 
    
    // --- Styles für den Hauptinhalt ---
    const containerStyle = {
        maxWidth: '1200px',
        margin: '50px auto 50px auto', 
        padding: '0 20px',
        textAlign: 'center',
    };
    
    // Style für das Intro-Banner
    const introBoxStyle = {
        padding: '40px',
        //backgroundColor: 'var(--farbe-hell, #ffffff)',
        //sborderRadius: '15px',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center', 
        marginBottom: '40px',
    };

    // Style für das Logo im Hauptinhalt
    const logoStyle = {
        Width: '500px', 
        //height: 'auto',
        margin: '0 auto 30px auto', 
        borderRadius: '10px',
        //boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
    };

    const textStyle = {
        fontSize: '1.1em',
        lineHeight: '1.6',
        color: 'var(--farbe-text)',
        marginBottom: '30px',
    };

    const buttonStyle = {
        padding: '12px 30px',
        backgroundColor: 'var(--farbe-akzent)',
        color: 'white',
        textDecoration: 'none',
        borderRadius: '8px',
        fontWeight: 'bold',
        fontSize: '1.1em',
        transition: 'background-color 0.3s ease',
    };
    
    const handleShopClick = (e) => {
        e.preventDefault();
        // Verwende useNavigate für die clientseitige Navigation
        navigate('/shop'); 
    };

    return (
        <div style={containerStyle}>
            {/* 1. Intro-Bereich */}
            <div style={introBoxStyle}>
                <img 
                    // Verwendung des importierten Assets
                    src={logoAsset} 
                    alt="Henkes Stoffzauber Logo" 
                    style={logoStyle} 
                    // Fallback-Bild, falls der Pfad nicht funktioniert (obwohl der Import das normalerweise verhindert)
                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/350x150/6f42c1/ffffff?text=LOGO+FEHLT'; }}
                />
                
                <h1 style={{ color: 'var(--farbe-primär, #6f42c1)', marginBottom: '20px' }}>Willkommen bei Henkes Stoffzauber</h1>
                <p style={textStyle}>
                    Entdecken Sie unsere handverlesene Auswahl an hochwertigen Stoffen und kreativen Näh-Ideen. 
                    Hier finden Sie alles, was Ihr Nähherz begehrt – von Jersey bis Leder. Lassen Sie sich inspirieren 
                    und beginnen Sie Ihr nächstes Nähprojekt noch heute!
                </p>
                <a 
                    href="/shop" 
                    style={buttonStyle}
                    // Implementierung eines einfachen Hover-Effekts
                    onMouseEnter={(e) => e.target.style.backgroundColor = 'var(--farbe-sekundär)'} 
                    onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--farbe-akzent)'}
                    onClick={handleShopClick}
                >
                    Zum Shop
                </a>
            </div>
            
            {/* 2. Featured Produkte Karussell */}
            <h2 style={{ color: 'var(--farbe-primär)', marginBottom: '20px', marginTop: '40px', fontSize: '1.8em' }}>
                ⭐ Unsere Highlights der Woche ⭐
            </h2>
            {/* HINWEIS: ImageCarousel muss als Datei ImageCarousel.js existieren */}
            <ImageCarousel /> 
            
            {/* 3. Text & Info */}
            <div style={{ padding: '40px 0', borderTop: '1px solid var(--farbe-grenze)' }}>
                <h2 style={{ color: 'var(--farbe-text)', marginBottom: '15px' }}>Qualität, die man fühlt</h2>
                <p style={textStyle}>
                    Jeder Stoff in unserem Sortiment wird sorgfältig ausgewählt, um höchste Ansprüche an Haptik,
                    Haltbarkeit und Farbtreue zu erfüllen. Bei uns kaufen Sie nicht nur Material, sondern die Grundlage
                    für Ihre Meisterwerke. Besuchen Sie auch unseren Blog für spannende Anleitungen!
                </p>
            </div>
        </div>
    );
};

export default Home;
