import React, { useState, useEffect, useMemo } from 'react';

// Verwendung von useMemo, um ESLint-Warnung (exhaustive-deps) zu beheben
const getInitialState = (productToEdit) => productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0, // FIX: Bestand (stock) wird hier als Standardwert 0 hinzugefügt
    isFeatured: false,
    imageFile: null,
    imageUrl: '', 
};

/**
 * Komponente für die Erstellung und Bearbeitung von Produkten.
 * Sie wird von AdminDashboard verwendet.
 */
const ProductForm = ({ productToEdit, onSubmit, onCancel }) => {
    
    // FIX: useMemo behebt die ESLint-Warnung für die Initialisierung des Zustands
    const initialState = useMemo(() => getInitialState(productToEdit), [productToEdit]);
    
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!productToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);
    const [error, setError] = useState(null);


    useEffect(() => {
        // Setzt das Formular zurück, wenn ein neues oder kein Produkt zum Bearbeiten übergeben wird
        setFormData(initialState);
        setIsEditing(!!productToEdit);
        setPreviewImage(initialState.imageUrl || null);
        setError(null);
    }, [initialState, productToEdit]); 
    

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
        setError(null);
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setFormData(prev => ({
            ...prev,
            imageFile: file,
            imageUrl: file ? URL.createObjectURL(file) : '', // Temporäre URL für die Vorschau
        }));
        setPreviewImage(file ? URL.createObjectURL(file) : null);
        setError(null);
    };
    
    // Hilfsfunktion zur Formatierung des Preises
    const formatPrice = (price) => {
        const num = parseFloat(price);
        return isNaN(num) ? 0 : num.toFixed(2);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        // Einfache Validierung
        if (!formData.name || formData.price <= 0 || formData.stock < 0) {
            setError('Bitte füllen Sie alle Felder korrekt aus (Preis > 0, Name benötigt).');
            return;
        }

        // Ruft die übergeordnete onSubmit-Funktion auf und übergibt die Daten
        // Die onSubmit-Funktion im Dashboard ist für die API-Logik verantwortlich
        onSubmit(formData, isEditing);
    };

    // --- Inline Styles für das Admin Formular (Teil der Migration zu einer flachen Struktur) ---
    const formStyle = {
        padding: '30px',
        backgroundColor: 'var(--farbe-hell)',
        borderRadius: '15px',
        boxShadow: '0 8px 25px rgba(0, 0, 0, 0.1)',
        marginBottom: '20px',
    };
    
    const inputStyle = {
        padding: '10px',
        border: '1px solid #ccc',
        borderRadius: '8px',
        width: '100%',
        boxSizing: 'border-box',
        marginTop: '5px',
        marginBottom: '15px',
    };
    
    const labelStyle = {
        display: 'block',
        marginBottom: '5px',
        fontWeight: 'bold',
        color: 'var(--farbe-text)',
    };
    
    const buttonStyle = {
        padding: '12px 25px',
        backgroundColor: 'var(--farbe-akzent)',
        color: 'white',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontWeight: 'bold',
        transition: 'background-color 0.3s ease',
    };
    
    const imagePreviewStyle = {
        maxWidth: '150px',
        maxHeight: '150px',
        objectFit: 'cover',
        borderRadius: '8px',
        border: '1px solid #ccc',
        marginBottom: '10px',
    };

    return (
        <form onSubmit={handleSubmit} style={formStyle}>
            <h2 style={{ color: 'var(--farbe-primär)', marginBottom: '20px' }}>
                {isEditing ? `Produkt bearbeiten: ${formData.name}` : 'Neues Produkt erstellen'}
            </h2>
            
            {error && (
                <p style={{ color: 'var(--farbe-rot)', marginBottom: '15px', fontWeight: 'bold' }}>
                    {error}
                </p>
            )}

            {/* Name */}
            <div className="admin-form-group">
                <label htmlFor="name" style={labelStyle}>Name des Stoffes / Zubehörs:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    style={inputStyle}
                    required
                />
            </div>
            
            {/* Beschreibung */}
            <div className="admin-form-group">
                <label htmlFor="description" style={labelStyle}>Beschreibung:</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                    style={{ ...inputStyle, resize: 'vertical' }}
                    required
                ></textarea>
            </div>
            
            {/* Preis & Bestand Grid */}
            <div style={{ display: 'flex', gap: '20px' }}>
                {/* Preis */}
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="price" style={labelStyle}>Preis (€):</label>
                    <input
                        type="number"
                        id="price"
                        name="price"
                        step="0.01"
                        min="0.01"
                        value={formData.price}
                        onChange={handleChange}
                        style={inputStyle}
                        required
                    />
                </div>
                {/* Bestand */}
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="stock" style={labelStyle}>Bestand:</label>
                    <input
                        type="number"
                        id="stock"
                        name="stock"
                        min="0"
                        value={formData.stock}
                        onChange={handleChange}
                        style={inputStyle}
                        required
                    />
                </div>
            </div>
            
            {/* Bild-Upload und Vorschau */}
            <div className="admin-form-group">
                <label htmlFor="imageFile" style={labelStyle}>Bild:</label>
                {/* Zeigt die aktuelle/alte Bild-URL oder die temporäre Vorschau */}
                {previewImage && (
                    <div>
                        <img src={previewImage} alt="Produktvorschau" style={imagePreviewStyle} />
                        {formData.imageFile && <p style={{fontSize: '0.9em', color: '#666'}}>Neues Bild zum Hochladen ausgewählt.</p>}
                        {!formData.imageFile && formData.imageUrl && <p style={{fontSize: '0.9em', color: '#666'}}>Aktuelles Bild vorhanden.</p>}
                    </div>
                )}
                {/* Nur Bilddatei hochladen, wenn nicht nur URL-Eingabe vorhanden ist */}
                <input
                    type="file"
                    id="imageFile"
                    name="imageFile"
                    accept="image/*"
                    onChange={handleFileChange}
                    // Wenn wir bearbeiten und bereits eine URL haben, ist die Datei optional
                    required={!isEditing || !formData.imageUrl} 
                    style={{ ...inputStyle, border: 'none', padding: '0', marginTop: '10px' }}
                />
            </div>
            
            <div className="admin-form-group" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <input
                    type="checkbox"
                    id="isFeatured"
                    name="isFeatured"
                    checked={formData.isFeatured}
                    onChange={handleChange}
                    style={{ width: '20px', height: '20px' }}
                />
                <label htmlFor="isFeatured" style={{ margin: 0, fontWeight: 'normal', cursor: 'pointer' }}>
                    Als "Featured Product" auf der Startseite anzeigen
                </label>
            </div>
            
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <button type="submit" style={buttonStyle}>
                    {isEditing ? 'Änderungen speichern' : 'Produkt erstellen'}
                </button>
            </div>
            
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} style={{...buttonStyle, backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};

export default ProductForm;
