// client/src/components/ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/500x500/F2B2B4/4F3C3C?text=Stoffmuster";

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

const fetchFabricName = async (fabricId) => {
  if (!fabricId) return null;
  try {
    const res = await fetch(`${API_URL}/fabrics/${fabricId}`);
    if (!res.ok) return null;
    const data = await res.json();
    return data.name;
  } catch {
    return null;
  }
};

const ProductDetail = ({ productId, navigate }) => {
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [fabricName, setFabricName] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const isInStock = product && product.stock > 0;

  useEffect(() => {
    const fetchProduct = async () => {
      setLoading(true);
      try {
        if (!productId) { navigate('/shop'); return; }
        const res = await fetch(`${API_URL}/products/${productId}`);
        if (!res.ok) throw new Error('Produkt nicht gefunden');
        const data = await res.json();
        setProduct(data);
        if (data.fabricId) setFabricName(await fetchFabricName(data.fabricId));
        setQuantity(Math.min(quantity, data.stock));
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [productId, navigate]);

  const handleAddToCart = () => {
    if (!product || !isInStock) return;
    addToCart(product, quantity);
    setMessage(`${quantity}x ${product.name} zum Warenkorb hinzugefügt`);
    setTimeout(() => setMessage(''), 3000);
  };

  if (loading) return <p style={{ textAlign:'center', padding:'50px' }}>Produkt wird geladen...</p>;
  if (error) return <p style={{ textAlign:'center', color:'red', padding:'50px', fontWeight:'bold' }}>Fehler: {error}</p>;
  if (!product) return <p style={{ textAlign:'center', padding:'50px' }}>Produkt nicht verfügbar</p>;

  return (
    <div style={{ maxWidth:'1000px', margin:'50px auto', padding:'20px' }}>
      <div style={{ display:'flex', gap:'30px', flexWrap:'wrap' }}>
        <div style={{ flex:'1 1 40%' }}>
          <img src={getImageUrl(product.imageUrl)} alt={product.name} style={{ width:'100%', borderRadius:'8px' }} />
        </div>
        <div style={{ flex:'1 1 55%' }}>
          <h1 style={{ color:'var(--farbe-primär)' }}>{product.name}</h1>
          <p>{product.description}</p>
          <p><strong>Preis:</strong> {product.price.toFixed(2)} €</p>
          <p><strong>Stoff:</strong> {fabricName || 'N/A'}</p>
          <p><strong>Bestand:</strong> {product.stock}</p>

          <div style={{ marginTop:'20px', display:'flex', alignItems:'center', gap:'15px' }}>
            <input type="number" min="1" max={product.stock} value={quantity} onChange={e => setQuantity(Math.max(1, Math.min(product.stock, parseInt(e.target.value)||1)))} disabled={!isInStock} />
            <button onClick={handleAddToCart} disabled={!isInStock} style={{ padding:'10px 20px', borderRadius:'5px', backgroundColor:isInStock?'var(--farbe-akzent)':'#aaa', color:'white', cursor:isInStock?'pointer':'not-allowed' }}>
              {isInStock ? 'In den Warenkorb' : 'Nicht verfügbar'}
            </button>
          </div>

          {message && <p style={{ marginTop:'20px', color:'green', fontWeight:'bold' }}>{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
