import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import logo from './assets/logo.jpg'; 
import ImageCarousel from './components/ImageCarousel'; // !!! NEU: Karussell importieren !!!

// --- Platzhalter-Komponenten für die Seiten ---

const HomePage = () => (
  <div style={{ padding: '20px', textAlign: 'center' }}>
    <img 
      src={logo} 
      alt="Henkes-Stoffzauber Logo" 
      style={{ width: '150px', margin: '20px auto', display: 'block' }}
    />
    <h2>Willkommen im Näh-Paradies!</h2>
    <p>Hier finden Sie die neuesten Schnitte, Stoffe und Zubehör für Ihre Mützen, Schals und Taschen. Starten Sie jetzt Ihre Kreativität!</p>

    {/* !!! NEU: Das Bilderkarussell hier einbinden !!! */}
    <ImageCarousel />

    {/* Die Buttons wurden entfernt, da das Karussell sie ersetzt */}
    {/* Wenn Sie Links zu Shop/Blog beibehalten möchten, könnten Sie sie unter dem Karussell platzieren */}
    {/* <Link to="/shop" className="nav-button">Zum Shop</Link>
    <Link to="/blog" className="nav-button" style={{ marginLeft: '10px' }}>Zum Blog</Link> */}
  </div>
);

