// src/components/Checkout.js
import React, { useState } from 'react';
import { useCart } from './CartContext';
import './Checkout.css'; // optional eigene Styles

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    payment: 'paypal'
  });

  const totalPrice = cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0);

  const handleChange = (e) => {
    setForm({...form, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Hier würdest du die Bestellung an deinen Server senden
    alert(`Bestellung abgeschickt!\nGesamt: € ${totalPrice.toFixed(2)}\nZahlung: ${form.payment}`);
    clearCart();
  };

  if(cartItems.length === 0) return <p>Dein Warenkorb ist leer</p>;

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <ul>
        {cartItems.map(item => (
          <li key={item.id}>
            {item.name} x {item.quantity} = € {(item.price * item.quantity).toFixed(2)}
          </li>
        ))}
      </ul>
      <p><strong>Gesamt: € {totalPrice.toFixed(2)}</strong></p>

      <form onSubmit={handleSubmit} className="checkout-form">
        <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="E-Mail" value={form.email} onChange={handleChange} required />
        <input type="tel" name="phone" placeholder="Telefon" value={form.phone} onChange={handleChange} required />
        <input type="text" name="address" placeholder="Adresse" value={form.address} onChange={handleChange} required />

        <select name="payment" value={form.payment} onChange={handleChange}>
          <option value="paypal">PayPal</option>
          <option value="bank">Überweisung</option>
          <option value="cash">Barzahlung bei Abholung</option>
        </select>

        <button type="submit">Bestellung abschicken</button>
      </form>
    </div>
  );
};

export default Checkout;
