// src/components/Checkout.js
import React, { useEffect, useState } from "react";

const Checkout = ({ cart, address, onPaymentSuccess }) => {
  const [paypalLoaded, setPaypalLoaded] = useState(false);
  const [paypalClientId, setPaypalClientId] = useState("");
  const [paypalEnv, setPaypalEnv] = useState("sandbox");

  // Hol PayPal-Konfiguration vom Server
  useEffect(() => {
    fetch("http://localhost:3001/api/config")
      .then((res) => res.json())
      .then((data) => {
        setPaypalClientId(data.paypalClientId);
        setPaypalEnv(data.paypalEnv);
      });
  }, []);

  // PayPal SDK dynamisch laden
  useEffect(() => {
    if (!paypalClientId || paypalLoaded) return;

    const script = document.createElement("script");
    script.src = `https://www.paypal.com/sdk/js?client-id=${paypalClientId}&currency=EUR&intent=capture`;
    script.addEventListener("load", () => setPaypalLoaded(true));
    document.body.appendChild(script);

    return () => document.body.removeChild(script);
  }, [paypalClientId, paypalLoaded]);

  // Render PayPal Button, wenn SDK geladen
  useEffect(() => {
    if (!paypalLoaded || !window.paypal) return;

    const total = cart.reduce((sum, i) => sum + i.price * i.quantity, 0).toFixed(2);

    window.paypal.Buttons({
      createOrder: async (_, actions) => {
        // Bestell-Daten an Backend schicken
        const response = await fetch("http://localhost:3001/api/checkout", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ cart, address, paymentMethod: "paypal" }),
        });

        const data = await response.json();
        if (!data.success) throw new Error(data.error);

        // Gib PayPal die ID der erstellten Order
        return data.paypalOrderId;
      },
      onApprove: async (data, actions) => {
        // Capture Order
        const res = await actions.order.capture();
        console.log("PayPal Capture Result:", res);

        if (onPaymentSuccess) onPaymentSuccess(res);
      },
      onError: (err) => console.error("PayPal Error:", err),
    }).render("#paypal-button-container");
  }, [paypalLoaded, cart, address, onPaymentSuccess]);

  return <div id="paypal-button-container"></div>;
};

export default Checkout;
