import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', email: '', phone: '',
    street: '', houseNumber: '', city: '', zip: '',
    paymentMethod: 'paypal'
  });
  const [paypalLoaded, setPaypalLoaded] = useState(false);

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = "https://www.paypal.com/sdk/js?client-id=" + process.env.REACT_APP_PAYPAL_CLIENT_ID + "&currency=EUR";
    script.addEventListener('load', () => setPaypalLoaded(true));
    document.body.appendChild(script);
    return () => document.body.removeChild(script);
  }, []);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.paymentMethod === 'paypal') return; // PayPal Button kümmert sich um alles

    // Bei Bank oder Cash
    const res = await fetch('http://localhost:3001/api/checkout', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cart: cartItems, address: formData, paymentMethod: formData.paymentMethod })
    });
    const data = await res.json();
    if (data.success) { alert('Vielen Dank für Ihre Bestellung!'); clearCart(); }
  };

  return (
    <div className="checkout-container">
      <h2>Zur Kasse ✨</h2>
      {cartItems.length === 0 ? <p>Dein Warenkorb ist leer</p> : (
        <>
          <p><strong>Gesamtbetrag: € {totalPrice.toFixed(2)}</strong></p>
          <form className="checkout-form" onSubmit={handleSubmit}>
            {/* Eingabefelder */}
            <label>Vorname</label><input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
            <label>Nachname</label><input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />
            <label>Email</label><input type="email" name="email" value={formData.email} onChange={handleChange} required />
            <label>Telefon</label><input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />
            <label>Straße</label><input type="text" name="street" value={formData.street} onChange={handleChange} required />
            <label>Hausnummer</label><input type="text" name="houseNumber" value={formData.houseNumber} onChange={handleChange} required />
            <label>Stadt</label><input type="text" name="city" value={formData.city} onChange={handleChange} required />
            <label>Postleitzahl</label><input type="text" name="zip" value={formData.zip} onChange={handleChange} required />

            <label>Zahlungsmethode</label>
            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
              <option value="paypal">PayPal</option>
              <option value="bank">Überweisung</option>
              <option value="cash">Barzahlung</option>
            </select>

            <div className="checkout-buttons">
              <button type="submit">Jetzt bestellen</button>
              <button type="button" onClick={clearCart}>Alles löschen</button>
            </div>
          </form>

          {paypalLoaded && formData.paymentMethod === 'paypal' && (
            <div id="paypal-button-container"></div>
          )}
        </>
      )}
    </div>
  );
};

export default Checkout;
