// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useMemo } from 'react';

// Da ProductForm in dieser Umgebung nicht separat aufgelöst werden konnte,
// fügen wir den Code von ProductForm.js hier temporär ein, um die Funktionalität
// für die Stoffauswahl zu integrieren und das Kompilierungsproblem zu beheben.

// =========================================================================
// BEGINN: EINGEFÜGTER CODE VON ProductForm.js (Temporär für Kompilierung)
// =========================================================================

// Hilfsfunktion für den initialen Zustand des Formulars
const getInitialState = (productToEdit) => productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0,
    isFeatured: false,
    imageFile: null,
    imageUrl: '',
    // NEU: Platzhalter für ausgewählten Stoff
    fabricId: '', 
};

// --- Komponente: Produktformular (ProductForm) ---
// HINWEIS: Diese Komponente wurde erweitert, um Stoffe (fabrics) als Prop zu empfangen.
const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
    
    // FIX: useMemo behebt die ESLint-Warnung
    const initialState = useMemo(() => getInitialState(productToEdit), [productToEdit]);
    
    // Zustand für das Formular
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!productToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);


    useEffect(() => {
        // Setzt das Formular zurück, wenn ein neues oder kein Produkt zum Bearbeiten übergeben wird
        setFormData(initialState);
        setIsEditing(!!productToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, productToEdit]); 
    

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setFormData(prev => ({
            ...prev,
            imageFile: file,
            imageUrl: '', // Löscht alte URL, wenn eine neue Datei ausgewählt wird
        }));
        if (file) {
            setPreviewImage(URL.createObjectURL(file));
        } else {
            setPreviewImage(formData.imageUrl || null);
        }
    };
    
    const handleSubmit = (e) => {
        e.preventDefault();
        
        if (!formData.name || formData.price <= 0 || formData.stock < 0) {
            // alert wird hier verwendet, um den bestehenden Stil der App beizubehalten
            alert("Bitte füllen Sie alle erforderlichen Felder korrekt aus (Name, Preis > 0, Bestand >= 0)."); 
            return;
        }

        // Ruft die onSubmit-Funktion des AdminDashboard auf
        onSubmit(formData, isEditing);
    };

    const getImageUrl = (imagePath) => {
        // Wenn der Pfad mit '/uploads/' beginnt, muss die Basis-URL hinzugefügt werden
        if (imagePath && imagePath.startsWith('/uploads/')) {
            return `http://localhost:3001${imagePath}`;
        }
        return imagePath;
    };
    
    // Fallback-Bild
    const FALLBACK_IMAGE = "https://placehold.co/100x100/eee/333?text=Bild";


    return (
        <form onSubmit={handleSubmit} className="admin-form">
            <h3 className="admin-form-title">
                {isEditing ? `Produkt bearbeiten: ${formData.name}` : 'Neues Produkt hinzufügen'}
            </h3>

            {/* Name */}
            <div className="admin-form-group">
                <label htmlFor="name">Produktname:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="z.B. Gestrickte Beanie-Mütze"
                    required
                />
            </div>
            
            {/* Beschreibung */}
            <div className="admin-form-group">
                <label htmlFor="description">Beschreibung:</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                    placeholder="Eine kuschelige Mütze aus Bio-Baumwolle..."
                    required
                ></textarea>
            </div>
            
            {/* Preis und Bestand (nebeneinander) */}
            <div style={{ display: 'flex', gap: '20px' }}>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="price">Preis (€):</label>
                    <input
                        type="number"
                        id="price"
                        name="price"
                        value={formData.price}
                        onChange={handleChange}
                        min="0.01"
                        step="0.01"
                        required
                    />
                </div>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label htmlFor="stock">Bestand:</label>
                    <input
                        type="number"
                        id="stock"
                        name="stock"
                        value={formData.stock}
                        onChange={handleChange}
                        min="0"
                        step="1"
                        required
                    />
                </div>
            </div>
            
            {/* NEU: Stoffauswahl */}
            <div className="admin-form-group">
                <label htmlFor="fabricId">Zugehöriger Stoff (optional):</label>
                <select
                    id="fabricId"
                    name="fabricId"
                    value={formData.fabricId}
                    onChange={handleChange}
                    // Stellt sicher, dass das Dropdown auch angezeigt wird, wenn keine Stoffe geladen sind
                    disabled={!fabrics || fabrics.length === 0} 
                >
                    <option value="">-- Kein Stoff zugeordnet --</option>
                    {fabrics && fabrics.map(fabric => (
                        <option key={fabric.id} value={fabric.id}>
                            {fabric.name}
                        </option>
                    ))}
                </select>
                {(!fabrics || fabrics.length === 0) && (
                    <p style={{ color: '#F97316', fontSize: '0.9em', marginTop: '5px' }}>
                        Bitte zuerst Stoffe in der Stoffverwaltung anlegen.
                    </p>
                )}
            </div>

            {/* Bild-Upload und Vorschau */}
            <div className="admin-form-group" style={{ display: 'flex', gap: '20px', alignItems: 'flex-start' }}>
                <div style={{ flex: 1 }}>
                    <label htmlFor="imageFile">Produktbild hochladen:</label>
                    <input
                        type="file"
                        id="imageFile"
                        name="imageFile"
                        onChange={handleFileChange}
                        accept="image/*"
                    />
                    {(isEditing && formData.imageUrl && !formData.imageFile) && (
                        <p style={{ fontSize: '0.8em', color: '#666', marginTop: '5px' }}>
                            Aktuelles Bild wird verwendet. Neues Bild überschreibt es.
                        </p>
                    )}
                </div>
                <div style={{ flex: 0, border: '1px solid #ddd', borderRadius: '8px', padding: '5px' }}>
                    <img 
                        src={previewImage ? getImageUrl(previewImage) : FALLBACK_IMAGE}
                        alt="Vorschau"
                        style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '4px' }}
                        onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                    />
                    {previewImage && (
                        <p style={{ fontSize: '0.7em', textAlign: 'center', margin: '5px 0 0' }}>Vorschau</p>
                    )}
                </div>
            </div>
            
            <div className="admin-form-group" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <input
                    type="checkbox"
                    id="isFeatured"
                    name="isFeatured"
                    checked={formData.isFeatured}
                    onChange={handleChange}
                    style={{ width: '20px', height: '20px' }}
                />
                <label htmlFor="isFeatured" style={{ margin: 0, fontWeight: 'normal', cursor: 'pointer' }}>
                    Als "Featured Product" auf der Startseite anzeigen
                </label>
            </div>
            
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <button type="submit" className="admin-form-submit-button">
                    {isEditing ? 'Änderungen speichern' : 'Produkt erstellen'}
                </button>
            </div>
            
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} className="admin-form-submit-button" style={{backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};

// =========================================================================
// ENDE: EINGEFÜGTER CODE VON ProductForm.js
// =========================================================================


const API_URL = 'http://localhost:3001/api/products';
const FABRICS_API_URL = 'http://localhost:3001/api/fabrics'; 
const FALLBACK_IMAGE = "https://placehold.co/50x50/eee/333?text=Bild";


// FIX: Diese Funktion stellt sicher, dass /uploads/ Pfade die Server-URL erhalten
const getImageUrl = (imagePath) => {
    // Wenn der Pfad mit '/uploads/' beginnt, muss die Basis-URL hinzugefügt werden
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `http://localhost:3001${imagePath}`;
    }
    // Handhabt externe URLs (wie placehold.co) oder defekte Pfade
    return imagePath || FALLBACK_IMAGE; // Fügt Fallback hinzu
};


// --- Komponente: Produktliste ---
const AdminProductList = ({ products, onEdit, onDelete, fabrics }) => {
    
    // NEU: Hilfsfunktion, um den Namen des Stoffs anhand der ID zu finden
    const getFabricName = (fabricId) => {
        const fabric = fabrics.find(f => f.id === fabricId);
        return fabric ? fabric.name : 'Kein Stoff zugeordnet';
    };

    return (
        // Verwendet jetzt CSS-Klassen aus index.css
        <div className="admin-list-container">
            <h2 style={{ fontSize: '1.5em', color: 'var(--farbe-text)', margin: '0 0 20px 0' }}>
                Alle Produkte im System ({products.length})
            </h2>
            {products.length === 0 ? (
                <p style={{ color: '#666' }}>Keine Produkte gefunden. Erstellen Sie das erste Produkt.</p>
            ) : (
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>Bild</th>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Stoff</th> {/* NEU: Spalte für Stoff */}
                            <th>Preis</th>
                            <th>Bestand</th>
                            <th>Aktionen</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map((product) => (
                            <tr key={product.id}>
                                <td>
                                    <img 
                                        src={getImageUrl(product.imageUrl)} 
                                        alt={product.name} 
                                        style={{ width: '50px', height: '50px', objectFit: 'cover', borderRadius: '4px' }}
                                        onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE; }}
                                    />
                                </td>
                                <td style={{ fontSize: '0.8em', color: '#888' }}>{product.id}</td>
                                <td style={{ fontWeight: '600' }}>{product.name}</td>
                                <td style={{ fontSize: '0.9em', color: '#0369A1' }}>{getFabricName(product.fabricId)}</td> {/* NEU: Stoffname anzeigen */}
                                <td>€ {product.price.toFixed(2)}</td>
                                <td>
                                    <span style={{ color: product.stock < 5 ? 'var(--farbe-rot)' : 'var(--farbe-gruen)', fontWeight: 'bold' }}>
                                        {product.stock}
                                    </span>
                                </td>
                                <td>
                                    <button 
                                        className="admin-button admin-button-edit" 
                                        onClick={() => onEdit(product)}
                                    >
                                        Bearbeiten
                                    </button>
                                    <button 
                                        className="admin-button admin-button-delete" 
                                        onClick={() => onDelete(product.id, product.name)}
                                    >
                                        Löschen
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};


// --- NEUE KOMPONENTE: Stoff-Formular ---
const FabricForm = ({ onAdd, onCancel }) => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!name) {
            // alert wird hier verwendet, um den bestehenden Stil der App beizubehalten
            alert("Der Stoffname darf nicht leer sein."); 
            return;
        }
        onAdd({ name, description });
        setName('');
        setDescription('');
    };

    return (
        <form onSubmit={handleSubmit} className="admin-form">
            <h3 className="admin-form-title">Neuen Stoff hinzufügen</h3>
            <div className="admin-form-group">
                <label htmlFor="fabricName">Stoffname:</label>
                <input
                    type="text"
                    id="fabricName"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    placeholder="z.B. Bio-Baumwoll-Jersey"
                    required
                />
            </div>
            <div className="admin-form-group">
                <label htmlFor="fabricDescription">Beschreibung (Optional):</label>
                <textarea
                    id="fabricDescription"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    rows="2"
                    placeholder="z.B. Elastischer, atmungsaktiver Stoff, GOTS-zertifiziert."
                ></textarea>
            </div>
            <button type="submit" className="admin-form-submit-button" style={{ width: 'auto', padding: '10px 20px', marginRight: '10px' }}>
                Stoff speichern
            </button>
        </form>
    );
};

// --- NEUE KOMPONENTE: Stoff-Liste ---
const AdminFabricList = ({ fabrics, onDelete }) => {
    return (
        <div className="admin-list-container" style={{ marginTop: '20px' }}>
            <h2 style={{ fontSize: '1.5em', color: 'var(--farbe-text)', margin: '0 0 20px 0' }}>
                Verfügbare Stoffe ({fabrics.length})
            </h2>
            {fabrics.length === 0 ? (
                <p style={{ color: '#666' }}>Keine Stoffe definiert.</p>
            ) : (
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>Stoffname</th>
                            <th>Beschreibung</th>
                            <th>Aktionen</th>
                        </tr>
                    </thead>
                    <tbody>
                        {fabrics.map((fabric) => (
                            <tr key={fabric.id}>
                                <td style={{ fontWeight: '600' }}>{fabric.name}</td>
                                <td style={{ fontSize: '0.9em', color: '#555' }}>{fabric.description || '-'}</td>
                                <td>
                                    <button 
                                        className="admin-button admin-button-delete" 
                                        onClick={() => onDelete(fabric.id, fabric.name)}
                                        style={{ padding: '5px 10px' }}
                                    >
                                        Löschen
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};


// --- HAUPTKOMPONENTE: AdminDashboard ---
const AdminDashboard = () => {
    const [products, setProducts] = useState([]);
    const [fabrics, setFabrics] = useState([]); // Zustand für Stoffe
    const [currentProduct, setCurrentProduct] = useState(null); // Produkt, das bearbeitet wird (oder null für Neu)
    const [statusMessage, setStatusMessage] = useState({ text: '', type: '' });
    
    // Status-Nachricht anzeigen und verstecken
    const displayStatus = (text, type = 'success') => {
        setStatusMessage({ text, type });
        const timer = setTimeout(() => {
            setStatusMessage({ text: '', type: '' });
        }, 5000); // 5 Sekunden
        return () => clearTimeout(timer); // Cleanup-Funktion (falls Komponente unmounted wird)
    };


    // 1a. Produkte vom Backend abrufen
    const fetchProducts = async () => {
        try {
            const response = await fetch(API_URL);
            if (!response.ok) {
                throw new Error('Netzwerkantwort war nicht erfolgreich');
            }
            const data = await response.json();
            setProducts(data);
        } catch (error) {
            console.error('Fehler beim Abrufen der Produkte:', error);
            displayStatus(`Fehler beim Laden der Produkte: ${error.message}`, 'error');
        }
    };
    
    // 1b. Stoffe vom Backend abrufen
    const fetchFabrics = async () => {
        try {
            const response = await fetch(FABRICS_API_URL);
            if (!response.ok) {
                throw new Error('Netzwerkantwort war nicht erfolgreich');
            }
            const data = await response.json();
            setFabrics(data);
        } catch (error) {
            console.error('Fehler beim Abrufen der Stoffe:', error);
            displayStatus(`Fehler beim Laden der Stoffe: ${error.message}`, 'error');
        }
    };
    
    // Beim ersten Rendern Produkte und Stoffe laden
    useEffect(() => {
        fetchProducts();
        fetchFabrics(); // Lädt auch Stoffe
    }, []);

    // 2. Produkt erstellen/aktualisieren (POST/PUT)
    const handleProductSubmit = async (formData, isEditing) => {
        const method = isEditing ? 'PUT' : 'POST';
        const url = isEditing ? `${API_URL}/${formData.id}` : API_URL;
        
        // Erstellt das FormData-Objekt für den Dateiupload
        const data = new FormData();
        
        // Alle Textfelder anhängen
        for (const key in formData) {
            // imageFile muss separat behandelt werden, imageUrl auch ignorieren, wenn neues File da ist
            if (key !== 'imageFile' && key !== 'imageUrl') {
                // Konvertiert Booleans und Zahlen in Strings. 
                // WICHTIG: formData.fabricId ist jetzt enthalten
                data.append(key, String(formData[key]));
            }
        }

        // Bilddatei anhängen, falls vorhanden
        if (formData.imageFile) {
            data.append('image', formData.imageFile);
        }

        try {
            const response = await fetch(url, {
                method: method,
                // Header NICHT manuell setzen bei FormData
                body: data, 
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP Fehler: ${response.status}`);
            }

            // Produkte neu laden
            await fetchProducts();
            
            // UI zurücksetzen
            setCurrentProduct(null); 
            displayStatus(`Produkt erfolgreich ${isEditing ? 'aktualisiert' : 'erstellt'}!`, 'success');
            
        } catch (error) {
            console.error('Fehler beim Speichern des Produkts:', error);
            displayStatus(`Fehler beim Speichern: ${error.message}`, 'error');
        }
    };


    // 3. Produkt löschen (DELETE) 
    const handleDelete = async (id, name) => {
        // HINWEIS: window.confirm() wird beibehalten, da Sie darum gebeten haben, den bestehenden Code nicht zu ändern.
        if (window.confirm(`Sind Sie sicher, dass Sie das Produkt "${name}" löschen möchten?`)) { 
            try {
                const response = await fetch(`${API_URL}/${id}`, {
                    method: 'DELETE',
                });

                if (!response.ok) {
                    throw new Error(`HTTP Fehler: ${response.status}`);
                }

                // Produkte neu laden
                await fetchProducts();
                displayStatus(`Produkt "${name}" erfolgreich gelöscht.`, 'info');
                
                // Falls das gelöschte Produkt gerade bearbeitet wurde, Formular zurücksetzen
                if (currentProduct && currentProduct.id === id) {
                    setCurrentProduct(null);
                }
                
            } catch (error) {
                console.error('Fehler beim Löschen des Produkts:', error);
                displayStatus(`Fehler beim Löschen: ${error.message}`, 'error');
            }
        }
    };
    
    // 4. Produkt zur Bearbeitung auswählen
    const handleEdit = (product) => {
        setCurrentProduct(product);
        displayStatus(`Produkt: ${product.name} zur Bearbeitung geladen.`, 'info');
        // Optional: Zum Formular scrollen
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };
    
    // Bearbeitungsmodus abbrechen 
    const handleCancelEdit = () => {
        setCurrentProduct(null);
        setStatusMessage({ text: '', type: '' });
    };


    // 5. Stoff hinzufügen
    const handleAddFabric = async (fabricData) => {
        try {
            const response = await fetch(FABRICS_API_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(fabricData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || `HTTP Fehler: ${response.status}`);
            }

            await fetchFabrics(); // Stoffe neu laden
            displayStatus(`Stoff "${fabricData.name}" erfolgreich hinzugefügt!`, 'success');
            
        } catch (error) {
            console.error('Fehler beim Hinzufügen des Stoffs:', error);
            displayStatus(`Fehler beim Speichern des Stoffs: ${error.message}`, 'error');
        }
    };
    
    // 6. Stoff löschen
    const handleDeleteFabric = async (id, name) => {
        // HINWEIS: window.confirm() wird hier ebenfalls verwendet, um den bestehenden Code-Stil beizubehalten.
        if (window.confirm(`Sind Sie sicher, dass Sie den Stoff "${name}" löschen möchten?`)) {
            try {
                const response = await fetch(`${FABRICS_API_URL}/${id}`, {
                    method: 'DELETE',
                });

                if (!response.ok) {
                    // Prüfen, ob der Fehler darauf zurückzuführen ist, dass der Stoff noch verwendet wird
                    const errorData = await response.json();
                    if (response.status === 409) { // Beispiel: Conflict HTTP-Status
                         throw new Error(`Stoff kann nicht gelöscht werden, da er noch in Produkten verwendet wird. (${errorData.message || 'Konflikt'})`);
                    }
                    throw new Error(`HTTP Fehler: ${response.status}`);
                }

                await fetchFabrics(); // Stoffe neu laden
                displayStatus(`Stoff "${name}" erfolgreich gelöscht.`, 'info');
                
            } catch (error) {
                console.error('Fehler beim Löschen des Stoffs:', error);
                displayStatus(`Fehler beim Löschen: ${error.message}`, 'error');
            }
        }
    };


    // Helferfunktion zur Bestimmung der Status-Klasse
    const getStatusClass = () => {
        if (!statusMessage.text) return '';
        if (statusMessage.type === 'error') return 'status-message-error';
        if (statusMessage.type === 'success') return 'status-message-success';
        if (statusMessage.type === 'info') return 'status-message-info';
        return '';
    };

    return (
        // Verwendet jetzt CSS-Klassen aus index.css
        <div className="admin-dashboard-container">
            
            {/* Status-Nachricht mit dynamischer CSS-Klasse */}
            {statusMessage.text && (
                <div className={`status-message ${getStatusClass()}`}>
                    {statusMessage.text}
                </div>
            )}
            
            {/* 1. Bereich: PRODUKT-VERWALTUNG (Bestehendes Grid) */}
            <div className="admin-dashboard-grid">
                
                {/* 1. Spalte: Produktformular */}
                <div>
                    <ProductForm
                        key={currentProduct ? currentProduct.id : 'new'} 
                        productToEdit={currentProduct}
                        onSubmit={handleProductSubmit}
                        onCancel={handleCancelEdit}
                        fabrics={fabrics} // WICHTIG: Stoffliste an ProductForm übergeben
                    />
                </div>

                {/* 2. Spalte: Produktliste */}
                <div>
                    <AdminProductList 
                        products={products}
                        onEdit={handleEdit}
                        onDelete={handleDelete}
                        fabrics={fabrics} // WICHTIG: Stoffliste an AdminProductList übergeben (zur Anzeige)
                    />
                </div>
            </div>
            
            
            {/* 2. Bereich: NEUE STOFF-VERWALTUNG (Unterhalb des Grids) */}
            <div style={{ 
                marginTop: '40px', 
                borderTop: '2px solid var(--farbe-grau)', 
                paddingTop: '30px' 
            }}>
                <h1 style={{ color: 'var(--farbe-primär)', marginBottom: '30px', fontSize: '1.8em' }}>
                    Stoff-Inventar-Verwaltung
                </h1>
                
                {/* Stoff-Formular */}
                <FabricForm onAdd={handleAddFabric} />
                
                {/* Stoff-Liste */}
                <AdminFabricList fabrics={fabrics} onDelete={handleDeleteFabric} />
            </div>
        </div>
    );
};

export default AdminDashboard;
