// client/src/App.js

import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/Home';
import Shop from './components/Shop';
import ProductDetail from './components/ProductDetail';
import AdminDashboard from './components/AdminDashboard';
import Blog from './components/Blog';
import Cart from './components/Cart';


// Basis-Stile für die gesamte Anwendung (Flexbox für Footer am Ende)
const appContainerStyle = {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
    fontFamily: '"Inter", sans-serif', // Die Standard-Schriftart
    backgroundColor: 'var(--farbe-hintergrund)',
};

const mainContentStyle = {
    flexGrow: 1,
    paddingTop: '0px',
    paddingBottom: '50px',
};

// Schlüssel für localStorage
const CART_STORAGE_KEY = 'henkesStoffzauberCart';


const App = () => {
    // State für die aktuelle Route (einfacher Routing-Mechanismus)
    const [route, setRoute] = useState(window.location.pathname);

    // 1. Initialer State: Lade Warenkorb aus localStorage, ansonsten leeres Array.
    const [cart, setCart] = useState(() => {
        try {
            const savedCart = localStorage.getItem(CART_STORAGE_KEY);
            return savedCart ? JSON.parse(savedCart) : [];
        } catch (error) {
            console.error("Fehler beim Laden des Warenkorbs aus localStorage:", error);
            return []; // Fallback bei Fehler
        }
    }); 


    // 2. useEffect zum Speichern des Warenkorbs (Persistenz)
    useEffect(() => {
        try {
            localStorage.setItem(CART_STORAGE_KEY, JSON.stringify(cart));
        } catch (error) {
            console.error("Fehler beim Speichern des Warenkorbs in localStorage:", error);
        }
    }, [cart]); // Wird jedes Mal ausgeführt, wenn sich der cart-State ändert


    // Funktion zur Navigation, wird an den Header übergeben
    const navigate = (path) => {
        window.history.pushState({}, '', path);
        setRoute(path);
    };

    // Funktion zum Hinzufügen eines Produkts zum Warenkorb
    const addToCart = (product, quantity = 1) => {
        const safeQuantity = Math.max(1, quantity);

        setCart(prevCart => {
            const existingItemIndex = prevCart.findIndex(item => item.id === product.id);

            if (existingItemIndex > -1) {
                const updatedCart = [...prevCart];
                updatedCart[existingItemIndex].quantity += safeQuantity;
                return updatedCart;
            } else {
                return [
                    ...prevCart,
                    { 
                        id: product.id,
                        name: product.name,
                        price: product.price,
                        imageUrl: product.imageUrl,
                        quantity: safeQuantity 
                    }
                ];
            }
        });

        console.log(`Produkt ${product.name} x${safeQuantity} zum Warenkorb hinzugefügt.`);
        navigate('/cart');
    };

    // Berechnung der Gesamtzahl der Artikel im Warenkorb (für den Header-Zähler)
    const cartItemCount = cart.reduce((total, item) => total + item.quantity, 0);

    // Event-Listener für Browser-Zurück-/Vor-Buttons
    useEffect(() => {
        const handlePopState = () => setRoute(window.location.pathname);
        window.addEventListener('popstate', handlePopState);
        return () => window.removeEventListener('popstate', handlePopState);
    }, []);

    // Einfacher Router: Rendert die Komponente basierend auf der aktuellen Route
    const renderContent = () => {
        const path = route.split('/');

        // Route: /
        if (path[1] === '' || path[1] === 'home') {
            return <Home navigate={navigate} />;
        }
        // Route: /shop
        if (path[1] === 'shop') {
            return <Shop navigate={navigate} />;
        }
        // Route: /product/:id
        if (path[1] === 'product' && path[2]) {
            return <ProductDetail productId={path[2]} addToCart={addToCart} />; 
        }
        // Route: /admin
        if (path[1] === 'admin') {
            return <AdminDashboard />;
        }
        // Route: /blog
        if (path[1] === 'blog') {
            return <Blog />;
        }
        // Route: /cart
        if (path[1] === 'cart') {
            return <Cart cart={cart} setCart={setCart} />; 
        }

        // Standard-Fallback für 404
        return (
            <div style={{ textAlign: 'center', padding: '100px', fontSize: '1.2em' }}>
                <h1>404 - Seite nicht gefunden</h1>
                <p>Die angeforderte Seite existiert nicht.</p>
                <a 
                    href="/" 
                    onClick={(e) => { e.preventDefault(); navigate('/'); }} 
                    style={{ color: 'var(--farbe-akzent)', textDecoration: 'underline' }}
                >
                    Zur Startseite
                </a>
            </div>
        );
    };

    return (
        <div style={appContainerStyle}>
            {/* Header erhält den Zähler als Prop */}
            <Header navigate={navigate} cartItemCount={cartItemCount} />

            <main style={mainContentStyle}>
                {renderContent()}
            </main>

            <Footer />
        </div>
    );
};

export default App;
