import React, { useState, useEffect, useRef } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', email: '', phone: '',
    street: '', houseNumber: '', city: '', zip: '',
    paymentMethod: 'paypal'
  });

  const [paypalClientId, setPaypalClientId] = useState(null);
  const [loadingPaypal, setLoadingPaypal] = useState(false);
  const [invoiceLink, setInvoiceLink] = useState('');
  const paypalRef = useRef(null);

  const totalPrice = cartItems.reduce(
    (sum, item) => sum + Number(item.price || 0) * item.quantity,
    0
  );

  // IDs vom Server laden
  useEffect(() => {
    fetch('http://localhost:3001/api/config')
      .then(res => res.json())
      .then(data => setPaypalClientId(data.paypalClientId || null))
      .catch(err => {
        console.warn("PayPal-Konfiguration konnte nicht geladen werden:", err);
        setPaypalClientId(null);
      });
  }, []);

  // PayPal-SDK einbinden
  useEffect(() => {
    if (formData.paymentMethod !== 'paypal') return;
    if (!paypalClientId) return;

    const existing = document.getElementById('paypal-sdk');
    if (existing) existing.remove();

    setLoadingPaypal(true);
    const script = document.createElement('script');
    script.id = 'paypal-sdk';
    script.src = `https://www.paypal.com/sdk/js?client-id=${paypalClientId}&currency=EUR`;
    script.async = true;

    script.onload = () => {
      setLoadingPaypal(false);

      if (!window.paypal) return;

      window.paypal.Buttons({
        style: { layout: 'vertical', color: 'gold', shape: 'pill', label: 'pay' },

        createOrder: async () => {
          const res = await fetch('http://localhost:3001/api/checkout', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              cart: cartItems,
              address: formData,
              paymentMethod: 'paypal'
            })
          });

          const data = await res.json();
          return data.paypalOrderId; // vom Server
        },

        onApprove: async (_, actions) => {
          try {
            await actions.order.capture();
            alert(`Vielen Dank für Ihre Bestellung, ${formData.firstName}!`);
            clearCart();
          } catch (err) {
            console.error("Capture Error:", err);
            alert("Zahlung erfolgreich, aber Fehler beim Abschluss.");
          }
        },

        onError: (err) => {
          console.error("PayPal Fehler:", err);
          alert("PayPal-Zahlung fehlgeschlagen.");
        }

      }).render(paypalRef.current);
    };

    script.onerror = () => {
      setLoadingPaypal(false);
      console.error("PayPal SDK konnte nicht geladen werden");
    };

    document.body.appendChild(script);

    return () => {
      const el = document.getElementById('paypal-sdk');
      if (el) el.remove();
      if (paypalRef.current) paypalRef.current.innerHTML = '';
    };
  }, [paypalClientId, formData.paymentMethod, cartItems, formData]);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.paymentMethod === 'paypal') return;

    const res = await fetch('http://localhost:3001/api/checkout', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        cart: cartItems,
        address: formData,
        paymentMethod: formData.paymentMethod
      })
    });

    const data = await res.json();
    if (data.success) {
      if (data.invoicePath) {
        setInvoiceLink(`http://localhost:3001/${data.invoicePath}`);
      }
      alert("Bestellung erfolgreich!");
      clearCart();
    } else {
      alert("Fehler bei der Bestellung.");
    }
  };

  return (
    <div className="checkout-container">
      <h2>Zur Kasse</h2>

      {cartItems.length === 0 ? (
        <p>Dein Warenkorb ist leer.</p>
      ) : (
        <>
          <p><strong>Gesamt: € {totalPrice.toFixed(2)}</strong></p>

          <form className="checkout-form" onSubmit={handleSubmit}>
            <label>Vorname</label>
            <input name="firstName" value={formData.firstName} onChange={handleChange} required />

            <label>Nachname</label>
            <input name="lastName" value={formData.lastName} onChange={handleChange} required />

            <label>E-Mail</label>
            <input name="email" value={formData.email} onChange={handleChange} required />

            <label>Telefon</label>
            <input name="phone" value={formData.phone} onChange={handleChange} required />

            <label>Straße</label>
            <input name="street" value={formData.street} onChange={handleChange} required />

            <label>Hausnummer</label>
            <input name="houseNumber" value={formData.houseNumber} onChange={handleChange} required />

            <label>Stadt</label>
            <input name="city" value={formData.city} onChange={handleChange} required />

            <label>PLZ</label>
            <input name="zip" value={formData.zip} onChange={handleChange} required />

            <label>Zahlungsmethode</label>
            <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange}>
              <option value="paypal">PayPal</option>
              <option value="bank">Überweisung</option>
              <option value="cash">Barzahlung</option>
            </select>

            {formData.paymentMethod !== 'paypal' && (
              <button type="submit" className="checkout-submit">Jetzt bestellen</button>
            )}
          </form>

          {formData.paymentMethod === 'paypal' && (
            <>
              {!paypalClientId ? (
                <p style={{ color: 'red' }}>
                  PayPal nicht verfügbar – keine Client-ID gefunden.
                </p>
              ) : loadingPaypal ? (
                <p>PayPal wird geladen…</p>
              ) : (
                <div ref={paypalRef} className="paypal-button-container" />
              )}
            </>
          )}

          {invoiceLink && (
            <a className="checkout-download" href={invoiceLink} download>
              PDF-Rechnung herunterladen
            </a>
          )}
        </>
      )}
    </div>
  );
};

export default Checkout;
