// client/src/components/Shop.js
import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';
import './Shop.css';
const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getAuthHeaders = () => {
  const token = localStorage.getItem('adminToken');
  return token ? { Authorization: `Bearer ${token}` } : {};
};

const getImageUrl = (imagePath) => {
  if (imagePath && imagePath.startsWith('/uploads/')) return `${BACKEND_URL}${imagePath}`;
  return imagePath || FALLBACK_IMAGE_URL;
};

// ------------------- ProductCard -------------------
const ProductCard = ({ product, addToCart }) => {
  const isInStock = product.stock > 0;

  return (
    <div className="product-card">
      <img
        src={getImageUrl(product.imageUrl)}
        alt={product.name}
        onError={e => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
        className="carousel-image"
      />
      <div style={{ padding: '15px' }}>
        <h3>{product.name}</h3>
        <p style={{ fontWeight: 'bold', color: 'var(--farbe-akzent)' }}>€ {product.price.toFixed(2)}</p>
        <p style={{ color: isInStock ? 'green' : 'red', fontWeight: 'bold' }}>
          {isInStock ? 'Sofort verfügbar' : 'Ausverkauft'}
        </p>
        <button
          onClick={() => isInStock && addToCart(product, 1)}
          disabled={!isInStock}
          style={{
            backgroundColor: isInStock ? 'var(--farbe-primär)' : '#aaa',
            color: 'var(--farbe-hell)',
            borderRadius: '8px',
            padding: '10px 15px',
            fontWeight: 'bold',
            cursor: isInStock ? 'pointer' : 'not-allowed',
            transition: 'transform 0.2s'
          }}
        >
          {isInStock ? 'In den Warenkorb (1)' : 'Nicht lieferbar'}
        </button>
      </div>
    </div>
  );
};

// ------------------- Shop -------------------
const Shop = () => {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');

  const { cart, addToCart, removeFromCart, clearCart } = useCart();

  // Produkte laden
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch(API_URL, { headers: getAuthHeaders() });
        if (!res.ok) throw new Error(`Netzwerkfehler: ${res.status}`);
        const data = await res.json();
        setProducts(data);
        setFiltered(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // Suche
  useEffect(() => {
    const lower = search.toLowerCase();
    setFiltered(products.filter(p => p.name.toLowerCase().includes(lower)));
  }, [search, products]);

  if (loading) return <p style={{ textAlign: 'center', padding: '50px' }}>Shop wird geladen...</p>;
  if (error) return <p style={{ textAlign: 'center', color: 'red', padding: '50px', fontWeight: 'bold' }}>Fehler: {error}</p>;

  // Warenkorb-Summe
  const total = cart.reduce((sum, item) => sum + item.quantity * item.price, 0);

  return (
    <div style={{ maxWidth: '1200px', margin: '50px auto', padding: '0 20px' }}>
      <h1 style={{ textAlign: 'center', color: 'var(--farbe-primär)', marginBottom: '40px' }}>Schau dich um</h1>

      {/* Suche */}
      <input
        type="text"
        placeholder="Suche ..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="search-input"
        style={{ marginBottom: '30px', width: '100%', padding: '12px', fontSize: '16px', borderRadius: '8px', border: '1px solid #ccc' }}
      />

      {filtered.length === 0 ? (
        <p style={{ textAlign: 'center', color: '#666', padding: '50px' }}>Kein Produkt gefunden.</p>
      ) : (
        <div className="product-grid">
          {filtered.map(p => <ProductCard key={p.id} product={p} addToCart={addToCart} />)}
        </div>
      )}

      {/* Warenkorb */}
      <div style={{ marginTop: '50px', padding: '20px', border: '1px solid #ccc', borderRadius: '10px' }}>
        <h2>Warenkorb</h2>
        {cart.length === 0 ? (
          <p>Keine Produkte im Warenkorb.</p>
        ) : (
          <>
            {cart.map(item => (
              <div key={item.id} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
                <span>{item.name} x {item.quantity}</span>
                <span>
                  € {(item.price * item.quantity).toFixed(2)}
                  <button onClick={() => removeFromCart(item.id)} style={{ marginLeft: '10px', color: 'red' }}>❌</button>
                </span>
              </div>
            ))}
            <hr />
            <p style={{ fontWeight: 'bold' }}>Summe: € {total.toFixed(2)}</p>
            <button onClick={clearCart} style={{ marginTop: '10px', padding: '10px 15px', borderRadius: '8px', background: 'var(--farbe-primär)', color: 'var(--farbe-hell)', fontWeight: 'bold' }}>Warenkorb leeren</button>
            <button onClick={() => alert('Checkout-Funktion noch nicht implementiert')} style={{ marginTop: '10px', marginLeft: '10px', padding: '10px 15px', borderRadius: '8px', background: 'green', color: 'white', fontWeight: 'bold' }}>Zur Kasse</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Shop;
