// server/server.js
const express = require('express');
const cors = require('cors');
const multer = require('multer');
const path = require('path');
const fs = require('fs');

const app = express();
const PORT = 3001;

// Middleware
app.use(cors());
app.use(express.json());
app.use('/uploads', express.static(path.join(__dirname, 'uploads')));

// --- JSON-Dateien ---
const productsPath = path.join(__dirname, 'products.json');
const fabricsPath = path.join(__dirname, 'fabrics.json');

// --- Hilfsfunktionen zum Laden/Speichern ---
function loadProducts() {
  try {
    return JSON.parse(fs.readFileSync(productsPath));
  } catch (err) {
    console.log('products.json konnte nicht geladen werden, leeres Array verwendet');
    return [];
  }
}

function loadFabrics() {
  try {
    return JSON.parse(fs.readFileSync(fabricsPath));
  } catch (err) {
    console.log('fabrics.json konnte nicht geladen werden, leeres Array verwendet');
    return [];
  }
}

function saveProducts(products) {
  fs.writeFileSync(productsPath, JSON.stringify(products, null, 2));
}

function saveFabrics(fabrics) {
  fs.writeFileSync(fabricsPath, JSON.stringify(fabrics, null, 2));
}

// --- Multer Setup für Bilder ---
const storage = multer.diskStorage({
  destination: (req, file, cb) => {
    const uploadDir = path.join(__dirname, 'uploads');
    if (!fs.existsSync(uploadDir)) fs.mkdirSync(uploadDir);
    cb(null, uploadDir);
  },
  filename: (req, file, cb) => {
    cb(null, Date.now() + '-' + file.originalname);
  },
});
const upload = multer({ storage });

// --- Admin Auth Middleware ---
const ADMIN_PASSWORD = 'stoffzauber2025';
const ADMIN_TOKEN = 'stoffzauber2025'; // Einfacher Token für Demo

function checkAdmin(req, res, next) {
  const auth = req.headers.authorization;
  if (!auth || auth !== `Bearer ${ADMIN_TOKEN}`) {
    return res.status(401).json({ error: 'Unauthorized' });
  }
  next();
}

// --- Admin Login Endpoint ---
app.post('/api/admin/login', (req, res) => {
  const { password } = req.body;
  if (password === ADMIN_PASSWORD) {
    return res.json({ token: ADMIN_TOKEN });
  } else {
    return res.status(401).json({ error: 'Falsches Passwort' });
  }
});

// --- Produkte ---
// Featured-Produkte
app.get('/api/products/featured', (req, res) => {
  const products = loadProducts();
  const featured = products.filter(p => p.isFeatured);
  res.json(featured);
});

// Alle Produkte
app.get('/api/products', (req, res) => {
  const products = loadProducts();
  res.json(products);
});

// Einzelnes Produkt
app.get('/api/products/:id', (req, res) => {
  const products = loadProducts();
  const product = products.find(p => p.id.toString() === req.params.id.toString());
  if (!product) return res.status(404).json({ error: 'Produkt nicht gefunden' });
  res.json(product);
});

// Produkt erstellen
app.post('/api/products', checkAdmin, upload.single('imageFile'), (req, res) => {
  const products = loadProducts();
  const { name, description, price, stock, isFeatured, fabrics, availableHeight, availableWidth } = req.body;
  const id = Date.now().toString();
  const imageUrl = req.file ? `/uploads/${req.file.filename}` : '';
  const product = {
    id,
    name,
    description,
    price: parseFloat(price),
    stock: parseInt(stock),
    isFeatured: isFeatured === 'true' || isFeatured === true,
    fabrics: fabrics || '',
    availableHeight: availableHeight ? parseFloat(availableHeight) : 0,
    availableWidth: availableWidth ? parseFloat(availableWidth) : 0,
    imageUrl,
  };
  products.push(product);
  saveProducts(products);
  res.json(product);
});

// Produkt bearbeiten
app.put('/api/products/:id', checkAdmin, upload.single('imageFile'), (req, res) => {
  const products = loadProducts();
  const product = products.find(p => p.id.toString() === req.params.id.toString());
  if (!product) return res.status(404).json({ error: 'Produkt nicht gefunden' });

  const { name, description, price, stock, isFeatured, fabrics, availableHeight, availableWidth } = req.body;
  if (name) product.name = name;
  if (description) product.description = description;
  if (price) product.price = parseFloat(price);
  if (stock) product.stock = parseInt(stock);
  if (isFeatured !== undefined) product.isFeatured = isFeatured === 'true' || isFeatured === true;
  if (fabrics !== undefined) product.fabrics = fabrics;
  if (availableHeight) product.availableHeight = parseFloat(availableHeight);
  if (availableWidth) product.availableWidth = parseFloat(availableWidth);
  if (req.file) product.imageUrl = `/uploads/${req.file.filename}`;

  saveProducts(products);
  res.json(product);
});

// Produkt löschen
app.delete('/api/products/:id', checkAdmin, (req, res) => {
  let products = loadProducts();
  products = products.filter(p => p.id.toString() !== req.params.id.toString());
  saveProducts(products);
  res.json({ message: 'Produkt gelöscht' });
