import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext'; // Angepasster Pfad

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

/**
 * HILFSFUNKTION: Korrigiert Bildpfade, die vom Backend kommen.
 */
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    return imagePath;
};

// --- Produktkarte (Einzelkomponente) ---
const ProductCard = ({ product }) => {
    const { addToCart } = useCart();
    const isInStock = product.stock > 0;

    const handleAddToCart = (e) => {
        e.preventDefault(); // Verhindert Navigation beim Klick
        if (isInStock) {
            addToCart(product, 1);
        }
    };

    const cardStyles = {
        backgroundColor: 'var(--farbe-hell)',
        borderRadius: '12px',
        boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
        overflow: 'hidden',
        transition: 'transform 0.3s ease, box-shadow 0.3s ease',
        display: 'flex',
        flexDirection: 'column',
    };

    const imageStyles = {
        width: '100%',
        height: '250px',
        objectFit: 'cover',
    };

    const contentStyles = {
        padding: '15px',
        flexGrow: 1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
    };

    const buttonStyles = {
        padding: '10px 15px',
        backgroundColor: isInStock ? 'var(--farbe-primär)' : '#aaa',
        color: 'white',
        border: 'none',
        borderRadius: '8px',
        cursor: isInStock ? 'pointer' : 'not-allowed',
        fontWeight: 'bold',
        marginTop: '10px',
        transition: 'background-color 0.3s ease',
    };
    
    const stockStyles = {
        fontSize: '0.9em',
        color: isInStock ? '#4caf50' : '#f44336',
        fontWeight: 'bold',
        marginBottom: '10px',
    };

    return (
        <Link 
            to={`/shop/${product.id}`} 
            style={{ textDecoration: 'none', color: 'inherit' }}
            onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-5px)'}
            onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
        >
            <div style={cardStyles}>
                <img 
                    src={getImageUrl(product.imageUrl)} 
                    alt={product.name} 
                    style={imageStyles} 
                    onError={(e) => { e.target.onerror = null; e.target.src = FALLBACK_IMAGE_URL; }}
                />
                <div style={contentStyles}>
                    <div>
                        <h3 style={{ margin: '0 0 5px 0', color: 'var(--farbe-text)', fontSize: '1.2em' }}>{product.name}</h3>
                        <p style={{ margin: '0 0 10px 0', color: 'var(--farbe-akzent)', fontWeight: 'bold', fontSize: '1.3em' }}>
                            € {product.price.toFixed(2)}
                        </p>
                        <p style={stockStyles}>
                            {isInStock ? 'Sofort verfügbar' : 'Ausverkauft'}
                        </p>
                    </div>
                    <button 
                        style={buttonStyles} 
                        onClick={handleAddToCart} 
                        disabled={!isInStock}
                        onMouseEnter={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-sekundär)' : '#aaa'} 
                        onMouseLeave={(e) => e.target.style.backgroundColor = isInStock ? 'var(--farbe-primär)' : '#aaa'}
                    >
                        {isInStock ? 'In den Warenkorb (1)' : 'Nicht lieferbar'}
                    </button>
                </div>
            </div>
        </Link>
    );
};


// --- Shop Hauptkomponente ---
const Shop = () => {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch(API_URL);
                if (!response.ok) {
                    throw new Error(`Netzwerkfehler: ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (e) {
                console.error("Fehler beim Abrufen der Produkte:", e);
                setError(e.message);
            } finally {
                setIsLoading(false);
            }
        };
        
        fetchProducts();
    }, []);

    const shopContainerStyle = {
        maxWidth: '1200px',
        margin: '50px auto',
        padding: '0 20px',
    };

    const gridStyle = {
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
        gap: '30px',
        marginTop: '30px',
    };

    if (isLoading) {
        return <p style={{ textAlign: 'center', padding: '50px' }}>Shop wird geladen...</p>;
    }
    if (error) {
        return <p style={{ textAlign: 'center', color: '#f44336', padding: '50px', fontWeight: 'bold' }}>Fehler: {error}</p>;
    }
    
    return (
        <div style={shopContainerStyle}>
            <h1 style={{ color: 'var(--farbe-primär)', textAlign: 'center', marginBottom: '40px' }}>
                Alle Stoffe & Zubehör
            </h1>
            
            {products.length === 0 ? (
                <p style={{ textAlign: 'center', color: '#666', padding: '50px' }}>
                    Der Shop ist momentan leer. Bitte fügen Sie Produkte über das Admin Dashboard hinzu.
                </p>
            ) : (
                <div style={gridStyle}>
                    {products.map(product => (
                        <ProductCard key={product.id} product={product} />
                    ))}
                </div>
            )}
        </div>
    );
};

export default Shop;
