// src/components/Shop.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from './CartContext';
import './Shop.css';

const API_URL = 'http://localhost:3001/api/products';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

const getImageUrl = (imagePath) => imagePath ? `${BACKEND_URL}${imagePath}` : FALLBACK_IMAGE_URL;

const ProductCard = ({ product, onAdd }) => {
  const isInStock = product.stock > 0;
  return (
    <div className="product-card">
      <Link to={`/shop/${product.id}`}>
        <img src={getImageUrl(product.imageUrl)} alt={product.name} />
      </Link>
      <h3>{product.name}</h3>
      <p>‚Ç¨ {product.price.toFixed(2)}</p>
      <p className={isInStock ? 'instock' : 'outstock'}>
        {isInStock ? 'Sofort verf√ºgbar' : 'Ausverkauft'}
      </p>
      <button onClick={() => onAdd(product)} disabled={!isInStock}>
        {isInStock ? 'In den Warenkorb' : 'Nicht lieferbar'}
      </button>
    </div>
  );
};

const Shop = () => {
  const { cartItems, addToCart, removeFromCart } = useCart();
  const [products, setProducts] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="shop-container">
      <h1>Shop</h1>
      <div className="product-grid">
        {products.map(p => (
          <ProductCard key={p.id} product={p} onAdd={addToCart} />
        ))}
      </div>

      {/* Floating Cart Button */}
      <button className="open-cart-button" onClick={() => setShowCart(prev => !prev)}>
        üõí {cartItems.reduce((sum, item) => sum + item.quantity, 0)}
      </button>

      {/* Cart Panel */}
      {showCart && (
        <div className="sticky-cart">
          <h3>üõí Dein Warenkorb</h3>
          {cartItems.length === 0 ? <p>Dein Warenkorb ist leer</p> :
            <ul>
              {cartItems.map(item => (
                <li key={item.id}>
                  {item.name} x {item.quantity}
                  <button onClick={() => removeFromCart(item.id)}>‚ùå</button>
                </li>
              ))}
            </ul>
          }
          <button onClick={() => setShowCart(false)}>Schlie√üen</button>
        </div>
      )}
    </div>
  );
};

export default Shop;
