import React from 'react'; 
import { useNavigate } from 'react-router-dom';
import './Home.css'; // Neue CSS-Datei importieren

import logoAsset from '../assets/logo.jpg';
import ImageCarousel from './ImageCarousel';

const Home = () => {
    const navigate = useNavigate(); 

    const handleShopClick = (e) => {
        e.preventDefault();
        navigate('/shop'); 
    };

    return (
        <div className="home-container">
            {/* 1. Intro-Bereich */}
            <div className="home-intro">
                <img 
                    src={logoAsset} 
                    alt="Henkes Stoffzauber Logo" 
                    className="home-logo"
                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/350x150/6f42c1/ffffff?text=LOGO+FEHLT'; }}
                />
                
                <h1 className="home-title">Willkommen bei Henkes Stoffzauber</h1>
                <p className="home-text">
                    Entdecken Sie unsere handverlesene Auswahl an hochwertigen Stoffen und kreativen Näh-Ideen. 
                    Hier finden Sie alles, was Ihr Nähherz begehrt – von Jersey bis French Terry. Lassen Sie sich inspirieren 
                    und beginnen Sie Ihr nächstes Nähprojekt noch heute!
                </p>
                <button 
                    className="home-button"
                    onClick={handleShopClick}
                >
                    Zum Shop
                </button>
            </div>
            
            {/* 2. Featured Produkte Karussell */}
            <h2 className="home-subtitle">⭐ Unsere Highlights der Woche ⭐</h2>
            <ImageCarousel /> 
            
            {/* 3. Text & Info */}
            <div className="home-info">
                <h2 className="home-info-title">Qualität, die man fühlt</h2>
                <p className="home-text">
                    Jeder Stoff in unserem Sortiment wird sorgfältig ausgewählt, um höchste Ansprüche an Haptik,
                    Haltbarkeit und Farbtreue zu erfüllen. Bei uns kaufen Sie nicht nur Material, sondern die Grundlage
                    für Ihre Meisterwerke. Besuchen Sie auch unseren Blog für spannende Anleitungen!
                </p>
            </div>
        </div>
    );
};

export default Home;
