// client/src/components/AdminDashboard.js
import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { LogOut, Edit, Trash2, PlusCircle, CheckCircle, XCircle } from 'lucide-react';

const API_URL = 'http://localhost:3001/api';
const BACKEND_URL = 'http://localhost:3001';
const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=N/A";
const FABRIC_FALLBACK_IMAGE_URL = "https://placehold.co/100x100/D9E3F0/333?text=Stoff";

// --- HILFSFUNKTIONEN ---

/**
 * Ruft den Admin-Authentifizierungstoken aus dem localStorage ab.
 */
const getAuthHeaders = () => {
    const adminToken = localStorage.getItem('adminToken');
    if (!adminToken) {
        return { error: 'Admin-Sitzung abgelaufen oder nicht gefunden.' };
    }
    return { 
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${adminToken}`,
    };
};

/**
 * Korrigiert Bildpfade, die vom Backend kommen.
 */
const getImageUrl = (imagePath) => {
    if (imagePath && imagePath.startsWith('/uploads/')) {
        return `${BACKEND_URL}${imagePath}`;
    }
    return imagePath || FALLBACK_IMAGE_URL;
};

// =========================================================================
// KOMPONENTE 1: Produktformular (ProductForm)
// =========================================================================

const getInitialProductState = (productToEdit) => productToEdit || {
    name: '',
    description: '',
    price: 0,
    stock: 0,
    isFeatured: false,
    imageFile: null,
    imageUrl: '',
    fabricId: '', // Verknüpfung zum Stoff
};

const ProductForm = ({ productToEdit, onSubmit, onCancel, fabrics }) => {
    const initialState = useMemo(() => getInitialProductState(productToEdit), [productToEdit]);
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!productToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);

    useEffect(() => {
        setFormData(initialState);
        setIsEditing(!!productToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, productToEdit]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (name === 'imageFile') {
            const file = e.target.files[0];
            setFormData(prev => ({ ...prev, imageFile: file }));
            setPreviewImage(file ? URL.createObjectURL(file) : (formData.imageUrl || null));
        } else if (type === 'checkbox') {
            setFormData(prev => ({ ...prev, [name]: checked }));
        } else if (type === 'number') {
            setFormData(prev => ({ ...prev, [name]: parseFloat(value) || 0 }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <form onSubmit={handleSubmit} className="admin-form-container" style={{ padding: '20px', backgroundColor: 'var(--farbe-hell)', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
            <h2 style={{ color: 'var(--farbe-akzent)', marginBottom: '20px', fontSize: '1.5em' }}>
                {isEditing ? 'Produkt bearbeiten' : 'Neues Produkt erstellen'}
            </h2>

            <div className="admin-form-group">
                <label>Name des Produkts:</label>
                <input type="text" name="name" value={formData.name} onChange={handleChange} required style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }} />
            </div>

            <div className="admin-form-group">
                <label>Beschreibung:</label>
                <textarea name="description" value={formData.description} onChange={handleChange} rows="3" required style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }} />
            </div>

            <div style={{ display: 'flex', gap: '15px' }}>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label>Preis (€):</label>
                    <input type="number" name="price" value={formData.price} onChange={handleChange} min="0" step="0.01" required style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }} />
                </div>
                <div className="admin-form-group" style={{ flex: 1 }}>
                    <label>Bestand:</label>
                    <input type="number" name="stock" value={formData.stock} onChange={handleChange} min="0" required style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }} />
                </div>
            </div>

            {/* STOFF-AUSWAHL */}
            <div className="admin-form-group">
                <label>Zugehöriger Stoff:</label>
                <select name="fabricId" value={formData.fabricId || ''} onChange={handleChange} style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}>
                    <option value="">-- Keinen Stoff auswählen --</option>
                    {fabrics && fabrics.map(f => (
                        <option key={f.id} value={f.id}>{f.name} ({f.fabricType})</option>
                    ))}
                </select>
            </div>

            <div className="admin-form-group">
                <label>Bild:</label>
                <input type="file" name="imageFile" accept="image/*" onChange={handleChange} />
                {previewImage && <img src={previewImage} alt="Vorschau" style={{ marginTop: '10px', maxWidth: '100px', borderRadius: '5px' }} />}
            </div>

            <div className="admin-form-group" style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                <input type="checkbox" name="isFeatured" checked={formData.isFeatured} onChange={handleChange} />
                <label>Als "Featured" auf Startseite anzeigen</label>
            </div>

            <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
                <button type="submit" style={{ padding: '10px 20px', backgroundColor: 'var(--farbe-akzent)', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
                    {isEditing ? 'Speichern' : 'Erstellen'}
                </button>
                {isEditing && (
                    <button type="button" onClick={onCancel} style={{ padding: '10px 20px', backgroundColor: '#ccc', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
                        Abbrechen
                    </button>
                )}
            </div>
        </form>
    );
};


// =========================================================================
// KOMPONENTE 2: Stoff-Formular (FabricForm) - NEU
// =========================================================================

const FABRIC_TYPES = ['Jersey', 'French Terry', 'Sweat', 'Bündchen', 'Musselin', 'Baumwolle', 'Cord', 'Fleece', 'Softshell', 'Leder'];
const USAGE_OPTIONS = ['Mütze', 'Hose', 'Oberteil', 'Tasche', 'Deko', 'Accessoire'];

const getInitialFabricState = () => ({
    name: '',
    description: '',
    fabricType: '',
    suitableFor: [],
    stock: 0,
    imageFile: null
});

const FabricForm = ({ onAdd }) => {
    const [formData, setFormData] = useState(getInitialFabricState());
    const [previewImage, setPreviewImage] = useState(null);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (name === 'suitableFor') {
            setFormData(prev => ({
                ...prev,
                suitableFor: checked 
                    ? [...prev.suitableFor, value] 
                    : prev.suitableFor.filter(v => v !== value)
            }));
        } else if (name === 'imageFile') {
            const file = e.target.files[0];
            setFormData(prev => ({ ...prev, imageFile: file }));
            setPreviewImage(file ? URL.createObjectURL(file) : null);
        } else {
            setFormData(prev => ({ 
                ...prev, 
                [name]: type === 'number' ? parseFloat(value) : value 
            }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        
        const data = new FormData();
        data.append('name', formData.name);
        data.append('description', formData.description);
        data.append('fabricType', formData.fabricType);
        data.append('stock', formData.stock);
        data.append('suitableFor', JSON.stringify(formData.suitableFor));
        if (formData.imageFile) data.append('imageFile', formData.imageFile);

        onAdd(data);
        
        // Reset
        setFormData(getInitialFabricState());
        setPreviewImage(null);
        // File input manuell resetten
        e.target.reset(); 
    };

    return (
        <form onSubmit={handleSubmit} className="admin-form-container" style={{ padding: '20px', backgroundColor: 'var(--farbe-hell)', borderRadius: '10px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)', marginBottom: '30px' }}>
            <h3 style={{ color: 'var(--farbe-primär-dunkel)', marginBottom: '15px' }}>Neuen Stoff hinzufügen</h3>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
                <div className="admin-form-group">
                    <label>Stoffname:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }} />
                </div>
                <div className="admin-form-group">
                    <label>Stoffart:</label>
                    <select name="fabricType" value={formData.fabricType} onChange={handleChange} required style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }}>
                        <option value="">-- Wählen --</option>
                        {FABRIC_TYPES.map(type => <option key={type} value={type}>{type}</option>)}
                    </select>
                </div>
            </div>

            <div className="admin-form-group">
                <label>Beschreibung:</label>
                <textarea name="description" value={formData.description} onChange={handleChange} rows="2" style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }} />
            </div>

            <div className="admin-form-group">
                <label>Bestand (Meter):</label>
                <input type="number" name="stock" value={formData.stock} onChange={handleChange} min="0" step="0.1" required style={{ width: '100%', padding: '8px', border: '1px solid #ccc', borderRadius: '4px' }} />
            </div>

            <div className="admin-form-group">
                <label>Geeignet für:</label>
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                    {USAGE_OPTIONS.map(usage => (
                        <label key={usage} style={{ display: 'flex', alignItems: 'center', gap: '5px', fontSize: '0.9em' }}>
                            <input type="checkbox" name="suitableFor" value={usage} checked={formData.suitableFor.includes(usage)} onChange={handleChange} />
                            {usage}
                        </label>
                    ))}
                </div>
            </div>

            <div className="admin-form-group">
                <label>Stoff-Bild:</label>
                <input type="file" name="imageFile" accept="image/*" onChange={handleChange} />
                {previewImage && <img src={previewImage} alt="Vorschau" style={{ marginTop: '10px', maxWidth: '80px', borderRadius: '5px' }} />}
            </div>

            <button type="submit" style={{ marginTop: '15px', padding: '10px 20px', backgroundColor: 'var(--farbe-primär)', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
                Stoff speichern
            </button>
        </form>
    );
};


// =========================================================================
// HAUPTKOMPONENTE: AdminDashboard
// =========================================================================

const AdminDashboard = ({ onLogout }) => {
    const navigate = useNavigate();
    const [products, setProducts] = useState([]);
    const [fabrics, setFabrics] = useState([]);
    const [currentProduct, setCurrentProduct] = useState(null);
    const [statusMessage, setStatusMessage] = useState({ text: '', type: '' });
    const [isLoading, setIsLoading] = useState(false);

    const showStatus = (text, type = 'success') => {
        setStatusMessage({ text, type });
        setTimeout(() => setStatusMessage({ text: '', type: '' }), 5000);
    };

    const fetchAllData = useCallback(async () => {
        setIsLoading(true);
        const headers = getAuthHeaders();
        if (headers.error) {
            if (onLogout) onLogout(); // Logout auslösen wenn Session ungültig
            return;
        }

        try {
            // Produkte laden
            const prodRes = await fetch(`${API_URL}/products`, { headers: { 'Authorization': headers.Authorization } });
            const productsData = await prodRes.json();
            setProducts(Array.isArray(productsData) ? productsData : []);

            // Stoffe laden
            const fabRes = await fetch(`${API_URL}/fabrics`, { headers: { 'Authorization': headers.Authorization } });
            const fabricsData = await fabRes.json();
            setFabrics(Array.isArray(fabricsData) ? fabricsData : []);

        } catch (err) {
            console.error("Ladefehler:", err);
            showStatus("Fehler beim Laden der Daten.", "error");
        } finally {
            setIsLoading(false);
        }
    }, [onLogout]);

    useEffect(() => {
        fetchAllData();
    }, [fetchAllData]);

    // --- HANDLER ---

    const handleProductSubmit = async (formData) => {
        // ... Logik für Produkt-Speicherung ...
        // Hier gekürzt für Übersichtlichkeit, muss mit fetch implementiert werden
        // Siehe vorherigen Code für vollständige Implementierung
        showStatus("Produkt gespeichert (Simulation)", "success");
    };

    const handleAddFabric = async (formData) => {
        const headers = getAuthHeaders();
        if (headers.error) return;

        try {
            const response = await fetch(`${API_URL}/fabrics`, {
                method: 'POST',
                headers: { 'Authorization': headers.Authorization },
                body: formData
            });
            
            if (!response.ok) throw new Error("Fehler beim Speichern");
            
            showStatus("Stoff erfolgreich hinzugefügt!", "success");
            fetchAllData(); // Aktualisieren
        } catch (err) {
            showStatus(`Fehler: ${err.message}`, "error");
        }
    };
    
    const handleDeleteProduct = async (id) => { /* ... Logik ... */ };
    const handleDeleteFabric = async (id) => {
        const headers = getAuthHeaders();
        if (!window.confirm("Stoff wirklich löschen?")) return;

        try {
            await fetch(`${API_URL}/fabrics/${id}`, {
                method: 'DELETE',
                headers: { 'Authorization': headers.Authorization }
            });
            showStatus("Stoff gelöscht.", "success");
            fetchAllData();
        } catch (err) {
            showStatus("Löschen fehlgeschlagen.", "error");
        }
    };

    // --- RENDER ---

    if (isLoading) return <p style={{textAlign:'center', padding:'50px'}}>Lade Dashboard...</p>;

    return (
        <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '20px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', borderBottom: '2px solid var(--farbe-primär)', paddingBottom: '20px', marginBottom: '30px' }}>
                <h1 style={{ color: 'var(--farbe-primär)', margin: 0 }}>Admin Dashboard</h1>
                <button onClick={() => { localStorage.removeItem('adminToken'); if(onLogout) onLogout(); }} style={{ backgroundColor: 'var(--farbe-rot)', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '5px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '5px' }}>
                    <LogOut size={18} /> Logout
                </button>
            </div>

            {statusMessage.text && (
                <div style={{ padding: '15px', marginBottom: '20px', borderRadius: '5px', backgroundColor: statusMessage.type === 'error' ? '#f8d7da' : '#d4edda', color: statusMessage.type === 'error' ? '#721c24' : '#155724' }}>
                    {statusMessage.text}
                </div>
            )}

            {/* TEIL 1: PRODUKTE */}
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '30px', marginBottom: '50px' }}>
                <div>
                    <ProductForm 
                        productToEdit={currentProduct} 
                        onSubmit={handleProductSubmit} 
                        onCancel={() => setCurrentProduct(null)} 
                        fabrics={fabrics} // Übergabe der Stoffe an Produktformular!
                    />
                </div>
                <div>
                    {/* Hier AdminProductList einfügen */}
                    <h2 style={{color:'var(--farbe-primär)'}}>Produkte ({products.length})</h2>
                    {/* Liste rendern... */}
                </div>
            </div>

            {/* TEIL 2: STOFFE (NEU HINZUGEFÜGT) */}
            <div style={{ borderTop: '2px solid #eee', paddingTop: '30px' }}>
                <h2 style={{ color: 'var(--farbe-primär)', marginBottom: '20px' }}>Stoff-Inventar Verwaltung</h2>
                
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '30px' }}>
                    {/* Stoff-Formular */}
                    <div>
                        <FabricForm onAdd={handleAddFabric} />
                    </div>

                    {/* Stoff-Liste */}
                    <div>
                        <h3 style={{marginTop:0}}>Verfügbare Stoffe ({fabrics.length})</h3>
                        {fabrics.length === 0 ? <p>Keine Stoffe vorhanden.</p> : (
                            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                                <thead>
                                    <tr style={{ backgroundColor: '#f9f9f9' }}>
                                        <th style={{padding:'10px', textAlign:'left'}}>Bild</th>
                                        <th style={{padding:'10px', textAlign:'left'}}>Name</th>
                                        <th style={{padding:'10px', textAlign:'left'}}>Art</th>
                                        <th style={{padding:'10px', textAlign:'left'}}>Bestand</th>
                                        <th style={{padding:'10px'}}>Aktion</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {fabrics.map(f => (
                                        <tr key={f.id} style={{ borderBottom: '1px solid #eee' }}>
                                            <td style={{padding:'10px'}}><img src={getImageUrl(f.imageUrl)} alt={f.name} style={{width:'40px', height:'40px', objectFit:'cover', borderRadius:'4px'}} /></td>
                                            <td style={{padding:'10px'}}><strong>{f.name}</strong></td>
                                            <td style={{padding:'10px'}}>{f.fabricType}</td>
                                            <td style={{padding:'10px'}}>{f.stock}m</td>
                                            <td style={{padding:'10px', textAlign:'center'}}>
                                                <button onClick={() => handleDeleteFabric(f.id)} style={{ background: 'none', border: 'none', cursor: 'pointer', color: 'red' }}><Trash2 size={18}/></button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
