import React, { useEffect, useRef, useState } from 'react';

export default function Checkout({ cartItems, onPaymentSuccess }) {
  const [loadingPaypal, setLoadingPaypal] = useState(true);
  const paypalRef = useRef(null);

  useEffect(() => {
    const loadPaypal = async () => {
      if (!window.paypal) {
        const script = document.createElement('script');
        script.src = `https://www.paypal.com/sdk/js?client-id=${process.env.REACT_APP_PAYPAL_CLIENT_ID}&currency=EUR`;
        script.addEventListener('load', () => setLoadingPaypal(false));
        document.body.appendChild(script);
      } else {
        setLoadingPaypal(false);
      }
    };
    loadPaypal();
  }, []);

  useEffect(() => {
    if (!loadingPaypal && paypalRef.current) {
      window.paypal.Buttons({
        createOrder: (data, actions) => {
          const total = cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0).toFixed(2);
          return actions.order.create({
            purchase_units: [{ amount: { value: total, currency_code: 'EUR' } }]
          });
        },
        onApprove: (data, actions) => {
          return actions.order.capture().then(details => {
            onPaymentSuccess(details);
          });
        }
      }).render(paypalRef.current);
    }
  }, [loadingPaypal, cartItems, onPaymentSuccess]);

  return (
    <div>
      {loadingPaypal && <p>Paypal l√§dt...</p>}
      <div ref={paypalRef}></div>
    </div>
  );
}
