// src/components/CartContext.js
import React, { createContext, useContext, useState } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);

  // Produkt erwartet numeric price. Wir konvertieren defensiv.
  const addToCart = (product, quantity = 1) => {
    setCartItems(prev => {
      const price = Number(product.price) || 0;
      const existing = prev.find(item => item.id === product.id);
      if (existing) {
        return prev.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + quantity } : item
        );
      }
      return [...prev, { id: product.id, name: product.name, price, quantity }];
    });
  };

  // removeFromCart(productId, qty) -> wenn qty null: komplett entfernen; sonst qty reduzieren
  const removeFromCart = (productId, qty = null) => {
    setCartItems(prev => {
      const existing = prev.find(it => it.id === productId);
      if (!existing) return prev;
      if (qty === null) {
        return prev.filter(it => it.id !== productId);
      } else {
        if (existing.quantity - qty <= 0) return prev.filter(it => it.id !== productId);
        return prev.map(it => it.id === productId ? { ...it, quantity: it.quantity - qty } : it);
      }
    });
  };

  const clearCart = () => setCartItems([]);

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
