// src/components/Shop.js
import React, { useEffect, useState } from 'react';
import { useCart } from './CartContext';
import './Shop.css';

const API_URL = 'http://localhost:3001/api/products';
const FALLBACK_IMAGE_URL = "https://placehold.co/400x400/CCCCCC/333333?text=Stoffmuster";

export default function Shop() {
  const { cartItems, addToCart, removeOneFromCart, removeFromCart, clearCart, getTotalPrice } = useCart();
  const [products, setProducts] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(setProducts)
      .catch(console.error);
  }, []);

  return (
    <div className="shop-container">
      <h1>Shop</h1>

      <div className="product-grid">
        {products.map(product => (
          <div className="product-card" key={product.id}>
            <img
              src={product.imageUrl || FALLBACK_IMAGE_URL}
              alt={product.name}
              onError={e => e.target.src = FALLBACK_IMAGE_URL}
            />
            <h3>{product.name}</h3>
            <p>‚Ç¨ {product.price.toFixed(2)}</p>
            <button onClick={() => addToCart(product)}>In den Warenkorb</button>
          </div>
        ))}
      </div>

      <div className={`sticky-cart ${showCart ? 'open' : ''}`}>
        <h3>üõí Warenkorb</h3>
        {cartItems.length === 0 ? (
          <p>Warenkorb leer</p>
        ) : (
          <>
            <ul>
              {cartItems.map(item => (
                <li key={item.id}>
                  {item.name} x {item.quantity} (‚Ç¨ {(item.price * item.quantity).toFixed(2)})
                  <button onClick={() => removeOneFromCart(item.id)}>‚ûñ</button>
                  <button onClick={() => addToCart(item, 1)}>‚ûï</button>
                  <button onClick={() => removeFromCart(item.id)}>‚ùå</button>
                </li>
              ))}
            </ul>
            <p>Gesamt: ‚Ç¨ {getTotalPrice().toFixed(2)}</p>
            <button onClick={() => alert('Checkout‚Ä¶')}>Bezahlen</button>
            <button onClick={clearCart}>Alles l√∂schen</button>
          </>
        )}
        <button className="close-cart-btn" onClick={() => setShowCart(false)}>Schlie√üen</button>
      </div>

      <button className="open-cart-button" onClick={() => setShowCart(prev => !prev)}>
        üõí {cartItems.reduce((sum, i) => sum + i.quantity, 0)}
      </button>
    </div>
  );
}
