import React, { useState } from 'react';

// Konstante für das hartkodierte Passwort (NUR FÜR FRONTEND-SCHUTZ)
const ADMIN_PASSWORD = "stoffzauber2025"; 

/**
 * AdminLogin-Komponente
 * Zeigt das Anmeldeformular für den Admin-Bereich an.
 * @param {object} props
 * @param {function} props.onLogin - Callback-Funktion, die bei erfolgreichem Login aufgerufen wird.
 */
const AdminLogin = ({ onLogin }) => {
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        
        if (password === ADMIN_PASSWORD) {
            // Erfolgreicher Login: Callback aufrufen, um den Zustand in App.js zu ändern
            onLogin(true);
        } else {
            setError('Falsches Passwort. Bitte versuchen Sie es erneut.');
            setPassword(''); // Passwortfeld leeren
        }
    };

    return (
        // Zentriertes Layout mit Tailwind-Klassen (simuliert durch inline-styles)
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', padding: '40px 0' }}>
            <div style={{ 
                width: '100%', 
                maxWidth: '400px', 
                margin: '0 auto',
                backgroundColor: 'var(--farbe-hell)',
                borderRadius: '10px',
                boxShadow: '0 5px 20px rgba(0, 0, 0, 0.1)',
                padding: '30px'
            }}>
                <h2 style={{ color: 'var(--farbe-text)', textAlign: 'center', marginBottom: '25px', borderBottom: '2px solid var(--farbe-primär)', paddingBottom: '10px' }}>
                    Admin-Anmeldung
                </h2>
                
                <form onSubmit={handleSubmit}>
                    <div style={{ marginBottom: '20px' }}>
                        <label htmlFor="password" style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold', color: 'var(--farbe-text)' }}>
                            Passwort:
                        </label>
                        <input
                            id="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            style={{ 
                                width: '100%', 
                                padding: '10px', 
                                border: '1px solid #ddd', 
                                borderRadius: '5px', 
                                boxSizing: 'border-box'
                            }}
                        />
                    </div>
                    
                    {error && (
                        <p style={{ color: 'var(--farbe-rot)', marginBottom: '15px', fontWeight: 'bold' }}>
                            {error}
                        </p>
                    )}

                    <button 
                        type="submit" 
                        style={{ 
                            width: '100%', 
                            backgroundColor: 'var(--farbe-akzent)', 
                            color: 'white', 
                            padding: '12px', 
                            borderRadius: '5px', 
                            border: 'none', 
                            cursor: 'pointer',
                            transition: 'background-color 0.2s'
                        }}
                        onMouseEnter={(e) => e.target.style.backgroundColor = 'var(--farbe-primär)'}
                        onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--farbe-akzent)'}
                    >
                        Anmelden
                    </button>
                </form>

            </div>
        </div>
    );
};

export default AdminLogin;
