import React, { useState, useEffect } from 'react';
import { useCart } from './CartContext';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [form, setForm] = useState({
    firstName: '', lastName: '', street: '', houseNumber: '',
    zip: '', city: '', email: '', phone: '', paymentMethod: 'paypal'
  });
  const [paypalLoaded, setPaypalLoaded] = useState(false);
  const [invoiceLink, setInvoiceLink] = useState(null);

  const total = cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0).toFixed(2);

  useEffect(() => {
    if (!window.paypal && form.paymentMethod === 'paypal') {
      const script = document.createElement('script');
      script.src = `https://www.paypal.com/sdk/js?client-id=${process.env.REACT_APP_PAYPAL_CLIENT_ID}&currency=EUR`;
      script.addEventListener('load', () => setPaypalLoaded(true));
      document.body.appendChild(script);
    } else setPaypalLoaded(true);
  }, [form.paymentMethod]);

  useEffect(() => {
    if (paypalLoaded && form.paymentMethod === 'paypal' && cartItems.length) {
      window.paypal.Buttons({
        createOrder: (data, actions) => actions.order.create({ purchase_units: [{ amount: { value: total } }] }),
        onApprove: async (data, actions) => {
          await actions.order.capture();
          handleSubmit();
        }
      }).render('#paypal-button-container');
    }
  }, [paypalLoaded, cartItems, form.paymentMethod]);

  const handleChange = (e) => setForm(prev => ({ ...prev, [e.target.name]: e.target.value }));

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    const res = await fetch('http://localhost:3001/api/checkout', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ cart: cartItems, address: form, paymentMethod: form.paymentMethod })
    });
    const data = await res.json();
    if (data.success) {
      setInvoiceLink(data.invoicePath);
      clearCart();
      alert('Bestellung erfolgreich!');
    } else alert('Fehler beim Checkout: ' + data.error);
  };

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <form className="checkout-form" onSubmit={handleSubmit}>
        <div className="checkout-row">
          <div>
            <label>Vorname</label>
            <input name="firstName" value={form.firstName} onChange={handleChange} required />
          </div>
          <div>
            <label>Nachname</label>
            <input name="lastName" value={form.lastName} onChange={handleChange} required />
          </div>
        </div>
        <div className="checkout-row">
          <div>
            <label>Straße</label>
            <input name="street" value={form.street} onChange={handleChange} required />
          </div>
          <div>
            <label>Hausnummer</label>
            <input name="houseNumber" value={form.houseNumber} onChange={handleChange} required />
          </div>
        </div>
        <div className="checkout-row">
          <div>
            <label>PLZ</label>
            <input name="zip" value={form.zip} onChange={handleChange} required />
          </div>
          <div>
            <label>Stadt</label>
            <input name="city" value={form.city} onChange={handleChange} required />
          </div>
        </div>
        <div>
          <label>E-Mail</label>
          <input name="email" type="email" value={form.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Telefon</label>
          <input name="phone" value={form.phone} onChange={handleChange} />
        </div>
        <div>
          <label>Zahlungsmethode</label>
          <select name="paymentMethod" value={form.paymentMethod} onChange={handleChange}>
            <option value="paypal">PayPal</option>
            <option value="invoice">Rechnung</option>
          </select>
        </div>
        <div className="checkout-buttons">
          <button type="submit" className="checkout-submit">Jetzt kaufen (€{total})</button>
        </div>
      </form>

      {form.paymentMethod === 'paypal' && <div id="paypal-button-container" className="paypal-button-container"></div>}
      {invoiceLink && <a href={invoiceLink} target="_blank" rel="noreferrer" className="checkout-download">Rechnung herunterladen</a>}
    </div>
  );
};

export default Checkout;
