// client/src/components/StoffForm.js
import React, { useState, useEffect, useMemo } from 'react';

const FALLBACK_IMAGE_URL = "https://placehold.co/100x100/CCCCCC/333333?text=Stoffmuster";

// Verwendung von useMemo, um ESLint-Warnung (exhaustive-deps) zu beheben
const getInitialState = (stoffToEdit) => stoffToEdit || {
    name: '',
    description: '',
    fabricType: '', // Neu: Stoffart (z.B. Jersey, French Terry)
    suitableFor: [], // Neu: Array von Verwendungszwecken
    stock: 0, 
    imageUrl: '', 
    imageFile: null,
};

// Liste der möglichen Verwendungszwecke (zum Checkbox-Rendern)
const USAGE_OPTIONS = [
    'Mütze (Außen)', 'Mütze (Innen)', 'Bündchen', 'Bekleidung (Oberteil)', 
    'Bekleidung (Hose)', 'Babybekleidung', 'Deko', 'Taschen', 'Accessoires'
];

// Liste der Stoffarten
const FABRIC_TYPES = [
    'Jersey', 'French Terry (Sommersweat)', 'Sweat', 'Bündchenstoff', 'Musselin', 
    'Webware Baumwolle', 'Cord', 'Fleece', 'Softshell', 'Leder (Imitat/Echt)'
];


// ** ÄNDERUNG: prop von onAdd zu onSubmit **
const StoffForm = ({ stoffToEdit, onSubmit, onCancel }) => {
    
    const initialState = useMemo(() => getInitialState(stoffToEdit), [stoffToEdit]);
    
    // Zustand für das Formular
    const [formData, setFormData] = useState(initialState);
    const [isEditing, setIsEditing] = useState(!!stoffToEdit);
    const [previewImage, setPreviewImage] = useState(formData.imageUrl || null);


    useEffect(() => {
        // Setzt das Formular zurück, wenn ein neues oder kein Stoff zum Bearbeiten übergeben wird
        setFormData(initialState);
        setIsEditing(!!stoffToEdit);
        setPreviewImage(initialState.imageUrl || null);
    }, [initialState, stoffToEdit]); 
    

    const handleChange = (e) => {
        const { name, value, type, checked, files } = e.target;
        
        if (name === 'imageFile') {
            const file = files[0];
            setFormData(prev => ({
                ...prev,
                imageFile: file,
            }));
            // Lokale URL für die Vorschau erzeugen
            if (file) {
                setPreviewImage(URL.createObjectURL(file));
            } else {
                setPreviewImage(formData.imageUrl || null);
            }
        } else if (name === 'suitableFor') {
            // Checkbox-Änderung für Verwendungszwecke
            setFormData(prev => {
                const currentUses = prev.suitableFor || [];
                const newUses = checked 
                    ? [...currentUses, value]
                    : currentUses.filter(use => use !== value);
                return { ...prev, suitableFor: newUses };
            });
        } else {
            // Normale Eingabe
            const newValue = type === 'checkbox' ? checked : value;
            setFormData(prev => ({
                ...prev,
                [name]: newValue,
            }));
        }
    };
    
    // Hilfsfunktion zur Konsolidierung der Vorschau-URL
    const getFinalPreviewUrl = () => {
        // Zeigt die lokale Dateivorschau, falls vorhanden, ansonsten die gespeicherte URL
        return previewImage || FALLBACK_IMAGE_URL; 
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Erstellen des FormData-Objekts für den Upload (inklusive Datei)
        const dataToSubmit = new FormData();
        
        // Fügen alle Felder hinzu (muss Strings sein)
        dataToSubmit.append('name', formData.name);
        dataToSubmit.append('description', formData.description);
        dataToSubmit.append('fabricType', formData.fabricType);
        // WICHTIG: Arrays müssen als JSON-String serialisiert werden, um sie in FormData zu senden!
        dataToSubmit.append('suitableFor', JSON.stringify(formData.suitableFor)); 
        dataToSubmit.append('stock', formData.stock.toString());
        
        // Bilddatei hinzufügen
        if (formData.imageFile) {
            dataToSubmit.append('imageFile', formData.imageFile);
        } else if (formData.imageUrl) {
            // Sende die bestehende URL, wenn kein neues Bild hochgeladen wird
            dataToSubmit.append('imageUrl', formData.imageUrl);
        }

        try {
            // Rufe die übergebene onSubmit-Funktion mit FormData und ID auf
            await onSubmit(dataToSubmit, stoffToEdit ? stoffToEdit.id : null);
            
            // Nur zurücksetzen, wenn es ein NEUES Element war (keine Bearbeitung)
            if (!isEditing) {
                setFormData(getInitialState(null));
                setPreviewImage(null);
            }
            
        } catch (error) {
            // Fehler werden im Dashboard angezeigt
            console.error('Formular-Fehler (wird im Dashboard angezeigt):', error.message);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="admin-form-style">
            <h2 style={{ color: 'var(--farbe-sekundär)', marginBottom: '20px', fontSize: '1.4em' }}>
                {isEditing ? `Stoff bearbeiten: ${formData.name}` : 'Neuen Stoff hinzufügen'}
            </h2>
            
            {/* 1. Basisinformationen */}
            <div className="admin-form-group">
                <label htmlFor="name">Name des Stoffes:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="admin-form-group">
                <label htmlFor="fabricType">Stoffart:</label>
                <select
                    id="fabricType"
                    name="fabricType"
                    value={formData.fabricType}
                    onChange={handleChange}
                    required
                >
                    <option value="">-- Bitte wählen --</option>
                    {FABRIC_TYPES.map(type => (
                        <option key={type} value={type}>{type}</option>
                    ))}
                </select>
            </div>
            
            <div className="admin-form-group">
                <label htmlFor="description">Beschreibung:</label>
                <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                    required
                />
            </div>

            {/* 2. Lagerbestand */}
            <div className="admin-form-group">
                <label htmlFor="stock">Lagerbestand (in Metern):</label>
                <input
                    type="number"
                    id="stock"
                    name="stock"
                    value={formData.stock}
                    onChange={handleChange}
                    min="0"
                    step="0.01"
                    required
                />
            </div>
            
            {/* 3. Verwendungszwecke (Checkboxen) */}
            <div className="admin-form-group">
                <label>Geeignet für (Mehrfachauswahl):</label>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '10px' }}>
                    {USAGE_OPTIONS.map(usage => (
                        <div key={usage} style={{ display: 'flex', alignItems: 'center' }}>
                            <input
                                type="checkbox"
                                id={`use-${usage}`}
                                name="suitableFor"
                                value={usage}
                                checked={formData.suitableFor.includes(usage)}
                                onChange={handleChange}
                                style={{ width: '15px', height: '15px', marginRight: '5px' }}
                            />
                            <label htmlFor={`use-${usage}`} style={{ margin: 0, fontWeight: 'normal', fontSize: '0.9em' }}>
                                {usage}
                            </label>
                        </div>
                    ))}
                </div>
            </div>

            {/* 4. Bild-Upload */}
            <div className="admin-form-group">
                <label htmlFor="imageFile">Stoffmuster Bild (optional):</label>
                <input
                    type="file"
                    id="imageFile"
                    name="imageFile"
                    accept="image/*"
                    onChange={handleChange}
                />
                
                <div style={{ marginTop: '15px' }}>
                    <img
                        src={getFinalPreviewUrl()}
                        alt="Vorschau"
                        style={{ maxWidth: '100px', maxHeight: '100px', borderRadius: '8px', border: '1px solid #ccc' }}
                    />
                </div>
            </div>

            {/* 5. Aktionen */}
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                <button type="submit" className="admin-form-submit-button">
                    {isEditing ? 'Änderungen speichern' : 'Stoff erstellen'}
                </button>
            </div>
            
            {isEditing && (
                <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
                    <button type="button" onClick={onCancel} className="admin-form-submit-button" style={{backgroundColor: '#666'}}>
                        Abbrechen (Zurück zur Erstellung)
                    </button>
                </div>
            )}
        </form>
    );
};

export default StoffForm;
